UPDATE `db_script_string` SET `content_loc3`='J... Ja, ja... natürlich. Wir brauchen nur die Kristalle und die Erze von diesem Ort. Da ist... Wir werden Reittiere benötigen.' WHERE `entry`=2000005710;
