UPDATE `db_script_string` SET `content_loc3`='Der Prozess ist aufwendig. Wir müssen zuerst Akolyten der Elemente beschwören. Anschließend müsst Ihr diese Akolythen zerstören, damit meine Diener die Vorbereitungen treffen können.' WHERE `entry`=2000005715;
