UPDATE `db_script_string` SET `content_loc3`='Danke, gute Seele. Ihr habt mich aus den Wassergefängnissen der Echsennarbe befreit, aber in der Nähe werden noch viel mehr gefangen gehalten. Wollt Ihr mir dabei helfen, sie auch zu befreien?' WHERE `entry`=2000005766;
