UPDATE `db_script_string` SET `content_loc3`='Oh nein. Wir kommen zu spät. Diese Leute sind allesamt infiziert. Noch sehen sie normal aus, aber es ist nur noch eine Frage der Zeit, bis sie Untote werden!' WHERE `entry`=2000005790;
