UPDATE db_script_string SET content_loc3='Lebt wohl, mein Freund' WHERE entry=2000000211; /* content: You want to go do the bounty off of this wanted poster after this shift is over? */
UPDATE db_script_string SET content_loc3='%s beschwört einen Boten...' WHERE entry=2000000212; /* content: Be the first on your block to own the incredible Electrostatic Maculation Blaster! */
UPDATE db_script_string SET content_loc3='%s beschwört einen Boten...' WHERE entry=2000000212; /* content: %s summons a messenger... */
UPDATE db_script_string SET content_loc3='Geht mein Diener. Sendet die Worte der Steinkrallen zu Apothekerin Zamah.' WHERE entry=2000000213; /* content: Go, my servant. Send word of Stonetalon to Apothecary Zamah. */
UPDATE db_script_string SET content_loc3='Geht mein Diener. Sendet die Worte der Steinkrallen zu Apothekerin Zamah.' WHERE entry=2000000213; /* content: Derived from cutting edge Shattered Hand Offensive military technology, it blasts away stubborn stains with ease! */
UPDATE db_script_string SET content_loc3='%s übergibt Apothekerin Zamah eine Nachricht.' WHERE entry=2000000214; /* content: %s gives message to Apothecary Zamah. */
UPDATE db_script_string SET content_loc3='%s übergibt Apothekerin Zamah eine Nachricht.' WHERE entry=2000000214; /* content: Wine stains, grass stains, even pet stains are no match for the Electrostatic Maculation Blaster! */
UPDATE db_script_string SET content_loc3='%s lächelt.' WHERE entry=2000000215; /* content: Apothecary Zamah smiles. */
UPDATE db_script_string SET content_loc3='%s lächelt.' WHERE entry=2000000215; /* content: Don\'t wait! Order now and receive my Ultra-Kinetic Waffle Iron at no additional charge! */
UPDATE db_script_string SET content_loc3='Ah ja, Loken ist mir wohl bekannt.' WHERE entry=2000000216; /* content: Ah, yes. Loken is well known to me. */
UPDATE db_script_string SET content_loc3='Es wird langsam, Waffelfritte, es wird... Hast du etwa keine Produktpräsentation zu machen, oder was?' WHERE entry=2000000216; /* content: It\'s coming along, Wafflefry. Haven\'t you got a product demonstration to do or something? */
UPDATE db_script_string SET content_loc3='Er ist derjenige, der den Eisenzwergen in diesem Krieg gegen uns die Befehle gibt.' WHERE entry=2000000217; /* content: It is he who commands the sons of iron in their war against us. */
UPDATE db_script_string SET content_loc3='Aus seinem Versteck heraus führt er die Aufsicht über die Kriegsvorbereitungen mit dem Ziel, die Steinriesen auszulöschen!' WHERE entry=2000000218; /* content: From his hiding place, he oversees their preparations for war with the goal of exterminating the stone giants! */
UPDATE db_script_string SET content_loc3='Er ist auch hinter mir her! Ich an Eurer Stelle wäre hier in der Gegend vorsichtig, $gReisender:Reisende;. Man weiß nie, hinter welchem Baum er sich versteckt!' WHERE entry=2000000219; /* content: He\'s out to get me, too! I\'d be careful around here if I was you, traveler. You never know which tree he\'s hiding behind! */
UPDATE db_script_string SET content_loc3='Hinkejoe würde so was nie fragen! Aber ja klar, ich kenne Loken.' WHERE entry=2000000220; /* content: That\'s not something Limpy Joe would ask! But yeah, I know Loken. */
UPDATE db_script_string SET content_loc3='Ich werde wohl Jagd auf diesen Getriebenen machen, jetzt, da eine Belohnung auf seinen Kopf aussteht.' WHERE entry=2000000220; /* content: I might just have to go hunting for that Wretched beast now that there\'s a bounty on his head. */
UPDATE db_script_string SET content_loc3='Hier oben auf dem Hügel, $n.' WHERE entry=2000000222; /* content: Up here on the hill, $N. */
UPDATE db_script_string SET content_loc3='%s legt sein Wolfsfellgewand an.' WHERE entry=2000000223; /* content: %s dons his wolf-fur vestments. */
UPDATE db_script_string SET content_loc3='%s heult als Antwort auf Malukaz\' Ruf.' WHERE entry=2000000224; /* content: %s howls in response to Malukaz\'s call. */
UPDATE db_script_string SET content_loc3='%s drückt sein Wohlwollen aus.' WHERE entry=2000000225; /* content: %s expresses its approval. */
UPDATE db_script_string SET content_loc3='Möge unser Band nie mehr zerreißen, Geist.' WHERE entry=2000000226; /* content: May our bond with you never be broken again, spirit. */
UPDATE db_script_string SET content_loc3='Wir haben es geschafft! Thrall wäre stolz auf uns.' WHERE entry=2000000227; /* content: We have done it! Thrall would be proud. */
UPDATE db_script_string SET content_loc3='Urtrak wird Euch töten!' WHERE entry=2000000228; /* content: Urtrak kill you! */
UPDATE db_script_string SET content_loc3='Die Rache ist unser! Greift an, meine Brüder!' WHERE entry=2000000229; /* content: Vengeance is ours!  Attack my brothers! */
UPDATE db_script_string SET content_loc3='Die misslungene Mischung fällt auf den Boden und scheint lebendig zu werden!' WHERE entry=2000000230; /* content: The failed concoction spills on the floor and appears to come to life! */
UPDATE db_script_string SET content_loc3='Hey, kann mir jemand helfen dies hier zu fangen!' WHERE entry=2000000231; /* content: Hey, someone help me catch this thing! */
UPDATE db_script_string SET content_loc3='Es lebt!' WHERE entry=2000000231; /* content: It\'s alive! */
UPDATE db_script_string SET content_loc3='Bist du es, $gder:die; mein Abendessen gestört hat? Nun, bist du\'s?' WHERE entry=2000000235; /* content: Are YOU the one who interrupted my dinner? Well, are you? */
UPDATE db_script_string SET content_loc3='Du bist zu hässlich und dürr, um ein Yetiweibchen zu sein! Und jetzt machst du mich sauer...' WHERE entry=2000000236; /* content: You\'re too ugly and scrawny to be a female yeti! And now you\'re making me mad... */
UPDATE db_script_string SET content_loc3='Nochmals danke, $n. Ich werde jetzt einfach hier warten, bis ich ungefährdet gehen kann.' WHERE entry=2000000237; /* content: Thank again, $n.  Now I\'ll just wait here until it\'s safe to leave. */
UPDATE db_script_string SET content_loc3='Sie bewahren ihre Werkzeuge in Kästen wie diesen auf. Sucht nach ihnen in der Nähe der Pavillons.' WHERE entry=2000000238; /* content: They keep their tools in boxes that look like this. Look for them near the pavilions. */
UPDATE db_script_string SET content_loc3='Ich werde nur die Runen mit einer Prägung versehen, bei denen ich meine, dass sie Euch die Kontrolle über einen Riesen geben werden.' WHERE entry=2000000239; /* content: I\'m only going to carve the runes I think will give you command over a giant. */
UPDATE db_script_string SET content_loc3='Wenn Ihr zu lange wartet, verbleibt nicht mehr viel von Eurer Zukunft für meine Voraussagen!' WHERE entry=2000000240; /* content: The longer you wait, the less future you have for me to foretell. */
UPDATE db_script_string SET content_loc3='Da, bitteschön, die Rune des Befehls... hoffentlich. Zwar nicht annähernd so gemein wie ihr eisenzwergischer Vetter, doch funktionsfähig.' WHERE entry=2000000241; /* content: There we go, the Rune of Command... hopefully. Not nearly as vicious as its Iron Dwarf cousin, but functional. */
UPDATE db_script_string SET content_loc3='Erhebt Euch, meine Brüder!' WHERE entry=2000000242; /* content: Arise from the earth, my brothers! */
UPDATE db_script_string SET content_loc3='Unsere Meister des Eisens haben eine Mission für uns!' WHERE entry=2000000243; /* content: Our iron masters have a mission for us! */
UPDATE db_script_string SET content_loc3='Kommt mit mir in die Berge, um den Plan der Meister auszuführen!' WHERE entry=2000000244; /* content: Follow me into the mountains to carry out the plan of the masters! */
UPDATE db_script_string SET content_loc3='Wir werden nicht versagen!' WHERE entry=2000000245; /* content: We will not fail! */
UPDATE db_script_string SET content_loc3='Nein! Ich flehe Euch an! Bitte tötet mich nicht!' WHERE entry=2000000246; /* content: No! I beg you! Please don\'t kill me! */
UPDATE db_script_string SET content_loc3='Was meint Ihr damit, meine Zeit sei gekommen?! Ich werde Euch auf der Stelle töten!' WHERE entry=2000000247; /* content: What do you mean my time has come?! I\'ll kill you where you stand! */
UPDATE db_script_string SET content_loc3='Der Meister hat viel riskiert, um an Informationen über den Verbleib der Exodar zu kommen. Deinetwegen wäre beinahe die gesamte Unternehmung geplatzt, einschließlich der Identität unseres Spions im innersten Kreis des Feindes.' WHERE entry=2000000248; /* content: The Master has gone to great lengths to secure information about the whereabouts of the Exodar. You could have blown the entire operation, including the cover of our spy on the inside. */
UPDATE db_script_string SET content_loc3='Was soll das bedeuten?! Haltet ein! Ich zahle das Doppelte von wie viel auch immer Ihr bekommt!' WHERE entry=2000000248; /* content: What is the meaning of this?! Stop! I\'ll double whatever they\'re paying you! */
UPDATE db_script_string SET content_loc3='Ihr könnt unmöglich... Dafür kriege ich Euch dran, $C!' WHERE entry=2000000249; /* content: You can\'t possibly mean to.... I\'ll write you up for this, $c! */
UPDATE db_script_string SET content_loc3='Ihr habt Recht, Etrigg. Der Zeitpunkt ist ungünstig. Stellt sicher, dass unsere besten Botschafter zu Höllschrei gesandt werden.' WHERE entry=2000000250; /* content: You are right, Eitrigg.  The timing is unfavorable.  Make sure our best ambassadors are sent as envoys to Hellscream. */
UPDATE db_script_string SET content_loc3='Wenn er seinem Vater auch nur im Entferntesten ähnlich ist, würde ein Bündnis mit den Mag\'har ein neues Zeitalter für die Horde einläuten.' WHERE entry=2000000252; /* content: If he\'s anything like his father, allying with the Mag\'har will mark the dawn of a new age for the Horde. */
UPDATE db_script_string SET content_loc3='...eine starke Mischung.' WHERE entry=2000000254; /* content: ...a potent concoction! */
UPDATE db_script_string SET content_loc3='Doch wo finde ich nur ein geeignetes Exemplar für den Test?' WHERE entry=2000000255; /* content: But where could I ever find a suitable test subject? */
UPDATE db_script_string SET content_loc3='Hmmm... sieht so aus, als würde ich ein neues Exemplar brauchen.' WHERE entry=2000000258; /* content: Hmmm... looks like I\'m going to need a new test subject. */
UPDATE db_script_string SET content_loc3='Gut gemacht, $n. Setzt Eure Suche nach Wissen fort und erinnert Euch an die Geschichte, von der Ihr erfahren habt.' WHERE entry=2000000259; /* content: You have done well, $N. Continue your search for knowledge, and remember the history you have learned. */
UPDATE db_script_string SET content_loc3='Terenthis, Raene schickte mich, um Euch zu suchen. Die Schildwachen des Eschentals brauchen Hilfe.' WHERE entry=2000000260; /* content: Terenthis, Raene sent me to find you. The Sentinels are in need of help in Ashenvale. */
UPDATE db_script_string SET content_loc3='Ich habe vor nichts Angst... immer her damit!' WHERE entry=2000000261; /* content: I\'m not afraid of anything -- bring it on! */
UPDATE db_script_string SET content_loc3='Das stimmt so überhaupt nicht, $n. In der Sage, die mir Windzähmerin Barah erzählt hat, war Sturmhuf ein Held, kein Verbrecher.' WHERE entry=2000000262; /* content: This isn\'t right at all, $N. In the tale Wind Tamer Barah told me, Stormhoof was a hero, not a villain. */
UPDATE db_script_string SET content_loc3='Der Nordwind ist der ewige Gegner der Taunka, ein Herrscher unter den Elementen, und die Kraft, welche das Leben aus diesem uralten Volk saugt.' WHERE entry=2000000263; /* content: The North Wind is the ancient enemy of the taunka, a ruler among the elements, and the very force which bleeds the life from these ancient people. */
UPDATE db_script_string SET content_loc3='Sie würden lieber sterben, als sich vor dem Nordwind zu verbeugen. Etwas stimmt hier nicht.' WHERE entry=2000000264; /* content: They would rather die than bow before the North Wind. Something is amiss here. */
UPDATE db_script_string SET content_loc3='Der Lehrenhammer...' WHERE entry=2000000265; /* content: The Lorehammer... */
UPDATE db_script_string SET content_loc3='Das bestätigt unsere Vermutungen. Die Fäden der Zeit wurden gekappt und in einem falschen Muster wieder aneinandergefügt.' WHERE entry=2000000266; /* content: It confirms our suspicions. The threads of time have been severed and rewoven in the wrong pattern. But, how? */
UPDATE db_script_string SET content_loc3='Mein Bruder, Sturmhuf, war ein größerer Krieger, als ich es bin. Während ich dafür trainierte, der Häuptling unseres Klans zu werden, hat er sich auf eine weit größere Aufgabe vorbereitet.' WHERE entry=2000000267; /* content: My brother, Stormhoof, was a far greater warrior than I. While I trained to be the chieftain of our clan, he prepared for a larger quest. */
UPDATE db_script_string SET content_loc3='Wie könnte er sich in Gefahr befinden? Alles, was er wollte, war, uns Macht über die Elemente zu geben, die uns das Leben hier so schwer gemacht haben.' WHERE entry=2000000268; /* content: How could he be in danger? All he wanted was to give us power over the elements that made life harsh for us here. */
UPDATE db_script_string SET content_loc3='Er hat nach einem Artefakt gesucht... ein Horn, glaube ich. Er hat das Dorf für eine lange Suche danach verlassen.' WHERE entry=2000000269; /* content: He sought an artifact... a horn, I think. He left the village on a long journey in search of it. */
UPDATE db_script_string SET content_loc3='Ich weiß, dass ihn ein schrecklicher Gegner gejagt hat, nachdem er das Horn erhalten hatte, aber ich... Ich kann mich nicht erinnern... was als Nächstes... geschehen ist.' WHERE entry=2000000270; /* content: I know that a terrible enemy pursued him after he won the horn, but I... I don\'t recall... what happened... next. */
UPDATE db_script_string SET content_loc3='Es ist, als wären meine Erinnerungen von Nebel umgeben. Ich kann mich noch nicht mal erinnern, was aus meinem Bruder geworden ist. Meint Ihr darum, er sei in Gefahr?' WHERE entry=2000000271; /* content: It is as though my memories are shrouded in mist. I cannot even recall what became of my brother. Is this how you mean that he is in danger? */
UPDATE db_script_string SET content_loc3='Die Unruhen in der Gruft müssen etwas damit zu tun haben. Schaut Euch um, $n. Seht Ihr sie?' WHERE entry=2000000272; /* content: The disturbances in the tomb, they must be involved. Look around you, $N. Do you see them? */
UPDATE db_script_string SET content_loc3='Ja, sie sind im Unrecht, aber ich bin machtlos, sie aufzuhalten. Werdet Ihr die Leute von Tunka\'lo warnen, Fremder, dass ihre Vergangenheit und ihre Vorfahren in Gefahr sind?' WHERE entry=2000000273; /* content: Yes, they are at fault, but I am powerless to stop them. Will you warn the people of Tunka\'lo, stranger, that their past and their ancestors are in danger? */
UPDATE db_script_string SET content_loc3='Der Lehrenhammer bleibt stumm. Das stammt nicht aus Sturmhufs Zeit.' WHERE entry=2000000275; /* content: The Lorehammer is silent. This isn\'t from Stormhoof\'s time. */
UPDATE db_script_string SET content_loc3='Verräterischer Wurm! Sironas wird Euch und Euer Volk vernichten!' WHERE entry=2000000276; /* content: Treacherous whelp! Sironas will destroy you and your people! */
UPDATE db_script_string SET content_loc3='Morae... Bist du das?' WHERE entry=2000000277; /* content: Morae... Is that you? */
UPDATE db_script_string SET content_loc3='Nein... Ihr seid nicht Morae, aber ich spüre, dass Ihr Euch begegnet seid. Wenn Ihr sie seht, sagt ihr, dass ich den Absturz überlebt habe, nur um wenig später durch die Verräter den Tod zu finden.' WHERE entry=2000000278; /* content: No... you\'re not Morae, but I sense that you have met. When you see her, tell her that I survived the crash, only to be done in by the infiltrators. */
UPDATE db_script_string SET content_loc3='Sagt ihr... dass meine letzten Gedanken einzig ihr galten...' WHERE entry=2000000279; /* content: Let her know... my last thought was of her... */
UPDATE db_script_string SET content_loc3='Möge $gder:die; Bessere gewinnen!' WHERE entry=2000000281; /* content: May the best win! */
UPDATE db_script_string SET content_loc3='Ihr werdet mich nicht bezwingen!' WHERE entry=2000000282; /* content: You will not defeat me! */
UPDATE db_script_string SET content_loc3='Ihr seid Thorims nicht würdig!' WHERE entry=2000000283; /* content: You\'re not worthy of Thorim! */
UPDATE db_script_string SET content_loc3='Aus dem Kultisten sprudelt es förmlich heraus.' WHERE entry=2000000286; /* content: The cultist practically sputters. */
UPDATE db_script_string SET content_loc3='Hmm... ich frage mich...' WHERE entry=2000000286; /* content: Hmm... I wonder... */
UPDATE db_script_string SET content_loc3='Mylady.' WHERE entry=2000000287; /* content: My lady. */
UPDATE db_script_string SET content_loc3='Nun, wo ist es?' WHERE entry=2000000287; /* content: Now, where is it? */
UPDATE db_script_string SET content_loc3='Berichtet.' WHERE entry=2000000288; /* content: Report. */
UPDATE db_script_string SET content_loc3='Dumme Magier... verbrauchen alle Reagenzien und besorgen nie Ersatz.' WHERE entry=2000000288; /* content: Stupid mages... always using the last of a reagent and never replacing it. */
UPDATE db_script_string SET content_loc3='Nachrichten aus Jotunheim. Die dortigen Schlafwächter glauben, jemanden gefunden zu haben, der wichtig sein könnte.' WHERE entry=2000000289; /* content: There is word from Jotunheim. The sleep-watchers there believe that they have found someone of significance. */
UPDATE db_script_string SET content_loc3='Beschreibt diesen Vrykul. Wie sieht er aus?' WHERE entry=2000000290; /* content: Describe this vrykul. What does he look like? */
UPDATE db_script_string SET content_loc3='Aussehen, Mylady? Wie ein Vrykul, nehme ich an. Sie haben ihn mir nicht wirklich gezeigt. Seit der Sache mit dem Schattengewölbe...' WHERE entry=2000000291; /* content: Look like, my lady? A vrykul, I suppose. They did not actually show him to me. Ever since The Shadow Vault.... */
UPDATE db_script_string SET content_loc3='Ausreden interessieren mich nicht. Vielleicht haben sie Euch einen Namen gegeben?' WHERE entry=2000000292; /* content: I am not interested in excuses. Perhaps they gave you a name? */
UPDATE db_script_string SET content_loc3='Ruhig bleiben? Hast du auch nur eine Ahnung davon, was Kael\'thas mit denjenigen macht, die ihn enttäuschen, Geezle? Endloses Leiden und Schmerzen... es darf NICHTS schief gehen, Trottel.' WHERE entry=2000000292; /* content: Relax? Do you know what Kael\'thas does to those that fail him, Geezle? Eternal suffering and pain... Do NOT screw this up, fool. */
UPDATE db_script_string SET content_loc3='Ein Name? Oh, ja, der Name! Ich glaube, es war Iskalder!' WHERE entry=2000000293; /* content: A name? Oh, yes, the name! I believe it was Iskalder. */
UPDATE db_script_string SET content_loc3='Unsere Späher auf der Blutmythosinsel haben unseren Kontakt ausfindig gemacht. Dieser Narr, Velen, wird schon bald einen Moment nicht Acht geben und uns dadurch schutzlos ausgeliefert sein. Lange genug, damit wir zuschlagen können! Und nun geh mir aus den Augen, bevor ich dich töte...' WHERE entry=2000000293; /* content: Our Bloodmyst scouts have located our contact. The fool, Velen, will soon leave himself open and defenseless -- long enough for us to strike! Now get out of my sight before I vaporize you... */
UPDATE db_script_string SET content_loc3='Kehrt nach Jotunheim zurück und sagt ihnen, sie sollen ihn weiterschlafen lassen, bis ich komme. Ich werde mir diesen Vrykul mit eigenen Augen ansehen.' WHERE entry=2000000295; /* content: Return to Jotunheim and tell them to keep him asleep until I arrive. I will judge this vrykul with my own eyes. */
UPDATE db_script_string SET content_loc3='Auf der Stelle, Mylady.' WHERE entry=2000000296; /* content: Right away, my lady. */
UPDATE db_script_string SET content_loc3='%s hebt die Nagaflagge auf.' WHERE entry=2000000297; /* content: %s picks up the naga flag. */
UPDATE db_script_string SET content_loc3='Okay, Leute. Macht euch an die Arbeit und bringt diese Knochen an.' WHERE entry=2000000298; /* content: What\'s the big idea, Spark? Why\'d you call for this meeting? */
UPDATE db_script_string SET content_loc3='Ich fühle mich... anders.' WHERE entry=2000000299; /* content: I feel... different. */
UPDATE db_script_string SET content_loc3='AAAAAAAARGH!' WHERE entry=2000000299; /* content: What\'s the big idea? You nearly blew my cover, idiot! I told you to put the compass and navigation maps somewhere safe - not out in the open for any fool to discover. */
UPDATE db_script_string SET content_loc3='Der Meister hat viel riskiert, um an Informationen über den Verbleib der Exodar zu kommen. Deinetwegen wäre beinahe die gesamte Unternehmung geplatzt, einschließlich der Identität unseres Spions im innersten Kreis des Feindes.' WHERE entry=2000000300; /* content: The Master has gone to great lengths to secure information about the whereabouts of the Exodar. You could have blown the entire operation, including the cover of our spy on the inside. */
UPDATE db_script_string SET content_loc3='%s spürt, wie sein Blut in Wallung gerät und die Arglist des Raptors ihn durchströmt.' WHERE entry=2000000301; /* content: Relax, Spark! I have it all under control. We\'ll strip mine the Exodar right out from under \'em - making both you and I very, very rich in the process. */
UPDATE db_script_string SET content_loc3='Ruhig bleiben? Hast du auch nur eine Ahnung davon, was Kael\'thas mit denjenigen macht, die ihn enttäuschen, Geezle? Endloses Leiden und Schmerzen... es darf NICHTS schief gehen, Trottel.' WHERE entry=2000000302; /* content: Relax? Do you know what Kael\'thas does to those that fail him, Geezle? Eternal suffering and pain... Do NOT screw this up, fool. */
UPDATE db_script_string SET content_loc3='Unsere Späher auf der Blutmythosinsel haben unseren Kontakt ausfindig gemacht. Dieser Narr, Velen, wird schon bald einen Moment nicht Acht geben und uns dadurch schutzlos ausgeliefert sein. Lange genug, damit wir zuschlagen können! Und nun geh mir aus den Augen, bevor ich dich töte...' WHERE entry=2000000303; /* content: Our Bloodmyst scouts have located our contact. The fool, Velen, will soon leave himself open and defenseless -- long enough for us to strike! Now get out of my sight before I vaporize you... */
UPDATE db_script_string SET content_loc3='%s windet sich vor Schmerzen.' WHERE entry=2000000304; /* content: Yes, sir. It won\'t happen again... */
UPDATE db_script_string SET content_loc3='%s lacht.' WHERE entry=2000000305; /* content: %s laughs. */
UPDATE db_script_string SET content_loc3='Macht Euch das Wissen Angst, dass jene, die der Legion so inbrünstig dienen, sich bis zu dem Moment ihres Einsatzes als rechtschaffene Bürger Eurer nutzlosen Zivilisation ausgeben?' WHERE entry=2000000306; /* content: Does it frighten you to know that there are those that would serve the Legion with such devotion as to remain unwavering citizens of your pointless civilizations until called upon? */
UPDATE db_script_string SET content_loc3='Lebt in Angst oder sterbt durch den Willen Kael\'thas\'... es kommt auf das Gleiche heraus.' WHERE entry=2000000307; /* content: Live in fear, die by the will of Kael\'thas... It\'s all the same. */
UPDATE db_script_string SET content_loc3='Und jetzt werde ich Euch aufschlitzen!' WHERE entry=2000000308; /* content: And now, I cut you! */
UPDATE db_script_string SET content_loc3='Sterbt!' WHERE entry=2000000309; /* content: DIE! */
UPDATE db_script_string SET content_loc3='Grek probieren!' WHERE entry=2000000310; /* content: Grek try! */
UPDATE db_script_string SET content_loc3='Dieses Öl nicht gut für Grek! Sieht Grek aus wie Ihr, Schwächling in Robe?' WHERE entry=2000000311; /* content: This oil no good for Grek! What Grek look like to you, some weakling in robes? */
UPDATE db_script_string SET content_loc3='Danke für meine Rettung, $n! Mein Vater wird höchst erfreut sein!' WHERE entry=2000000313; /* content: Thank you for saving me, $N! My father will be delighted! */
UPDATE db_script_string SET content_loc3='Ihr könnt uns nicht davon abhalten, die Öffentlichkeit über Jainas Feigheit aufzuklären!' WHERE entry=2000000314; /* content: You can\'t stop us from exposing the truth about Jaina\'s cowardice! */
UPDATE db_script_string SET content_loc3='Die Leute werden Admiral Prachtmeer als den Helden kennen, der er wirklich war!' WHERE entry=2000000316; /* content: These people will know Admiral Proudmoore for the true hero he was! */
UPDATE db_script_string SET content_loc3='Unverschämter Narr! Ihr wolltet Zelemars Blut stehlen? Dafür werdet Ihr mit Eurem eigenen bezahlen!' WHERE entry=2000000317; /* content: Insolent fool! You thought to steal Zelemar\'s blood? You shall pay with your own! */
UPDATE db_script_string SET content_loc3='Hoffen wir, dass er das Licht sieht und er seine Energien von nun ab für den Aufbau des einst so schönen Quel\'Thalas einsetzt.' WHERE entry=2000000322; /* content: I can only hope that he will see the Light and instead turn his energies to restoring once-beautiful Quel\'Thalas. */
UPDATE db_script_string SET content_loc3='Frisches Brot zu verkaufen!' WHERE entry=2000000323; /* content: Get him out of there! */
UPDATE db_script_string SET content_loc3='Greif zu, solange es noch heiß ist!' WHERE entry=2000000324; /* content: Perhaps Ograbisi will use your head as a tiny little hat when I\'m through with you. */
UPDATE db_script_string SET content_loc3='Frisches Brot zu verkaufen!' WHERE entry=2000000325; /* content: Where I come from, you get shanked for opening another inmate\'s cell door! */
UPDATE db_script_string SET content_loc3='Greif zu, solange es noch heiß ist!' WHERE entry=2000000326; /* content: Ograbisi needs new hat. */
UPDATE db_script_string SET content_loc3='Der Eisbrocken ist geschmolzen!' WHERE entry=2000000327; /* content: The Ice Stone has melted! */
UPDATE db_script_string SET content_loc3='Ahune, stärker sollst du nicht mehr werden!' WHERE entry=2000000328; /* content: Ahune, your strength grows no more! */
UPDATE db_script_string SET content_loc3='Niemals wird Kälte herrschen!' WHERE entry=2000000329; /* content: Your frozen reign will not come to pass! */
UPDATE db_script_string SET content_loc3='Es stirbt! Der zweite Teil des Wortes der Verdammnis gehört uns! Ich...' WHERE entry=2000000330; /* content: It is dying! The second part of the Cipher of Damnation is ours. I... */
UPDATE db_script_string SET content_loc3='Ich schwinde... Kehrt zu Ar\'tor zurück... Keh.. rt... zu...' WHERE entry=2000000331; /* content: I am fading... Return to Ar\'tor... Ret... rn... to... */
UPDATE db_script_string SET content_loc3='Was für ein Kampf! Habt Ihr das gesehen, Rhydian?!' WHERE entry=2000000332; /* content: What a battle! Did you see that, Rhydian?! */
UPDATE db_script_string SET content_loc3='Wir haben Branns Nachricht erhalten und mit Vorbereitungen begonnen.' WHERE entry=2000000333; /* content: We received Brann\'s message, and we have begun preparations. */
UPDATE db_script_string SET content_loc3='Gerade rechtzeitig. Lasst es uns zu Ende bringen.' WHERE entry=2000000334; /* content: Just in time. Let\'s see this thing off, then. */
UPDATE db_script_string SET content_loc3='Bürger von Dalaran! Richtet Eure Blicke gen Himmel und beobachtet aufmerksam!' WHERE entry=2000000335; /* content: Citizens of Dalaran! Raise your eyes to the skies and observe! */
UPDATE db_script_string SET content_loc3='Heute wurde die Zerstörung dieser Welt abgewendet, im Widerstand gegen unsere eigenen Schöpfer!' WHERE entry=2000000336; /* content: Today our world\'s destruction has been averted in defiance of our very makers! */
UPDATE db_script_string SET content_loc3='Algalon der Beobachter, Herold der Titanen, wurde von unseren tapferen Kameraden in den Tiefen der Titanenstadt Ulduar bezwungen.' WHERE entry=2000000337; /* content: Algalon the Observer, herald of the titans, has been defeated by our brave comrades in the depths of the titan city of Ulduar. */
UPDATE db_script_string SET content_loc3='Algalon wurde zu uns gesandt, um über diese Welt zu richten.' WHERE entry=2000000338; /* content: Algalon was sent here to judge the fate of our world. */
UPDATE db_script_string SET content_loc3='Er fand eine Welt vor, deren Völker nicht mehr den Blaupausen der Titanen entsprachen. Eine Welt, in der nicht alles nach Plan gelaufen war.' WHERE entry=2000000339; /* content: He found a planet whose races had deviated from the titans\' blueprints. A planet where not everything had gone according to plan. */
UPDATE db_script_string SET content_loc3='Kalte Logik befand unseren Planeten nicht der Rettung würdig. Jedoch trägt Logik nicht der Macht des freien Willens Rechnung. Es ist an uns, zu beweisen, dass diese Welt es wert ist, gerettet zu werden.' WHERE entry=2000000340; /* content: Cold logic deemed our world not worth saving. Cold logic, however, does not account for the power of free will. It\'s up to each of us to prove this is a world worth saving. */
UPDATE db_script_string SET content_loc3='Dass unsere Leben es wert sind... gelebt zu werden.' WHERE entry=2000000341; /* content: That our lives... our lives are worth living. */
UPDATE db_script_string SET content_loc3='ACHTUNG! Lasst sie nicht durchbrechen! Konzentriert das Feuer! LOS, LOS, LOS!' WHERE entry=2000000344; /* content: INCOMING! Do not let them through! Focus fire! NOW, NOW, NOW! */
UPDATE db_script_string SET content_loc3='Die Macht der Träne der Monde gehört jetzt mir! Mir ganz allein!' WHERE entry=2000000345; /* content: The power of the Tear of the Moons is mine! Mine I say! */
UPDATE db_script_string SET content_loc3='%s beginnt, die Träne der Monde zu reiben.' WHERE entry=2000000346; /* content: %s begins to rub the Tear of the Moons. */
UPDATE db_script_string SET content_loc3='Macht! Glorreiche Macht!' WHERE entry=2000000347; /* content: Power! Glorious power! */
UPDATE db_script_string SET content_loc3='Erhebt Euch, meine Brüder!' WHERE entry=2000000348; /* content: Arise from the earth, my brothers! */
UPDATE db_script_string SET content_loc3='%s beginnt, seltsame Grunzgeräusche von sich zu geben. Die Träne der Monde fällt zu Boden und zerspringt.' WHERE entry=2000000348; /* content: %s begins to make strange grunting noises. The Tear of the Moons drops to the ground and shatters. */
UPDATE db_script_string SET content_loc3='Unsere Meister des Eisens haben eine Mission für uns!' WHERE entry=2000000349; /* content: Our iron masters have a mission for us! */
UPDATE db_script_string SET content_loc3='Kommt mit mir in die Berge, um den Plan der Meister auszuführen!' WHERE entry=2000000350; /* content: Follow me into the mountains to carry out the plan of the masters! */
UPDATE db_script_string SET content_loc3='Schmerzen... unerträgliche Schmerzen... Beendet meine Qualen... Ich bitte Euch!' WHERE entry=2000000350; /* content: I hurt... suffering unbearable... end my pain... I beg of you! */
UPDATE db_script_string SET content_loc3='Ich kehre aus dem Grabe zurück, um zu tun, was mein Meister befiehlt.' WHERE entry=2000000352; /* content: I return from the grave to do my master\'s bidding. */
UPDATE db_script_string SET content_loc3='Ein weiterer Versager...' WHERE entry=2000000353; /* content: Another failure... */
UPDATE db_script_string SET content_loc3='Dieser hier ist zu früh erwacht. Er beinhaltet Gefühle und Erinnerungen...' WHERE entry=2000000354; /* content: This one has awoken too soon. It retains emotion and memory... */
UPDATE db_script_string SET content_loc3='Der Lichkönig wird zufrieden sein mit diesem Initianden.' WHERE entry=2000000355; /* content: The Lich King will be pleased with this initiate. */
UPDATE db_script_string SET content_loc3='Nutzlos...' WHERE entry=2000000356; /* content: Useless... */
UPDATE db_script_string SET content_loc3='Ja, Lehrer.' WHERE entry=2000000360; /* content: Yes, instructor. */
UPDATE db_script_string SET content_loc3='Sofort, Lehrer.' WHERE entry=2000000361; /* content: Right away, instructor. */
UPDATE db_script_string SET content_loc3='Erhebt Euch, meine Diener. Erhebt Euch und labt Euch an den Schwachen!' WHERE entry=2000000362; /* content: Rise, minions. Rise and feast upon the weak! */
UPDATE db_script_string SET content_loc3='Sieg der Geißel!' WHERE entry=2000000365; /* content: Victory to the Scourge! */
UPDATE db_script_string SET content_loc3='Tod allen, die sich uns widersetzen!' WHERE entry=2000000366; /* content: Death to all that oppose us! */
UPDATE db_script_string SET content_loc3='Erbärmlich...' WHERE entry=2000000368; /* content: Pathetic... */
UPDATE db_script_string SET content_loc3='Was glaubt Ihr eigentlich, was Ihr da tut? Ihr wagt es, die Pläne meines Meisters zu durchkreuzen?' WHERE entry=2000000369; /* content: And just what do you think you are doing?  You dare to interfere with my master\'s experiment? */
UPDATE db_script_string SET content_loc3='Gefällt Euch, was wir hier getan haben? Vielleicht werfen wir diese Kristalle überall über der Scherbenwelt ab.' WHERE entry=2000000370; /* content: Do you like what we\'ve done here?  Perhaps we will drop these crystals from the sky all over Outland. */
UPDATE db_script_string SET content_loc3='Euer Versuch, das Land zu heilen und die Energien zu zügeln, die die Kolosse rufen und verrückt machen, langweilt mich. Goliathon, König der Kolosse, Prinz Kael\'thas und ich befehlen dir, den Kristall zu verteidigen!' WHERE entry=2000000371; /* content: I grow bored with your attempt to heal the land and quell the energies summoning and driving the colossi mad.  Goliathon, King of the Colossi, Prince Kael\'thas and I demand that you defend the crystal! */
UPDATE db_script_string SET content_loc3='Wir werden uns bald wiedersehen.' WHERE entry=2000000372; /* content: We will meet again soon. */
UPDATE db_script_string SET content_loc3='Wo kommt das denn her? Das bringe ich besser runter zu den Schiffen, bevor der Großknecht es sieht!' WHERE entry=2000000373; /* content: Where\'d this come from? I better get this down to the ships before the foreman sees it! */
UPDATE db_script_string SET content_loc3='Ich werde es einfach hier lassen und keiner weiß was davon!' WHERE entry=2000000374; /* content: I\'ll just leave it here and nobody will be the wiser! */
UPDATE db_script_string SET content_loc3='Bleibt in meiner Nähe, $n. Ich werde Euch beschützen, falls etwas schief geht.' WHERE entry=2000000376; /* content: Stand near me, $N. I will protect you should anything go wrong. */
UPDATE db_script_string SET content_loc3='Malfurion!' WHERE entry=2000000377; /* content: Malfurion! */
UPDATE db_script_string SET content_loc3='Alles ist von schrecklichen Alpträumen durchzogen, Malfurion. Was geht im Smaragdgrünen Traum nur vor? Was könnte ihn derartig grausam verzerren?' WHERE entry=2000000379; /* content: It was shrouded in nightmares, Malfurion. What is happening in the Dream? What could cause such atrocities? */
UPDATE db_script_string SET content_loc3='Ich habe es auch gespürt, Malfurion. Dunkle Zeiten werfen ihre Schatten voraus.' WHERE entry=2000000381; /* content: I sensed as much, Malfurion. Dark days loom ahead. */
UPDATE db_script_string SET content_loc3='Lebt wohl, alter Freund... Lebt wohl...' WHERE entry=2000000386; /* content: Farewell, old friend... Farewell... */
UPDATE db_script_string SET content_loc3='Lasst uns zum Hain zurückkehren, Sterblicher.' WHERE entry=2000000387; /* content: Let us return to the grove, mortal. */
UPDATE db_script_string SET content_loc3='Ok, lasst uns gehen. Ich kenne da einen perfekten Ort!' WHERE entry=2000000388; /* content: Ok, let\'s go.  I know just the perfect spot! */
UPDATE db_script_string SET content_loc3='Was... passiert mit... mir? Haltet diese Katze von mir fern!' WHERE entry=2000000389; /* content: What... is happening... to me?  Get this cat away from me! */
UPDATE db_script_string SET content_loc3='Samtpfote sieht Viera Sonnenwisper mit hungrigen Augen an.' WHERE entry=2000000390; /* content: Twinkle looks at Viera Sunwhisper with very hungry eyes. */
UPDATE db_script_string SET content_loc3='Kommt raus, kleine Vögelchen! Mug\'gok wollen spielen!' WHERE entry=2000000392; /* content: Come out, little birdies! Mug\'gok want to play! */
UPDATE db_script_string SET content_loc3='Nicht verletzen Gefühle von Mug\'gok, Vogelleute!' WHERE entry=2000000393; /* content: No hurt Mug\'gok\'s feelings, bird-men! */
UPDATE db_script_string SET content_loc3='Gut! Wenn Vögelchen es so wollen! Mug\'gok finden neue Freunde!' WHERE entry=2000000394; /* content: Fine! Birdies be dat way! Mug\'gok find new friends! */
UPDATE db_script_string SET content_loc3='%s staubt den Sockel des Artefakts ab.' WHERE entry=2000000395; /* content: %s dusts the base of the artifact. */
UPDATE db_script_string SET content_loc3='%s untersucht die riesige Vase minutiös.' WHERE entry=2000000396; /* content: %s meticulously examines the large vase. */
UPDATE db_script_string SET content_loc3='Armer Dorius. Wenn ich diese Dunkeleisenzwerge in die Finger bekomme, hat ihr letztes Stündlein geschlagen!' WHERE entry=2000000397; /* content: Poor Dorius. If I ever get my hands on those Dark Irons, so help me... */
UPDATE db_script_string SET content_loc3='Spendet den Wohltätigkeitsorganisationen, die den Opfern dieser schweren Zeit zu helfen versuchen! Bitte.' WHERE entry=2000000398; /* content: Give to the charities who seek to help the victims of these hard times! Please. */
UPDATE db_script_string SET content_loc3='Vergesst nicht die Waisen aus Sturmwind!' WHERE entry=2000000399; /* content: Don\'t forget the orphans of Stormwind! */
UPDATE db_script_string SET content_loc3='Gebt von Herzen und aus vollen Taschen! Gebt für die Kinder aus Sturmwind, die ihre Eltern verloren haben.' WHERE entry=2000000400; /* content: Give of your hearts and your purses! Give to the children of Stormwind who have lost their parents. */
UPDATE db_script_string SET content_loc3='Helft den Kindern aus Sturmwind... den Opfern des Krieges und der Seuche!' WHERE entry=2000000401; /* content: Help the children of Stormwind... victims of the war and plague! */
UPDATE db_script_string SET content_loc3='%s ruft die mächtigen Armeen der Geißel zu Hilfe!' WHERE entry=2000000402; /* content: %s calls upon the mighty armies of the Scourge! */
UPDATE db_script_string SET content_loc3='Armeen der Geißel, erhört meinen Ruf! Die Scharlachrote Apokalypse hat begonnen! Reißt dieses Land in Stücke und lasst nur noch Tod auf eurem Weg zurück!' WHERE entry=2000000403; /* content: Armies of the Scourge, hear my call! The scarlet apocalypse has begun! Tear this land asunder and leave only death in your wake! */
UPDATE db_script_string SET content_loc3='Ich arbeite so schnell ich kann, Hauptmann!' WHERE entry=2000000407; /* content: I\'m workin\' as fast as I can, cap\'n! */
UPDATE db_script_string SET content_loc3='Ich kann nicht in diesem Tempo weiter machen, Sir! Falls mich die Geißel nicht umbringt, dann die Erschöpfung!' WHERE entry=2000000408; /* content: I can\'t keep this pace up, sir! If the Scourge don\'t get me, exhaustion will! */
UPDATE db_script_string SET content_loc3='Geleidet Euch wohl, Todesritter...' WHERE entry=2000000409; /* content: Suffer well, death knight... */
UPDATE db_script_string SET content_loc3='Das Reich der Schatten wartet...' WHERE entry=2000000410; /* content: The realm of shadows awaits... */
UPDATE db_script_string SET content_loc3='Was sollen wir machen? Wir haben alles verloren?' WHERE entry=2000000411; /* content: What are we to do? We\'ve lost everything! */
UPDATE db_script_string SET content_loc3='Wir kämpfen! Wir drängen sie zurück, so wie wir es immer getan haben!' WHERE entry=2000000413; /* content: We fight! We push them back, just as we have always done! */
UPDATE db_script_string SET content_loc3='%s wacht erschrocken auf.' WHERE entry=2000000467; /* content: %s wakes up, startled. */
UPDATE db_script_string SET content_loc3='Was erwarten sie denn, was passiert, wenn sie die Fledermäuse zwingen, in so einem Winkel hereinzukommen? Genickbrüche und schlecht schmeckenden Fledermauseintopf, das ist, was sie kriegen!' WHERE entry=2000000467; /* content: What do they expect, making the bats come in at that angle? Broken necks and gamey bat stew, that\'s what they get. */
UPDATE db_script_string SET content_loc3='War... War alles ein Traum? Azurmythos, Blutmythos, Zangarmarschen, Schattenmond... sogar Zul\'Aman... Jane! Jane, wo bist du!' WHERE entry=2000000468; /* content: Was... Was it all a dream? Azuremyst, Bloodmyst, Zangarmarsh, Shadowmoon... even Zul\'Aman... Jane! Jane where are you! */
UPDATE db_script_string SET content_loc3='Ich bin hier, Vater. Du hast den ganzen Tag lang geschlafen. Du musst sehr müde sein!' WHERE entry=2000000469; /* content: I\'m right here, daddy. You\'ve been asleep all day long. You sure must be tired! */
UPDATE db_script_string SET content_loc3='Ah, wunderbar. Es war alles nur ein Traum.' WHERE entry=2000000470; /* content: Ah, wonderful. It was all just a dream... */
UPDATE db_script_string SET content_loc3='%s spielt weiter am Strand.' WHERE entry=2000000471; /* content: %s resumes playing on the beach. */
UPDATE db_script_string SET content_loc3='%s seufzt und stellt die Flaschen zur Seite.' WHERE entry=2000000474; /* content: Putting the bottles away, %s sighs. */
UPDATE db_script_string SET content_loc3='Was für ein Schuss!' WHERE entry=2000000482; /* content: What a beautiful shot! */
UPDATE db_script_string SET content_loc3='Ha! Volltreffer!' WHERE entry=2000000483; /* content: Hah! Bullseye! */
UPDATE db_script_string SET content_loc3='Ahhh... mir läuft es eiskalt den Rücken herunter.' WHERE entry=2000000484; /* content: Agh... a chill ran up my spine. */
UPDATE db_script_string SET content_loc3='Ich hoffe, dass sie das Licht erblicken werden.' WHERE entry=2000000485; /* content: They\'ll see the light, I hope. */
UPDATE db_script_string SET content_loc3='Ich... Ich habe genug gesehen, glaube ich... das ist überaus anstrengend...' WHERE entry=2000000522; /* content: I... I\'ve seen enough I think... so very draining... */
UPDATE db_script_string SET content_loc3='%s überfliegt die Seiten des Buchs.' WHERE entry=2000000524; /* content: %s skims the pages of the book. */
UPDATE db_script_string SET content_loc3='Glorreich, in der Tat, Malzdarr. Es scheint, mein Plan ist aufgegangen. Würdet Ihr mir nicht zustimmen, sterbliches Wesen?' WHERE entry=2000000526; /* content: Glorious, indeed, Maltrake. It looks as if my plan worked perfectly. Wouldn\'t you say so, mortal? */
UPDATE db_script_string SET content_loc3='%s lacht ein reptilienhaftes Lachen... zumindest haltet Ihr es für ein Lachen.' WHERE entry=2000000527; /* content: %s lets loose a reptilian laugh... at least you think it\'s a laugh. */
UPDATE db_script_string SET content_loc3='Ihr hättet der Schwarzfelslegion sicher nicht geholfen, wenn wir Euch nur um Unterstützung gebeten hätten, oder?' WHERE entry=2000000528; /* content: You would not have helped the legion of Blackrock had we just asked for your assistance, now would you? */
UPDATE db_script_string SET content_loc3='Was sollen wir mit ihnen tun, Meister? Sie sind uns nicht länger von Nutzen.' WHERE entry=2000000529; /* content: What will we do with them, master? They are of no use to us anymore. */
UPDATE db_script_string SET content_loc3='RUHE, SCHWACHKOPF!' WHERE entry=2000000530; /* content: SILENCE FOOL! */
UPDATE db_script_string SET content_loc3='Ich verlasse Euch jetzt, sterbliche Wesen. Lebend und mit diesem Plunder. Malzdarr! Gebt ihnen den Plunder.' WHERE entry=2000000532; /* content: I leave you now, mortals. Alive and with these trinkets. Maltrake! Present them with the trinkets! */
UPDATE db_script_string SET content_loc3='Sofort, Meister!' WHERE entry=2000000533; /* content: Right away, master! */
UPDATE db_script_string SET content_loc3='%s schlägt schneller mit seinen riesigen Schwingen. Gleich wird er abheben.' WHERE entry=2000000534; /* content: %s begins to flap his massive wings faster. He is preparing for flight. */
UPDATE db_script_string SET content_loc3='Oh ja, die abgestreifte Drachenhaut: verliert sie nicht. In der Brennenden Steppe gibt es welche meiner Art, die Dinge fertigen, die nur die Abgestreifte Haut des schwarzen Drachenschwarms festigen kann.' WHERE entry=2000000535; /* content: Oh yes, the molt - do not lose it. There are those of my kin in the Burning Steppes that would craft items that only the molt of the black dragonflight could fortify. */
UPDATE db_script_string SET content_loc3='Die Schwarzfelslegion naht, ihr Winzlinge! Die Erde wird brennen und der Himmel in Flammen stehen. Niemand wird überleben!' WHERE entry=2000000536; /* content: The legion of Blackrock comes, dwarflings! We shall scorch the earth and set fire to the heavens. None shall survive... */
UPDATE db_script_string SET content_loc3='Wir müssen uns beeilen.' WHERE entry=2000000537; /* content: We must hurry. */
UPDATE db_script_string SET content_loc3='Geister des Waldes, kommt hervor und folgt dem Ruf der Natur!' WHERE entry=2000000538; /* content: Spirits of the forest come forth and heed nature\'s call! */
UPDATE db_script_string SET content_loc3='Geister des Waldes, ihr werdet gebraucht! Eilt zum verbrannten Tal!' WHERE entry=2000000539; /* content: Spirits of the forest, you are needed! Make haste to the Charred Vale! */
UPDATE db_script_string SET content_loc3='Mal sehen... Wenn ich Recht habe, dann... Hmmm...' WHERE entry=2000000540; /* content: Now, let\'s see... If I am correct, then... Hmmm... */
UPDATE db_script_string SET content_loc3='Blau plus rot... Ich würde mal sagen...' WHERE entry=2000000541; /* content: Blue plus red... I predict... */
UPDATE db_script_string SET content_loc3='$n, das ist es! Endlich weiß ich Bescheid!' WHERE entry=2000000542; /* content: $N, that\'s it! I have finally figured it out! */
UPDATE db_script_string SET content_loc3='Danke vielmals.' WHERE entry=2000000558; /* content: Thank you, friend. */
UPDATE db_script_string SET content_loc3='Ich stehe auf ewig in Eurer Schuld, Freund.' WHERE entry=2000000559; /* content: I am forever indebted to you, friend. */
UPDATE db_script_string SET content_loc3='Mein Dank sei mit Euch, $GFremder:Fremde;.' WHERE entry=2000000561; /* content: You have my thanks, stranger. */
UPDATE db_script_string SET content_loc3='%s lässt ein lautstarkes Zeichen der Zustimmung hören.' WHERE entry=2000000562; /* content: %s trumpets in approval! */
UPDATE db_script_string SET content_loc3='Meine Brüder und Schwestern, hört mir zu! Wir müssen über das Überleben unseres Volkes sprechen. Nur mit Mühe haben wir die letzte Katastrophe überstanden, doch fürchte ich nun, dass die nächste uns alle vernichten wird!' WHERE entry=2000000571; /* content: My brothers and sisters. Listen to me. We must speak of the survival of our people. We have narrowly survived one calamity, but I fear the next shall destroy us. */
UPDATE db_script_string SET content_loc3='Seht Ihr denn nicht, was hier geschieht? Öffnet eure Augen. Die Wahrheit ist offensichtlich. Was ist diese neue Allianz? Haben wir unsere alten Verbündeten nur deshalb verraten, um uns nun mit den Feinden allen Lebens einzulassen?' WHERE entry=2000000572; /* content: Can you not see what is happening? Open your eyes. It is laid bare for all to see. What is this new alliance? Why have we betrayed our allies of old, only to take up with the enemies of all that live? */
UPDATE db_script_string SET content_loc3='Nein, ihr liegt falsch! Wir sind es, die verraten wurden!' WHERE entry=2000000573; /* content: No, you\'re wrong! We were the ones betrayed! */
UPDATE db_script_string SET content_loc3='Wann waren die Zwerge schon unsere Freunde?' WHERE entry=2000000574; /* content: When have the dwarves ever been our friends? */
UPDATE db_script_string SET content_loc3='Die Menschen haben uns in unserer dunkelsten Stunde allein gelassen! Es war ihr Prinz, der den Angriff auf unser Land anführte!' WHERE entry=2000000575; /* content: The humans abandoned us in our darkest hour! Their prince himself led the assault on our lands. */
UPDATE db_script_string SET content_loc3='Ihr seid ein Narr! Die Gefolgsleute des Prinzen waren dieselben Verlassenen, mit denen wir uns jetzt verbündet haben - doch er hat uns verraten!' WHERE entry=2000000576; /* content: You fool! The prince\'s minions were the very Forsaken we have now allied ourselves with, and now he has betrayed us! */
UPDATE db_script_string SET content_loc3='Der Waldläufergeneral von Silbermond wird ein loyaler Verbündeter sein!' WHERE entry=2000000577; /* content: The Ranger-General of Silvermoon will be a loyal ally! */
UPDATE db_script_string SET content_loc3='Ihr sprecht von ihr, als sei sie eine Freundin des Volkes? Ein Narr seid Ihr! Die Dunkle Fürstin ist nicht mehr die Sylvanas von einst.' WHERE entry=2000000578; /* content: You speak of her as friend? You are a fool. This "Dark Lady" is not Sylvanas. */
UPDATE db_script_string SET content_loc3='Das Blut ist die Wahrheit. Wir können niemandem außer uns selbst vertrauen!' WHERE entry=2000000579; /* content: Blood will be true. We can trust in none but our own! */
UPDATE db_script_string SET content_loc3='Blut? Und was ist mit unseren angeblichen Freunden, den Orcs? Sie sollen uns helfen?' WHERE entry=2000000580; /* content: Blood? And these orcs, these fair weather friends? They will aid us? */
UPDATE db_script_string SET content_loc3='Der Argentumkreuzzug lädt Euch ein, beim Argentumturnier Eure Fähigkeiten im ehrenhaften Kampf gegen andere Helden von Azeroth zu beweisen.' WHERE entry=2000000580; /* content: The Argent Crusade invites you to test your skills in honorable combat against other heroes of Azeroth at the Argent Tournament. */
UPDATE db_script_string SET content_loc3='Ein großes Übel liegt über unserem Land! Es wächst mit jedem Tag. Unsere Sucht nach Magie schwächt uns und wird uns in den Untergang drängen, so wie es schon einmal geschehen ist!' WHERE entry=2000000581; /* content: A great blight lies across our land! It grows every day. Our need for magic weakens us, and will bring us to destruction, just as it has before! */
UPDATE db_script_string SET content_loc3='Die Champions der größten Nationen der Welt haben sich auf dem Argentumturnierplatz im Nordosten Eiskrones versammelt, um allen Herausforderern entgegen zu treten.' WHERE entry=2000000581; /* content: The champions of the world\'s great nations have gathered at the Argent Tournament Grounds in northeastern Icecrown to face all challengers. */
UPDATE db_script_string SET content_loc3='Wie sollen wir ohne Magie überleben?' WHERE entry=2000000582; /* content: How are we to live without magic? */
UPDATE db_script_string SET content_loc3='Seht euch nur die Wunder dieser Stadt an! Das ist Magie!' WHERE entry=2000000583; /* content: Look at the wonders of this city! This is magic. */
UPDATE db_script_string SET content_loc3='Ihr bezeichnet uns als Verräter?' WHERE entry=2000000585; /* content: You call us traitors? */
UPDATE db_script_string SET content_loc3='Die Magister sind allesamt Verräter! Sie haben unser Schicksal besiegelt!' WHERE entry=2000000586; /* content: The magisters are the traitors! They have sealed our doom. */
UPDATE db_script_string SET content_loc3='Idealistisches Geschwätz. Geht zurück in Euren Wald!' WHERE entry=2000000587; /* content: Idealistic blather. Go back to your forests! */
UPDATE db_script_string SET content_loc3='Also hat das Orakel beschlossen, Euch zu helfen, $g Fremder : Fremde;. Nun gut, ich werde mein Wissen mit Euch teilen.' WHERE entry=2000000587; /* content: So the oracle has decided to aid you, outlander. Very well, I will offer you what i know. */
UPDATE db_script_string SET content_loc3='Ihr wollt, dass wir wie Wilde leben!' WHERE entry=2000000588; /* content: You would have us live like the savages! */
UPDATE db_script_string SET content_loc3='Euer Gefährte sucht den Splitterhornpatriarchen Farunn, der sich von seinen Brüdern fernhält.' WHERE entry=2000000588; /* content: Your companion seeks the shardhorn patriarch Farunn, who holds himself aloof from his brothers. */
UPDATE db_script_string SET content_loc3='Nein, nicht wie Wilde! Vielmehr in Buße, mit einem reinen Gewissen. Die Exzesse unseres Volkes werden uns am Ende vernichten und damit alles, was wir uns aufgebaut haben, zunichtemachen.' WHERE entry=2000000589; /* content: No, not savagery! Conscience. The excesses of our people will destroy us, and all that we have struggled to build. */
UPDATE db_script_string SET content_loc3='Wir haben nicht so hart um unser Überleben gekämpft nur, um uns am Ende selbst den Todesstoß zu geben.' WHERE entry=2000000590; /* content: We have not struggled so hard to survive, only to bring about the true destruction. */
UPDATE db_script_string SET content_loc3='Kehrt zu Eurem Gefährten zurück. Sagt ihm, dass er losgehen und in Farunn seinen Preis einfordern soll.' WHERE entry=2000000590; /* content: Return to your companion. Tell him to go forth and claim his prize in Farunn. */
UPDATE db_script_string SET content_loc3='Wir stehen vor dem Abgrund der Zerstörung. Wir müssen unser bisheriges Handeln überdenken.' WHERE entry=2000000591; /* content: We stand on the brink of destruction. We must reconsider this rash course of action. */
UPDATE db_script_string SET content_loc3='Sobald er dies getan hat, sagt ihm, dass er dieses Land friedlich verlassen soll. Er gehört hier nicht her.' WHERE entry=2000000591; /* content: Once he has done so, tell him to leave this land in peace. He does not belong here. */
UPDATE db_script_string SET content_loc3='Wenn wir alle mit einer Stimme sprechen, kann man uns nicht ignorieren!' WHERE entry=2000000592; /* content: If we speak as one, we cannot be ignored! */
UPDATE db_script_string SET content_loc3='Gebt uns eure Stimmen! Jetzt ist der Zeitpunkt gekommen, an dem wir zur Rettung unseres Volkes handeln müssen.' WHERE entry=2000000593; /* content: Lend us your voices! Now is the time we must act to save our people. */
UPDATE db_script_string SET content_loc3='Lor\'themar muss uns anhören! Wir werden nicht einfach schweigen!' WHERE entry=2000000594; /* content: Lor\'themar must hear us! We will not be silenced! */
UPDATE db_script_string SET content_loc3='Das sollte es gewesen sein. Ruft mich, wenn ihr meine Dienste noch einmal benötigt.' WHERE entry=2000000595; /* content: That should take care of that. Summon me if you have further need of my services. */
UPDATE db_script_string SET content_loc3='Die Allianz wird für ihren Verrat bezahlen! Niemals werden wir vergeben und vergessen, was geschehen ist.' WHERE entry=2000000596; /* content: We will repay the betrayal of the Alliance! We shall never forget, and never forgive! */
UPDATE db_script_string SET content_loc3='Ihr behaupt, unser Leben wäre nicht besser als vorher? Wie könnt ihr die Besserung unserer Lebensumstände einfach verleugnen?' WHERE entry=2000000597; /* content: How can you say our lives are not better now? How can you deny the betterment of our lives? */
UPDATE db_script_string SET content_loc3='Lor\'themar hat Silbermond zu etwas gemacht, auf das man wieder stolz sein kann!' WHERE entry=2000000598; /* content: Lor\'themar has remade Silvermoon into something we may truly be proud of. */
UPDATE db_script_string SET content_loc3='Sind das Winterhyazinthen? Für mich?' WHERE entry=2000000599; /* content: Are those winter hyacinths? For me? */
UPDATE db_script_string SET content_loc3='Unsere neuen Verbündeten werden uns zu alter Größe verhelfen. Dieses Bündnis ist die Wahrheit und man wird uns nicht noch einmal verraten!' WHERE entry=2000000599; /* content: Our new allies will bring us back to our former glory. These bonds will be true, and we will not be betrayed again! */
UPDATE db_script_string SET content_loc3='Als sich der Neutralisierungswirkstoff im See verteilt, scheint sich das Wasser, das zuvor durch den Kristall verseucht war, zu klären.' WHERE entry=2000000633; /* content: As the neutralizing agent mixes into the waters of the lake, the water that was irradiated by the crystal seems to clear. */
UPDATE db_script_string SET content_loc3='Nur zu Tedon, probiert den Neutralisierungswirkstoff an den mutierten Kreaturen aus.' WHERE entry=2000000634; /* content: Tedon, go ahead and try the neutralizing agent on the mutated creature. */
UPDATE db_script_string SET content_loc3='Es wird wohl langsam Zeit, dass man dich aus dem Käfig lässt.' WHERE entry=2000000635; /* content: Looks like it\'s time to let you out of this cage. */
UPDATE db_script_string SET content_loc3='So, und nun der Neutralisierungswirkstoff...' WHERE entry=2000000636; /* content: Now, to put the neutralizing agent on it... */
UPDATE db_script_string SET content_loc3='Erfolg!' WHERE entry=2000000637; /* content: Success! */
UPDATE db_script_string SET content_loc3='Das wird nie im Leben funktionieren! Das Gebäude wird in sich zusammenbrechen, wenn wir es nicht besser abstützen.' WHERE entry=2000000645; /* content: This won\'t work at all! The buildings will surely collapse if we do not shore up the support beams. */
UPDATE db_script_string SET content_loc3='Das funktioniert niemals! Wir müssen das Fundament verstärken, oder die gesamte Konstruktion wird einstürzen!' WHERE entry=2000000646; /* content: This won\'t do at all! We have to secure the foundation or the entire structure will crumble! */
UPDATE db_script_string SET content_loc3='Dieses Bauwerk verstößt gegen mindestens 8 verschiedene Bestimmungen!' WHERE entry=2000000647; /* content: This structure violates at least 8 different ordinances! */
UPDATE db_script_string SET content_loc3='Hm, das sieht schon viel besser aus. Sehen wir uns mal das Innere an.' WHERE entry=2000000649; /* content: Hrm, this looks much better. Let\'s have a look on the inside. */
UPDATE db_script_string SET content_loc3='Bei der faulen Warze von Kil\'Jaedans stinkendem Leichnam, die gesamte Rückseite fehlt! Wie kann man von jemanden erwarten, hier zu wohnen? Ich habe genug gesehen!' WHERE entry=2000000650; /* content: By the foul teat of Kil\'Jaeden\'s rotted torso, the entire backside is blown out! How can someone be expected to live in here? I\'ve seen enough! */
UPDATE db_script_string SET content_loc3='Das sollte funktionieren...' WHERE entry=2000000655; /* content: This should do it... */
UPDATE db_script_string SET content_loc3='Wacht auf, kleine Kreatur. Erwacht aus Eurem Schlummer... Ihr seid jetzt in Sicherheit.' WHERE entry=2000000656; /* content: Wake up little creature. Wake up from your slumber... You are safe now. */
UPDATE db_script_string SET content_loc3='%s stöhnt.' WHERE entry=2000000657; /* content: %s groans. */
UPDATE db_script_string SET content_loc3='%s setzt sich auf.' WHERE entry=2000000658; /* content: %s sits up. */
UPDATE db_script_string SET content_loc3='Wo... Wo bin ich? Was.... ist das für ein Ort?' WHERE entry=2000000659; /* content: Whe... Where am I? What... What is this place? */
UPDATE db_script_string SET content_loc3='%s fällt wieder ins Koma.' WHERE entry=2000000661; /* content: %s falls back into a coma. */
UPDATE db_script_string SET content_loc3='Das bedeuten Krieg!' WHERE entry=2000000661; /* content: This mean war! */
UPDATE db_script_string SET content_loc3='Wie soll ich zum Arbeiten kommen, wenn dieses Ding hier Ketzereien verbreitet?' WHERE entry=2000000663; /* content: How can I be expected to get any work done with that thing spreading its heresy? */
UPDATE db_script_string SET content_loc3='Ich bin der Herold von Mechazod. Ihr werdet vom Fluch befreit werden!' WHERE entry=2000000663; /* content: I am the herald of Mechazod. You will be decursed! */
UPDATE db_script_string SET content_loc3='Eure Einmischung findet hier ein Ende. Mechazod wird Euch von Eurem Fluch befreien, wenn ich mit Euch fertig bin.' WHERE entry=2000000664; /* content: Your meddling is at an end. Mechazod will relieve your curse once I am done with you. */
UPDATE db_script_string SET content_loc3='Wir brauchen Euresgleichen hier nicht.' WHERE entry=2000000665; /* content: We do not need your kind here. */
UPDATE db_script_string SET content_loc3='ED-210 online!' WHERE entry=2000000665; /* content: ED-210 online! */
UPDATE db_script_string SET content_loc3='%s drängt Euch zu folgen.' WHERE entry=2000000666; /* content: %s urges you to follow him. */
UPDATE db_script_string SET content_loc3='Twonkie!' WHERE entry=2000000666; /* content: Twonky! */
UPDATE db_script_string SET content_loc3='%s deutet auf das Totem von Coo.' WHERE entry=2000000667; /* content: %s points to the totem of Coo. */
UPDATE db_script_string SET content_loc3='%s gibt Euch ein Zeichen zu folgen.' WHERE entry=2000000668; /* content: %s motions for you to follow him. */
UPDATE db_script_string SET content_loc3='[Furbolg] Ritk kin\'chikx azul azur oben...' WHERE entry=2000000669; /* content: [Furbolg] Ritk kin\'chikx azul azure summit... */
UPDATE db_script_string SET content_loc3='[Furbolg] Coo xip flieg... über ilos Fluss. Findet Tikti.' WHERE entry=2000000670; /* content: [Furbolg] Coo xip fly... Jump ilos river. Find Tikti. */
UPDATE db_script_string SET content_loc3='%s zeigt auf die andere Seite des Flusses.' WHERE entry=2000000671; /* content: %s points across the river. */
UPDATE db_script_string SET content_loc3='%s fordert Euch auf zu folgen.' WHERE entry=2000000672; /* content: %s asks that you follow him. */
UPDATE db_script_string SET content_loc3='[Furbolg] Ihr weit mixik tak gekommen. Blicken über den kitch\'kal Fluss. Folgen. Ilog zu Yor.' WHERE entry=2000000673; /* content: [Furbolg] Far you mixik tak come. Gaze upon the kitch\'kal river. Follow. Ilog to Yor. */
UPDATE db_script_string SET content_loc3='[Furbolg] Geht... sucht yitix\'kil Grund von Fluss. Süden!' WHERE entry=2000000674; /* content: [Furbolg] Go... Search yitix\'kil bottom river. South! */
UPDATE db_script_string SET content_loc3='[Furbolg] Kommt, $n. Lasst uns das Wasser gemeinsam verlassen, geläutert.' WHERE entry=2000000675; /* content: [Furbolg] Come, $n. Let us leave the water together, purified. */
UPDATE db_script_string SET content_loc3='Habt Ihr Drull schon befreit? Ich weiß nicht, wo er festgehalten wird.' WHERE entry=2000000676; /* content: Have you freed Drull yet? I don\'t know where he\'s being held. */
UPDATE db_script_string SET content_loc3='[Furbolg] Wir gehen jetzt, zusammen. Wir werden Vark aufsuchen.' WHERE entry=2000000676; /* content: [Furbolg] We go now, together. We will seek Vark. */
UPDATE db_script_string SET content_loc3='Ich werde Euch nur aufhalten, aber kein rosahäutiger Mensch kann mir jetzt noch meine Freiheit nehmen!' WHERE entry=2000000677; /* content: I will only slow you down, but no pink-skinned humans will keep me from freedom! */
UPDATE db_script_string SET content_loc3='[Furbolg] Folgt mir zu Vark.' WHERE entry=2000000677; /* content: [Furbolg] Follow me to Vark. */
UPDATE db_script_string SET content_loc3='[Furbolg] Beeilt Euch, $n. Vark erwartet Euch.' WHERE entry=2000000678; /* content: [Furbolg] Hurry, $n. Vark awaits you. */
UPDATE db_script_string SET content_loc3='[Furbolg] Ich danke Euch, $n!' WHERE entry=2000000680; /* content: [Furbolg] Thank you, $n! */
UPDATE db_script_string SET content_loc3='[Furbolg] Die Prophezeiung ist wahr!' WHERE entry=2000000681; /* content: [Furbolg] The prophecy is true! */
UPDATE db_script_string SET content_loc3='[Furbolg] Die Furbolgs der Tannenruhfeste werden Eure Tapferkeit nicht so schnell vergessen!' WHERE entry=2000000682; /* content: [Furbolg] The Stillpine furbolgs will not soon forget your bravery! */
UPDATE db_script_string SET content_loc3='[Furbolg] Die noch bei der Tannenruhfeste Verbliebenen werden Euch als Held willkommen heißen!' WHERE entry=2000000683; /* content: [Furbolg] Those remaining at Stillpine Hold will welcome you as a hero! */
UPDATE db_script_string SET content_loc3='Adamant, könntet Ihr bitte einen Blick auf diese Tafel werfen und versuchen, die Runen zu entziffern?' WHERE entry=2000000691; /* content: Adamant, could you please take a look at this tablet and try to decipher the runes? */
UPDATE db_script_string SET content_loc3='Natürlich! Alles was du magst, Schätzchen.' WHERE entry=2000000692; /* content: Of course! Anything for you, darling! */
UPDATE db_script_string SET content_loc3='Gut, dann sehe ich mir mal die Schrifttafel an.' WHERE entry=2000000693; /* content: Alright, let me take a look at that tablet. */
UPDATE db_script_string SET content_loc3='%s liest die Schrifttafel langsam und seine Lippen formen die Worte der alten Sprache.' WHERE entry=2000000694; /* content: %s slowly reads the tablet, mouthing the words in their native tongue. */
UPDATE db_script_string SET content_loc3='Kriegsherr der Naga? Oh verdammt...' WHERE entry=2000000695; /* content: Naga warlord! Oh dear... */
UPDATE db_script_string SET content_loc3='Nun, Ihr werdet nicht mögen, was ich Euch zu sagen habe. Diese Schrifttafel ist ein Dokument der Naga. Ihr Anführer, Kriegsherr Sriss\'tiz, hat die gewaltsame Eroberung der restlichen Insel befohlen.' WHERE entry=2000000696; /* content: Well, you aren\'t going to like what I have to tell you, young missy. This tablet is a naga communication. Their leader, Warlord Sriss\'tiz, has ordered his naga to take the rest of the island by force. */
UPDATE db_script_string SET content_loc3='Sieht aus, als würde er aus Nazjatar Verstärkung erhalten!' WHERE entry=2000000697; /* content: And it looks like he\'s got reinforcements comin\' from Nazjatar! */
UPDATE db_script_string SET content_loc3='Wir müssen etwas dagegen unternehmen!' WHERE entry=2000000698; /* content: We\'ve got to do something about this! */
UPDATE db_script_string SET content_loc3='Danke, Adamant. Ich glaube, ich kenne die richtige Person für diese Aufgabe.' WHERE entry=2000000699; /* content: Thank you, Adamant. I think I know just the person for the job. */
UPDATE db_script_string SET content_loc3='Sprecht mit mir, um Euch meine Geschichte anzuhören.' WHERE entry=2000000813; /* content: Speak with me to hear my tale. */
UPDATE db_script_string SET content_loc3='Seht ihr Leute denn nie in eurem Briefkasten nach?' WHERE entry=2000000827; /* content: Don\'t you people ever check the mail? */
UPDATE db_script_string SET content_loc3='Wie soll ich nur all diese Botschaften zustellen?' WHERE entry=2000000828; /* content: How am I expected to deliver all of these messages? */
UPDATE db_script_string SET content_loc3='Ich sollte um eine Versetzung bitten. Diese Arbeit treibt mich noch in den Wahnsinn...' WHERE entry=2000000829; /* content: I need to be re-assigned. This job is pushing me over the edge... */
UPDATE db_script_string SET content_loc3='Er glaubt, dass er so verdammt wichtig sei. Schubst mich immer herum... ich werde es ihm zeigen. Ich werde es ihnen allen zeigen!' WHERE entry=2000000830; /* content: He thinks he\'s so damned important. Always pushing me around... I\'ll show him. I\'ll show all of them! */
UPDATE db_script_string SET content_loc3='Wenn ich nur zurück zum Pilzdickicht könnte. Ich habe noch nie in meinem Leben solch reife Geflechte gesehen!' WHERE entry=2000000831; /* content: If only I could get back to the mushroom thicket. I have not seen such a mature cluster in my lifetime! */
UPDATE db_script_string SET content_loc3='Oh nein! Ich verliere ihn!' WHERE entry=2000001010; /* content: Oh no!  I\'m losing this one! */
UPDATE db_script_string SET content_loc3='Opus möchte etwas von Cuergos Gold... mit Wurm.' WHERE entry=2000005001; /* content: Opus wants some Cuergo\'s Gold... with worm. */
UPDATE db_script_string SET content_loc3='Bijou ist verloren! KRÄCHZ! *hust*' WHERE entry=2000005002; /* content: Bijou lost! SQUAWK! *cough */
UPDATE db_script_string SET content_loc3='Kibler wird vom Fantasy-Tierschutz untersucht.' WHERE entry=2000005003; /* content: Kibler is being investigated by G.E.T.A. */
UPDATE db_script_string SET content_loc3='Bijou weiß, wo Schätze verborgen sind.' WHERE entry=2000005004; /* content: Bijou knows where treasures are hidden. */
UPDATE db_script_string SET content_loc3='Großdrachen kontrollieren Orcs!? KRÄCHZ!' WHERE entry=2000005005; /* content: Dragons controlling orcs!? SQUAWK! */
UPDATE db_script_string SET content_loc3='Alle gehen zur Spitze, aber keiner kommt zurück... genau wie Bijou.' WHERE entry=2000005006; /* content: They all go to the Spire but none never come back... just like Bijou. */
UPDATE db_script_string SET content_loc3='Fragt Kibler nach dem Schatz! KRÄCHZ!' WHERE entry=2000005007; /* content: Ask Kibler about the treasure! SQUAWK! */
UPDATE db_script_string SET content_loc3='KRÄCHZ!!! *hust* *hust* Ich muss mit dem Rauchen aufhören!' WHERE entry=2000005008; /* content: SQUAWK!! *cough* *cough* I need to quit smoking! */
UPDATE db_script_string SET content_loc3='Gut gemacht, Soldaten! Rührt euch!' WHERE entry=2000005009; /* content: Well done, soldiers! At ease! */
UPDATE db_script_string SET content_loc3='Zug zug! Freut sich zu helfen!' WHERE entry=2000005010; /* content: Zug zug! Happy to help! */
UPDATE db_script_string SET content_loc3='Ja auf der Stelle Mama!' WHERE entry=2000005011; /* content: Yes right away momma! */
UPDATE db_script_string SET content_loc3='Geheimnisvolle Hufabdrücke, versengte Schilde, seltsame Abzeichen... wie passt das nur alles zusammen?' WHERE entry=2000005012; /* content: Mysterious hoofprints, scorched shields, stray badges... how do we make sense of all this? */
UPDATE db_script_string SET content_loc3='Ich glaube kaum, dass Drazzit jemanden finden wird, der seine verdammten Raptoren jagt. Ich meine, er hat seine Belohnung schon zweimal erhöht...' WHERE entry=2000005013; /* content: I don\'t think Drazzit\'s going to have any luck finding anyone to kill this raptor. I mean, he\'s already raised the reward twice... */
UPDATE db_script_string SET content_loc3='Aber es ist das Einzige, in das Brogg hineinpasst! Ihr Goblins baut viel zu kleine Unterkünfte!' WHERE entry=2000005014; /* content: But this the only building that fit Brogg! Goblins make buildings too small! */
UPDATE db_script_string SET content_loc3='Wir haben schon oft genug darüber gesprochen, Brogg. Ihr könnt nicht in diesem Gebäude bleiben. Sicher, es bietet Euch genug Platz, doch es passt sonst niemand mehr hinein!' WHERE entry=2000005015; /* content: I thought we talked about this, Brogg. You can\'t stay in this building. Sure, it can house you, but there\'s no room for anyone else! */
UPDATE db_script_string SET content_loc3='Ja, natürlich! Aber dieses Gebäude ist unser Hauptquartier! Alle unsere Baupläne und Handbücher befinden sich da drinnen.' WHERE entry=2000005016; /* content: Yes, we said you can stay here, but this building is our headquarters! All of our manuals and blueprints are stored here! */
UPDATE db_script_string SET content_loc3='Ich widerspreche nur ungern, aber wenn Ihr mich fragt, seid ihr Oger einfach nur zu groß! Ich sehe schon, heute werde ich Euch wohl nicht zu einem Umzug bewegen können. Aber glaubt nicht, dass Ihr auf ewig hier wohnen könnt!' WHERE entry=2000005017; /* content: I\'m inclined to say they made ogres too big! I can see I\'m not going to convince you to move now, but don\'t think this is permanent! */
UPDATE db_script_string SET content_loc3='Ah, das muss er jetzt sein... nein?' WHERE entry=2000005018; /* content: Ah, this must be him now... no? */
UPDATE db_script_string SET content_loc3='Ihr solltet besser etwas Abstand halten. Das Ausnehmen von Fischen ist eine unschöne Angelegenheit.' WHERE entry=2000005019; /* content: You might wanna stand back. Fish guttin\' is a dirty job. */
UPDATE db_script_string SET content_loc3='Es ist gut zu sehen, dass Ihr Theramore mit Stolz verteidigt, Freund.' WHERE entry=2000005020; /* content: It\'s good to see you defending Theramore with pride, friend. */
UPDATE db_script_string SET content_loc3='Lasst uns zum Gasthaus gehen, wenn Ihr Dienstschluss habt. Ich gebe eine Runde aus und wir können uns über die gute alte Zeit unterhalten.' WHERE entry=2000005021; /* content: When you\'re off duty, let\'s go to the inn. I\'ll buy a round and we can reminisce about the good old days. */
UPDATE db_script_string SET content_loc3='Danke für den Käse!' WHERE entry=2000005025; /* content: Prizes! Get your prizes right here! Get over here and turn in your Darkmoon Faire Prize Tickets for valuable prizes! */
UPDATE db_script_string SET content_loc3='Zeit, wieder zurück in den Laden zu gehen...' WHERE entry=2000005026; /* content: Come speak with me, and what once was cloudy shall become crystal clear. */
UPDATE db_script_string SET content_loc3='Die Macht des Lichkönigs wird stärker. Es ist von äußerster Wichtigkeit, dass unser Zeitplan seinem Vorhaben entspricht!' WHERE entry=2000005027; /* content: The Lich King\'s forces are building. It is imperative that our timetable supports his plans. */
UPDATE db_script_string SET content_loc3='Morgen werden wir mit dem Training unserer vielversprechenden Drachen beginnen, also vergesst Eure Kauspielzeuge nicht.' WHERE entry=2000005028; /* content: Tomorrow we will begin training of our promising dragons, so don\'t forget your chew toys. */
UPDATE db_script_string SET content_loc3='Unser ältesten Gelege von Drachen sind noch immer weit von der Reife entfernt, aber mit Geduld und Studien sind wir überzeugt, dass die Dachen bald fertig sein werden.' WHERE entry=2000005029; /* content: Our oldest clutch of dragons are still far from maturity, but with patience and study, we are confident the dragons will soon be ready. */
UPDATE db_script_string SET content_loc3='Ich komme aus dem Land weit im Süden... wo die Frauen umtreiben und die Männer sich zerreisen.' WHERE entry=2000005031; /* content: I come from the land down under... where women plague and men sunder. */
UPDATE db_script_string SET content_loc3='...Und dann sagte der Taure, "33 ZENTIMETER!"' WHERE entry=2000005032; /* content: ...And then the tauren said, "13 INCHES!" */
UPDATE db_script_string SET content_loc3='Ich entscheide mich für die dritte Option: EUCH TÖTEN!' WHERE entry=2000005033; /* content: Come one, come all! Welcome to the Darkmoon Faire! Do you crave adventure? Do you seek exotic and mysterious treasures? Then look no further! You, my friend, have come to the right place! Dive right in and take part in all that the Faire has to offer! We\'ll be at this location all week, so be sure to tell your friends and loved ones! */
UPDATE db_script_string SET content_loc3='Wie Ihr befehlt...' WHERE entry=2000005035; /* content: It\'s getting away! */
UPDATE db_script_string SET content_loc3='Ich werde wohl Jagd auf diesen Getriebenen machen, jetzt, da eine Belohnung auf seinen Kopf aussteht.' WHERE entry=2000005037; /* content: I might just have to go hunting for that Wretched beast now that there\'s a bounty on his head. */
UPDATE db_script_string SET content_loc3='Es ist eine lange Zeit gewesen, Bethor, mein Freund' WHERE entry=2000005039; /* content: The wind speaks of our enemies\' movements, Ashyen. The Naga will attack again. */
UPDATE db_script_string SET content_loc3='Wenn wir Zeit haben, müssen wir ausfürhlicher reden. Wir haben viel zu besprechen.' WHERE entry=2000005040; /* content: Very well. When that decision is made, I will be ready. Let us hope it won\'t be too late. */
UPDATE db_script_string SET content_loc3='Als die Erschütterungen endeten, kamen seine wahren Motive ans Licht. Wir waren gefangen und er entzog uns langsam unsere Macht.' WHERE entry=2000005041; /* content: I will do my best to take care of the children, Grandmother. */
UPDATE db_script_string SET content_loc3='Schön zu sehen, dass es Euch so gut geht, Harlan. Und ich hoffe, wir sehen uns bald wieder...' WHERE entry=2000005043; /* content: I WILL DESTROY ALL */
UPDATE db_script_string SET content_loc3='Dann will ich mal wieder. Macht\'s gut, Harlan.' WHERE entry=2000005044; /* content: US ALREADY ALL ANYMORE AND ANYMORE */
UPDATE db_script_string SET content_loc3='Und danke, $N. Ohne Eure Hilfe hätte ich nie den Weg zu den Verlassenen gefunden.' WHERE entry=2000005046; /* content: I\'m up to looking into that bounty after this shift. */
UPDATE db_script_string SET content_loc3='Lebt wohl, mein Freund' WHERE entry=2000005047; /* content: You want to go do the bounty off of this wanted poster after this shift is over? */
UPDATE db_script_string SET content_loc3='Amüsiert Ihr Euch?' WHERE entry=2000005048; /* content: Having a good time? */
UPDATE db_script_string SET content_loc3='Sein Leutnant, Ilkrud Magskluve, besitzt einen Folianten, der Hinweise auf den Verbleib der letzten Seelensteine geben könnte.' WHERE entry=2000005049; /* content: I have another reading from the nether. */
UPDATE db_script_string SET content_loc3='Er fürchtete, seine Macht würde schwinden und dass bald selbst wir, die letzten seiner kostbaren Seelenedelsteine, verblassen und sterben würden.' WHERE entry=2000005051; /* content: Great Gazlowe! */
UPDATE db_script_string SET content_loc3='Buh!' WHERE entry=2000005053; /* content: Bip! */
UPDATE db_script_string SET content_loc3='Habt Ihr etwas Kleingeld für einen hungernden Künstler?' WHERE entry=2000005054; /* content: Spare some change for a starving artist? */
UPDATE db_script_string SET content_loc3='%s riecht das Elixier und gräbt eifrig weiter!' WHERE entry=2000005056; /* content: Right. We just busted our tails to haul all of this stuff up here, and you want us to work more? */
UPDATE db_script_string SET content_loc3='Heulsuse! Heulsuse!' WHERE entry=2000005057; /* content: Crybaby! Crybaby! */
UPDATE db_script_string SET content_loc3='Das Baby will seine Puppe!' WHERE entry=2000005058; /* content: Baby wants her dolly! */
UPDATE db_script_string SET content_loc3='Ich frage mich, ob deine Puppe schwimmen kann!' WHERE entry=2000005059; /* content: I wonder if your dolly can swim! */
