UPDATE db_script_string SET content_loc3='Vorsicht sollte nicht bedeuten, dass wir unserem Feind unnötige Vorteile verschaffen.' WHERE entry=2000005246; /* content: Prudence should not mean granting our enemy unnecessary advantages. */
