UPDATE db_script_string SET content_loc3='Wie hat der Wyrmkult so viele Anhänger bekommen? Wo kommen sie her?' WHERE entry=2000005268; /* content: How has the Wyrmcult gained so many followers? Where are they coming from?! */
UPDATE db_script_string SET content_loc3='Der Wyrmkult muss einen anderen Pfad benutzen. Wir finden sie immer noch im Ruuanwald.' WHERE entry=2000005269; /* content: The Wyrmcult must be using another path. We are still finding them in Ruuan Weald. */
UPDATE db_script_string SET content_loc3='Wir sollen hier warten. Sie haben andere voraus geschickt, um das Tunnelinnere zu überprüfen.' WHERE entry=2000005270; /* content: We are to wait here. They sent others ahead to check the tunnel interior. */
UPDATE db_script_string SET content_loc3='Bei allem Respekt, A\'dal hat uns hierher geschickt, um Kaels Pläne herauszufinden, nicht um einen ausgewachsenen Angriff zu starten.' WHERE entry=2000005271; /* content: With all due respect, A\'dal sent us here to investigate Kael\'s activities, not mount a full-scaled attack. */
UPDATE db_script_string SET content_loc3='...und schaut, wohin es uns gebracht hat...' WHERE entry=2000005278; /* content: ...and just look at where that got us! */
UPDATE db_script_string SET content_loc3='Endlich redet der Große mal von Taten!' WHERE entry=2000005280; /* content: Finally some talk of action from the big guy! */
UPDATE db_script_string SET content_loc3='Ist es jetzt sicher, dieses verdammte Ding zu betreten?' WHERE entry=2000005283; /* content: Is that damned thing safe to go through yet? */
UPDATE db_script_string SET content_loc3='Nicht wirklich.' WHERE entry=2000005284; /* content: Not really. */
UPDATE db_script_string SET content_loc3='Hört Ihr bald auf, mich das zu fragen?' WHERE entry=2000005285; /* content: Will you stop asking that? */
UPDATE db_script_string SET content_loc3='Ok, sieht sicher aus. Rein da, Ingenieur!' WHERE entry=2000005286; /* content: Alright, it looks safe. Get in there, engineer! */
UPDATE db_script_string SET content_loc3='Hier rührt sich gar nichts!' WHERE entry=2000005287; /* content: Here goes nothing! */
