UPDATE script_texts SET content_loc3='Ihr werdet jetzt... sterben!' WHERE entry=-1533033; /* content: You die now! */
UPDATE script_texts SET content_loc3='Jetzt spürt ihr den Schmerz...' WHERE entry=-1533034; /* content: Now YOU feel pain! */
UPDATE script_texts SET content_loc3='Euch... dankbar sein!' WHERE entry=-1533035; /* content: Thank... you... */
UPDATE script_texts SET content_loc3='Bitteee!' WHERE entry=-1533036; /* content: Pleeease! */
UPDATE script_texts SET content_loc3='Aufhören, bitte aufhören!' WHERE entry=-1533037; /* content: Stop, make it stop! */
