UPDATE script_texts SET content_loc3='Bitte, neeein!' WHERE entry=-1533039; /* content: Please, nooo! */
UPDATE script_texts SET content_loc3='Ihr Narren habt euren eigenen Untergang heraufbeschworen. Voller Leichtsinn habt ihr euch mit Kräften angelegt, die weit über euren Verstand hinausgehen. Tapfer seid ihr in das Reich des Seelenjägers vorgedrungen. Doch jetzt gibt es nur noch einen Ausweg - den einsamen Pfad der Verdammten.' WHERE entry=-1533040; /* content: Foolishly you have sought your own demise. */
UPDATE script_texts SET content_loc3='Tod ist der einzige Ausweg!' WHERE entry=-1533041; /* content: Death is the only escape. */
UPDATE script_texts SET content_loc3='Das... ist... mein... Ende.' WHERE entry=-1533042; /* content: I... am... undone! */
UPDATE script_texts SET content_loc3='Ich habe lange genug gewartet. Stellt euch dem Seelenjäger.' WHERE entry=-1533043; /* content: I have waited long enough! Now, you face the harvester of souls! */
UPDATE script_texts SET content_loc3='Verteidigt euch!' WHERE entry=-1533044; /* content: Defend youself! */
UPDATE script_texts SET content_loc3='Zeliek, tötet sie noch nicht. Erst haben wir noch unseren Spaß mit ihnen!' WHERE entry=-1533045; /* content: Come, Zeliek, do not drive them out. Not before we\'ve had our fun. */
UPDATE script_texts SET content_loc3='Ich hoffe sie bleiben lange genug am Leben... damit ich mich vorstellen kann.' WHERE entry=-1533046; /* content: I do hope they stay alive long enough for me to... introduce myself. */
UPDATE script_texts SET content_loc3='Ich töte den Ersten! Wer wette dagegen?' WHERE entry=-1533047; /* content: The first kill goes to me! Anyone care to wager? */
UPDATE script_texts SET content_loc3='Euer Leben gehört mir!' WHERE entry=-1533048; /* content: Your life is mine! */
UPDATE script_texts SET content_loc3='Wer ist der Nächste?' WHERE entry=-1533049; /* content: Who\'s next? */
UPDATE script_texts SET content_loc3='Tou... che!' WHERE entry=-1533050; /* content: Tou... che! */
UPDATE script_texts SET content_loc3='Kommt und kämpft, ihr Wichte!' WHERE entry=-1533051; /* content: Come out and fight, ye wee ninny! */
UPDATE script_texts SET content_loc3='Zu den Waffen, Ihr Schmarotzer! Wir haben Gesellschaft!' WHERE entry=-1533052; /* content: To arms, ye roustabouts! We\'ve got company! */
UPDATE script_texts SET content_loc3='Ich hab\' genug von eurem Geflenne. Haltet die Klappe oder ich stopfe euch das Maul!' WHERE entry=-1533053; /* content: I heard about enough of yer sniveling. Shut yer fly trap \'afore I shut it for ye! */
UPDATE script_texts SET content_loc3='Ich werde es genießen diese Waschlappen aus dem Weg zu räumen!' WHERE entry=-1533054; /* content: I\'m gonna enjoy killin\' these slack-jawed daffodils! */
UPDATE script_texts SET content_loc3='Ich mag mein Fleisch besonders knusprig!' WHERE entry=-1533055; /* content: I like my meat extra crispy! */
UPDATE script_texts SET content_loc3='Bringt das nächste Mal mehr Freunde mit!' WHERE entry=-1533056; /* content: Next time, bring more friends! */
UPDATE script_texts SET content_loc3='Was für eine verdammte Verschwendung!' WHERE entry=-1533057; /* content: What a bloody waste this is! */
UPDATE script_texts SET content_loc3='Flieht, bevor es zu spät ist!' WHERE entry=-1533058; /* content: Flee, before it\'s too late! */
UPDATE script_texts SET content_loc3='Eindringlinge, macht diesem törichten Unterfangen ein für alle Mal ein Ende! Kehrt um!' WHERE entry=-1533059; /* content: Invaders, cease this foolish venture at once! Turn away while you still can! */
UPDATE script_texts SET content_loc3='Vielleicht kommen sie ja noch zur Vernunft... und fliehen so schnell sie können.' WHERE entry=-1533060; /* content: Perhaps they will come to their senses, and run away as fast as they can! */
UPDATE script_texts SET content_loc3='Hört auf! Kehrt zurück! Kehrt zurück solange ihr noch könnt!' WHERE entry=-1533061; /* content: Do not continue! Turn back while there\'s still time! */
UPDATE script_texts SET content_loc3='Ich habe keine Wahl!' WHERE entry=-1533062; /* content: I- I have no choice but to obey! */
UPDATE script_texts SET content_loc3='Vergebt mir!' WHERE entry=-1533063; /* content: Forgive me! */
UPDATE script_texts SET content_loc3='Es verläuft... ganz nach Plan.' WHERE entry=-1533064; /* content: It is... as it should be. */
UPDATE script_texts SET content_loc3='Sucht Ihr den Tod?' WHERE entry=-1533065; /* content: You seek death? */
UPDATE script_texts SET content_loc3='Keiner darf passieren!' WHERE entry=-1533066; /* content: None shall pass! */
UPDATE script_texts SET content_loc3='Schweigt!' WHERE entry=-1533067; /* content: Be still! */
UPDATE script_texts SET content_loc3='Im Tod werdet Ihr keinen Frieden finden!' WHERE entry=-1533068; /* content: You will find no peace in death. */
UPDATE script_texts SET content_loc3='Des Meisters Wille ist geschehen!' WHERE entry=-1533069; /* content: The master\'s will is done. */
UPDATE script_texts SET content_loc3='Verbeugt Euch vor der Macht der Geißel!' WHERE entry=-1533070; /* content: Bow to the might of the Highlord! */
UPDATE script_texts SET content_loc3='Verbeugt Euch vor der Macht der Geißel!' WHERE entry=-1533070; /* content: Bow to the might of the scourge! */
UPDATE script_texts SET content_loc3='Genug mit dem Geschwätz. Lasst sie nur kommen. Wir werden ihre Knochen zu Staub zermalmen.' WHERE entry=-1533071; /* content: Enough prattling. Let them come! We shall grind their bones to dust. */
UPDATE script_texts SET content_loc3='Bewahrt Euch Euren Zorn! Doch haltet Eure Wut in Zaum! Ihr alle werdet Eure Missmut schon noch früh genug freien Lauf lassen können!' WHERE entry=-1533072; /* content: Conserve your anger! Harness your rage! You will all have outlets for your frustration soon enough. */
UPDATE script_texts SET content_loc3='Das Leben ist bedeutungslos. Der Tod ist die wahre Prüfung.' WHERE entry=-1533073; /* content: Life is meaningless. It is in death that we are truly tested. */
UPDATE script_texts SET content_loc3='Der Tod... hält mich nicht auf...' WHERE entry=-1533074; /* content: I...am...released! Perhaps it\'s not too late to */
UPDATE script_texts SET content_loc3='Ehre unserem Meister!' WHERE entry=-1533075; /* content: Glory to the master! */
UPDATE script_texts SET content_loc3='Euer Leben ist verwirkt!' WHERE entry=-1533076; /* content: Your life is forfeit! */
UPDATE script_texts SET content_loc3='Sterbt, Eindringling!' WHERE entry=-1533077; /* content: Die, trespasser! */
UPDATE script_texts SET content_loc3='Erhebt Euch, Soldaten! Erhebt euch und kämpft erneut!' WHERE entry=-1533078; /* content: Rise, my soldiers! Rise and fight once more! */
UPDATE script_texts SET content_loc3='Meine Pflicht ist getan!' WHERE entry=-1533079; /* content: My task is done! */
UPDATE script_texts SET content_loc3='Sterbt!' WHERE entry=-1533080; /* content: Breathe no more! */
UPDATE script_texts SET content_loc3='Ich werde dem Meister über den Tod hinaus dienen!' WHERE entry=-1533081; /* content: I will serve the master... in... death! */
UPDATE script_texts SET content_loc3='%s atmet tief ein...' WHERE entry=-1533082; /* content: %s takes in a deep breath... */
