UPDATE script_texts SET content_loc3='Meister, ich benötige Beistand.' WHERE entry=-1533103; /* content: Master! I require aid! */
UPDATE script_texts SET content_loc3='Wohlan, Krieger der Eisigen Weiten, erhebt euch! Ich befehle euch für euren Meister zu kämpfen, zu töten und zu sterben! Keiner darf überleben!' WHERE entry=-1533104; /* content: Very well... warriors of the frozen wastes, rise up! I command you to fight, kill, and die for your master. Let none survive... */
UPDATE script_texts SET content_loc3='Lakaien, Diener, Soldaten der eisigen Finsternis! Folg dem Ruf von Kel\'Thuzad!' WHERE entry=-1533105; /* content: Minions, servants, soldiers of the cold dark, obey the call of Kel\'Thuzad! */
UPDATE script_texts SET content_loc3='Eure armselige Magie kann der Macht der Geißel niemals kontollieren!' WHERE entry=-1533106; /* content: Your petty magics are no challenge to the might of the Scourge! */
UPDATE script_texts SET content_loc3='Genug! Diese Ablenkungen ermüden mich!' WHERE entry=-1533107; /* content: Enough! I grow tired of these distractions! */
UPDATE script_texts SET content_loc3='Narren, Ihr habt Euch zu sehr verausgabt. Befreit euch, meine Diener!' WHERE entry=-1533108; /* content: Fools, you have spread your powers too thin. Be free, my minions! */
UPDATE script_texts SET content_loc3='Ihr gehört mir!' WHERE entry=-1533109; /* content: You are mine now! */
UPDATE script_texts SET content_loc3='Ihr entgeht mir nicht...' WHERE entry=-1533110; /* content: I see you! */
UPDATE script_texts SET content_loc3='Ihr seid... als nächstes dran.' WHERE entry=-1533111; /* content: You...are next! */
UPDATE script_texts SET content_loc3='Schließt eure Augen. Schlaft.' WHERE entry=-1533112; /* content: Close your eyes... sleep! */
UPDATE script_texts SET content_loc3='Die Völker dieser Welt werden zu grunde gehen. Es ist nur eine Frage der Zeit.' WHERE entry=-1533113; /* content: The races of the world will perish. It is only a matter of time. */
UPDATE script_texts SET content_loc3='Ich sehe endloses Leiden. Ich sehe Qualen. Ich sehe Zorn. Ich sehe... alles!' WHERE entry=-1533114; /* content: I see endless suffering, I see torment, I see rage. I see... everything! */
UPDATE script_texts SET content_loc3='Schon bald... wird die Welt erzittern!' WHERE entry=-1533115; /* content: Soon... the world will tremble! */
UPDATE script_texts SET content_loc3='Euer Ende naht.' WHERE entry=-1533116; /* content: The end is upon you. */
UPDATE script_texts SET content_loc3='Maden werden sich Eurem faulenden Fleisch laben!' WHERE entry=-1533117; /* content: Hungry worms will feast on your rotten flesh! */
UPDATE script_texts SET content_loc3='Arghh... h...' WHERE entry=-1533118; /* content: Noo... o... */
UPDATE script_texts SET content_loc3='%s erspäht einen Zombie zum Verschlingen!' WHERE entry=-1533119; /* content: %s spots a nearby Zombie to devour! */
UPDATE script_texts SET content_loc3='Ha ha, ich fange gerade erst an!' WHERE entry=-1533120; /* content: Hah hah, I\'m just getting warmed up! */
