UPDATE script_texts SET content_loc3='Helft mir, Ihr Narren, bevor es zu spät ist!' WHERE entry=-1555004; /* content: Aid me, you fools, before it\'s too late! */
UPDATE script_texts SET content_loc3='Habt Ihr Angst vor dem Tod?' WHERE entry=-1555005; /* content: Do you fear death? */
UPDATE script_texts SET content_loc3='Das wird Euch noch leid tun!' WHERE entry=-1555011; /* content: You\'ll be sorry! */
UPDATE script_texts SET content_loc3='Zeit für Spaß!' WHERE entry=-1555012; /* content: Time for fun! */
UPDATE script_texts SET content_loc3='Das ist... nicht gut.' WHERE entry=-1555017; /* content: This... no... good... */
UPDATE script_texts SET content_loc3='Das wird Euch noch leid tun!' WHERE entry=-1555021; /* content: You\'ll be sorry! */
UPDATE script_texts SET content_loc3='Zeit zu töten!' WHERE entry=-1555022; /* content: Time to kill! */
UPDATE script_texts SET content_loc3='Ich werde Euer Blut als Opfer darbieten!' WHERE entry=-1555029; /* content: I\'ll make an offering of your blood! */
UPDATE script_texts SET content_loc3='Ich diene mit Stolz.' WHERE entry=-1555033; /* content: I serve with pride. */
UPDATE script_texts SET content_loc3='Ich lasse mein Leben... gerne.' WHERE entry=-1555035; /* content: I give my life... Gladly. */
UPDATE script_texts SET content_loc3='Nein! Wie kann das sein?!' WHERE entry=-1556016; /* content: No! How can this be? */
UPDATE script_texts SET content_loc3='Schmerz soll der Lohn Eurer Anmaßung sein! Ihr könnt mich nicht aufhalten! Der Smaragdgrüne Traum wird mir gehören!' WHERE entry=-1556017; /* content: Pain will be the price for your insolence! You cannot stop me from claiming the Emerald Dream as my own! */
UPDATE script_texts SET content_loc3='Erwacht, meine Kinder, und helft Eurem Meister!' WHERE entry=-1556018; /* content: Awaken, my children and assist your master! */
UPDATE script_texts SET content_loc3='Eure Magie soll Euer Untergang sein... ak-a-ak...' WHERE entry=-1556019; /* content: Your magics shall be your undoing... ak-a-ak... */
UPDATE script_texts SET content_loc3='%s wird wieder zu Stein.' WHERE entry=-1556020; /* content: %s returns to stone. */
UPDATE script_texts SET content_loc3='Eure Kräfte... ak-ak... wenden sich gegen Euch...' WHERE entry=-1556021; /* content: Your powers... ak-ak... turn against you... */
UPDATE script_texts SET content_loc3='Eure Zauber... ke-kaw... sind schwache Magie... leicht gegen Euch selbst einsetzbar...' WHERE entry=-1556022; /* content: Your spells... ke-kaw... are weak magics... easy to turn against you... */
UPDATE script_texts SET content_loc3='Was ist das? Verzeiht, aber ich habe keinen Besuch erwartet. Wie Ihr sehen könnt, bin ich ein wenig beschäftigt. Aber egal. Als guter Gastgeber werde ich mich um Euch kümmern... persönlich.' WHERE entry=-1557000; /* content: What is this? You must forgive me, but I was not expecting company. As you can see, we are somewhat preoccupied right now. But no matter. As I am a gracious host, I will tend to you... personally. */
UPDATE script_texts SET content_loc3='Es war sehr... unterhaltsam.' WHERE entry=-1557004; /* content: It has been... entertaining. */
UPDATE script_texts SET content_loc3='Und schon müsst Ihr gehen.' WHERE entry=-1557005; /* content: And now we part company. */
UPDATE script_texts SET content_loc3='Ich habe so viele faszinierende Dinge, die ich Euch zeigen möchte.' WHERE entry=-1557006; /* content: I have such fascinating things to show you. */
UPDATE script_texts SET content_loc3='Ich muss mich nun... verabschieden.' WHERE entry=-1557007; /* content: I must bid you... farewell. */
UPDATE script_texts SET content_loc3='Wehrt Euch... nicht.' WHERE entry=-1557010; /* content: Do not... resist. */
UPDATE script_texts SET content_loc3='%s tritt in die Leere ein...' WHERE entry=-1557014; /* content: %s shifts into the void... */
UPDATE script_texts SET content_loc3='Das sollte nicht allzu lange dauern. Gebt mir einfach Deckung, während ich diese Nethersammler leere.' WHERE entry=-1557015; /* content: This should\'t take very long. Just watch my back as I empty these nether collectors. */
UPDATE script_texts SET content_loc3='Fantastisch! Lasst uns weitermachen, in Ordnung?' WHERE entry=-1557016; /* content: Fantastic! let\'s move on, shall we? */
UPDATE script_texts SET content_loc3='Schaut Euch diese Energieeinstellungen an! Shaffar war auf Töten vorbereitet.' WHERE entry=-1557017; /* content: Looking at these energy levels, Shaffar was set to make a killing! */
UPDATE script_texts SET content_loc3='Das sollte genügen...' WHERE entry=-1557018; /* content: That should do it... */
UPDATE script_texts SET content_loc3='Ah, da ist er ja. Folgt mir, Fleischling.' WHERE entry=-1557020; /* content: Ah, there it is. Follow me, fleshling. */
UPDATE script_texts SET content_loc3='Es kann nicht mehr allzu viele von diesen Sammlern geben. Schützt mich einfach, während ich meine Arbeit erledige.' WHERE entry=-1557021; /* content: There can\'t be too many more of these collectors. Just keep me safe as I do my job. */
UPDATE script_texts SET content_loc3='Wir sind nahe am Ausgang. Ich lasse Euch etwa dreißig Sekunden ausruhen, aber dann müssen wir hier raus.' WHERE entry=-1557023; /* content: We\'re close to the exit. I\'ll let you rest for about thirty seconds, but then we\'re out of here. */
UPDATE script_texts SET content_loc3='Seid Ihr abmarschbereit?' WHERE entry=-1557024; /* content: Are you ready to go? */
UPDATE script_texts SET content_loc3='Ok, Pause VORBEI. Lasst uns gehen!' WHERE entry=-1557025; /* content: Ok break time is OVER. Let\'s go! */
UPDATE script_texts SET content_loc3='Euer Plan war gut, Sha\'heen. Ihr wärt fast damit durchgekommen, wenn Ihr eines nicht vergessen hättet...' WHERE entry=-1557027; /* content: Your plan was a good one, Sha\'heen, and you would have gotten away with it if not for one thing... */
UPDATE script_texts SET content_loc3='Ach wirklich? Und was sollte das sein?' WHERE entry=-1557028; /* content: Oh really? And what might that be? */
UPDATE script_texts SET content_loc3='Unterschätzt niemals die Gier der anderen Astralen!' WHERE entry=-1557029; /* content: Never underestimate the other ethereal\'s greed! */
UPDATE script_texts SET content_loc3='Er hatte Recht, müsst Ihr wissen.' WHERE entry=-1557030; /* content: He was right, you know. I\'ll have to take that tag-line for my own... It\'s not like he\'ll have a use for it anymore! */
UPDATE script_texts SET content_loc3='Danke und viel Glück!' WHERE entry=-1557031; /* content: Thanks and good luck! */
UPDATE script_texts SET content_loc3='%s überprüft, ob sein Körper heil ist.' WHERE entry=-1557032; /* content: %s checks to make sure his body is intact. */
UPDATE script_texts SET content_loc3='Ihr habt es geschafft! Gut gemacht, $R. Wenn Ihr mich jetzt entschuldigen würdet, ich muss den Rest unserer Gruppe hineinbringen.' WHERE entry=-1557033; /* content: You made it! Well done, $r. Now if you\'ll excuse me, I have to get the rest of our crew inside. */
UPDATE script_texts SET content_loc3='%s macht sich fachmännisch an der Bedienungskonsole zu schaffen.' WHERE entry=-1557034; /* content: %s expertly manipulates the control panel. */
UPDATE script_texts SET content_loc3='Lasst uns keine Zeit verschwenden! Nehmt alles, was nicht niet- und nagelfest ist, und teleportiert Euch direkt in die Sturmsäule! Hopp hopp!' WHERE entry=-1557035; /* content: Let\'s not waste any time! Take anything that isn\'t nailed down to the floor and teleport directly to Stormspire! Chop chop! */
UPDATE script_texts SET content_loc3='Ihr werdet mit Eurem Leben bezahlen.' WHERE entry=-1558002; /* content: You will pay with your life! */
UPDATE script_texts SET content_loc3='Tut Buße!' WHERE entry=-1558004; /* content: Serve your penitence! */
UPDATE script_texts SET content_loc3='Möge Euer Verstand sich trüben.' WHERE entry=-1558005; /* content: Let your mind be clouded. */
UPDATE script_texts SET content_loc3='Schaut in die Finsternis Eurer Seele.' WHERE entry=-1558006; /* content: Stare into the darkness of your soul. */
UPDATE script_texts SET content_loc3='Meine magischen Fähigkeiten können den Fluss der Zeit bis zu einem Zeitpunkt vor Thralls Tod umkehren, aber seid vorsichtig. Meine Fähigkeit die Zeit zu manipulieren ist begrenzt.' WHERE entry=-1560001; /* content: My magical power can turn back time to before Thrall\'s death, but be careful. My power to manipulate time is limited. */
UPDATE script_texts SET content_loc3='Was hat das alles zu bedeuten? WACHEN!' WHERE entry=-1560003; /* content: What\'s the meaning of this? GUARDS! */
UPDATE script_texts SET content_loc3='Alles was Ihr wisst, wird ungeschehen.' WHERE entry=-1560004; /* content: All that you know... will be undone. */
UPDATE script_texts SET content_loc3='Auf geht\'s!' WHERE entry=-1560005; /* content: Let\'s go. */
UPDATE script_texts SET content_loc3='Ich dachte, ich hätte jemanden in die Scheune gehen sehen.' WHERE entry=-1560007; /* content: I thought I saw something go into the barn. */
UPDATE script_texts SET content_loc3='Ich habe nichts gesehen.' WHERE entry=-1560008; /* content: I didn\'t see anything. */
UPDATE script_texts SET content_loc3='Irgendetwas hat dieses Pferd erschreckt. Vorwärts!' WHERE entry=-1560010; /* content: Something riled that horse. Let\'s go! */
UPDATE script_texts SET content_loc3='Taretha ist nicht hier. Gehen wir in die Stadt.' WHERE entry=-1560011; /* content: Taretha isn\'t here. Let\'s head into town. */
UPDATE script_texts SET content_loc3='Sie ist nicht hier.' WHERE entry=-1560012; /* content: She\'s not here. */
UPDATE script_texts SET content_loc3='Thrall! Kommt heraus und stellt Euch Eurem Schicksal!' WHERE entry=-1560013; /* content: Thrall! Come outside and face your fate! */
UPDATE script_texts SET content_loc3='In der Kapelle sitzt Thrall in der Falle. Dieses Mal kann er nicht entkommen.' WHERE entry=-1560016; /* content: Thrall\'s trapped himself in the chapel. He can\'t escape now. */
UPDATE script_texts SET content_loc3='Er ist hier, haltet ihn auf!' WHERE entry=-1560017; /* content: He\'s here, stop him! */
UPDATE script_texts SET content_loc3='Wir haben alle Zeit der Welt...' WHERE entry=-1560018; /* content: We have all the time in the world.... */
UPDATE script_texts SET content_loc3='Ihr könnt uns nicht entkommen!' WHERE entry=-1560019; /* content: You cannot escape us! */
UPDATE script_texts SET content_loc3='Denkt nicht, Ihr könntet gewinnen!' WHERE entry=-1560020; /* content: Do not think you can win! */
UPDATE script_texts SET content_loc3='Nun gut, denn. Lasst uns gehen!' WHERE entry=-1560023; /* content: Very well then. Let\'s go! */
UPDATE script_texts SET content_loc3='Ich lasse mich nie wieder in Ketten legen!' WHERE entry=-1560026; /* content: I\'ll never be chained again! */
UPDATE script_texts SET content_loc3='Nun gut. Tarrens Mühle liegt westlich von hier. Wir müssen uns beeilen...' WHERE entry=-1560027; /* content: Very well. Tarren Mill lies just west of here. Since time is of the essence... */
UPDATE script_texts SET content_loc3='Lasst uns losreiten!' WHERE entry=-1560028; /* content: Let\'s ride! */
UPDATE script_texts SET content_loc3='Taretha muss im Gasthaus sein. Lasst uns gehen.' WHERE entry=-1560029; /* content: Taretha must be in the inn. Let\'s go. */
UPDATE script_texts SET content_loc3='Taretha! Welch fauler Zauber ist das?' WHERE entry=-1560030; /* content: Taretha! What foul magic is this? */
UPDATE script_texts SET content_loc3='Wer oder was ist das?' WHERE entry=-1560031; /* content: Who or what was that? */
UPDATE script_texts SET content_loc3='Nein!' WHERE entry=-1560032; /* content: No! */
UPDATE script_texts SET content_loc3='Lebe wohl, Taretha. Niemals werde ich deine Güte vergessen.' WHERE entry=-1560033; /* content: Goodbye, Taretha. I will never forget your kindness. */
UPDATE script_texts SET content_loc3='Ich habe meine Freiheit verdient!' WHERE entry=-1560038; /* content: I have earned my freedom! */
UPDATE script_texts SET content_loc3='Ich bin nicht länger ein Sklave!' WHERE entry=-1560040; /* content: I am a slave no longer! */
UPDATE script_texts SET content_loc3='Schwarzmoor wird sich verantworten müssen!' WHERE entry=-1560041; /* content: Blackmoore has much to answer for! */
UPDATE script_texts SET content_loc3='Ihr habt mich dazu gezwungen!' WHERE entry=-1560042; /* content: You have forced my hand! */
UPDATE script_texts SET content_loc3='Es hätte nicht so weit kommen dürfen!' WHERE entry=-1560043; /* content: It should not have come to this! */
UPDATE script_texts SET content_loc3='Das habe ich nicht gewollt!' WHERE entry=-1560044; /* content: I did not ask for this! */
UPDATE script_texts SET content_loc3='Ich stehe wahrhaftig in eurer Schuld, Fremde.' WHERE entry=-1560045; /* content: I am truly in your debt, strangers. */
UPDATE script_texts SET content_loc3='Vielen Dank, Fremde. Ihr habt mir Hoffnung gegeben.' WHERE entry=-1560046; /* content: Thank you, strangers. You have given me hope. */
UPDATE script_texts SET content_loc3='Ich werde diese Chance nutzen und mein Schicksal suchen.' WHERE entry=-1560047; /* content: I will not waste this chance. I will seek out my destiny. */
UPDATE script_texts SET content_loc3='Ich bin frei! Ich danke Euch allen!' WHERE entry=-1560048; /* content: I\'m free! Thank you all! */
UPDATE script_texts SET content_loc3='Thrall, du bist entkommen!' WHERE entry=-1560049; /* content: Thrall, you escaped! */
UPDATE script_texts SET content_loc3='Das sollte genügen.' WHERE entry=-1560050; /* content: That\'s enough out of him. */
UPDATE script_texts SET content_loc3='Dieser Zauber sollte ihre Erinnerungen an uns und an das gerade Geschehene auslöschen. Alles, woran sie sich noch erinnern werden, ist die Realität, wie sie sein sollte. Nichts wird mehr auf die versuchte Sabotage der Zeitlinien hinweisen. Gut gemacht. Thrall wird ausziehen, um seine Bestimmung zu finden, und Taretha...' WHERE entry=-1560051; /* content: That spell should wipe their memories of us and what just happened. All they should remember now is what reality would be like without the attempted temporal interference. Well done. Thrall will journey on to find his destiny, and Taretha... */
UPDATE script_texts SET content_loc3='Bedauerlicherweise lässt sich ihr Schicksal nicht verhindern.' WHERE entry=-1560052; /* content: Her fate is regrettably unavoidable. */
UPDATE script_texts SET content_loc3='Ich bin so froh, dass Ihr in Sicherheit seid, Taretha. Nichts davon wäre möglich gewesen, wenn Eure Freunde mir nicht zur Seite gestanden hätten. Sie haben all das erst möglich gemacht.' WHERE entry=-1560054; /* content: I\'m glad you\'re safe, Taretha. None of this would have been possible without your friends. They made all of this happen. */
UPDATE script_texts SET content_loc3='Thrall, ich habe diese Leute noch nie zuvor gesehen.' WHERE entry=-1560055; /* content: Thrall, I\'ve never met these people before in my life. */
UPDATE script_texts SET content_loc3='Aber, wer sind dann diese Leute?' WHERE entry=-1560056; /* content: Then who are these people? */
UPDATE script_texts SET content_loc3='Wenn Ihr mir einen Moment Eurer Zeit schenkt, kann ich Euch beiden alles erklären.' WHERE entry=-1560057; /* content: I believe I can explain everything to you two if you give me a moment of your time. */
UPDATE script_texts SET content_loc3='Im Namen Lady Vashjs werdet Ihr sterben!' WHERE entry=-1564000; /* content: You will die in the name of Lady Vashj! */
UPDATE script_texts SET content_loc3='Dranbleiben!' WHERE entry=-1564001; /* content: Stick around! */
UPDATE script_texts SET content_loc3='Der Boden beginnt aufzubrechen!' WHERE entry=-1564012; /* content: The ground begins to crack open! */
UPDATE script_texts SET content_loc3='Hoch Akama!' WHERE entry=-1564017; /* content: Hail Akama! */
UPDATE script_texts SET content_loc3='Wer spielen will, muss zahlen!' WHERE entry=-1564018; /* content: You play, you pay. */
UPDATE script_texts SET content_loc3='Ich bin nicht beeindruckt.' WHERE entry=-1564019; /* content: I\'m not impressed. */
UPDATE script_texts SET content_loc3='Habt Ihr Spaß?' WHERE entry=-1564020; /* content: Enjoying yourselves? */
UPDATE script_texts SET content_loc3='Also... Geschäft oder Vergnügen?' WHERE entry=-1564021; /* content: So... business or pleasure? */
UPDATE script_texts SET content_loc3='Nicht so schüchtern.' WHERE entry=-1564023; /* content: Don\'t be shy. */
UPDATE script_texts SET content_loc3='Ich gehöre ganz Euch.' WHERE entry=-1564024; /* content: I\'m all... yours. */
UPDATE script_texts SET content_loc3='Schnell gekommen, schnell gegangen.' WHERE entry=-1564025; /* content: Easy come, easy go. */
UPDATE script_texts SET content_loc3='So viel zum Thema "glückliches Ende".' WHERE entry=-1564026; /* content: So much for a happy ending. */
UPDATE script_texts SET content_loc3='Spielt nicht mit meinen Gefühlen!' WHERE entry=-1564027; /* content: Stop toying with my emotions! */
UPDATE script_texts SET content_loc3='Horde... wird Euch zerschmettern.' WHERE entry=-1564029; /* content: Horde will... crush you. */
UPDATE script_texts SET content_loc3='Mehr! Ich will mehr!' WHERE entry=-1564031; /* content: More! I want more! */
UPDATE script_texts SET content_loc3='Ich hungere!' WHERE entry=-1564033; /* content: I hunger! */
UPDATE script_texts SET content_loc3='Ich reiße Euch das Fleisch von den Knochen!' WHERE entry=-1564035; /* content: I\'ll rip the meat from your bones! */
UPDATE script_texts SET content_loc3='Die Rache ist mein!' WHERE entry=-1564038; /* content: Vengeance is mine! */
UPDATE script_texts SET content_loc3='Wovor habt Ihr Angst?' WHERE entry=-1564041; /* content: What are you afraid of? */
UPDATE script_texts SET content_loc3='Ich habe etwas für Euch...' WHERE entry=-1564044; /* content: I have something for you... */
UPDATE script_texts SET content_loc3='Ihr werdet mir den gebührenden Respekt zollen!' WHERE entry=-1564045; /* content: YOU WILL SHOW THE PROPER RESPECT! */
UPDATE script_texts SET content_loc3='Auf Euch warten nur Schmerz und Leid!' WHERE entry=-1564047; /* content: Pain and suffering are all that await you! */
UPDATE script_texts SET content_loc3='Lasst mich nicht allein!' WHERE entry=-1564048; /* content: Don\'t leave me alone! */
UPDATE script_texts SET content_loc3='Darum habe ich nicht gebeten!' WHERE entry=-1564050; /* content: I didn\'t ask for this! */
UPDATE script_texts SET content_loc3='Ich will nicht zurück!' WHERE entry=-1564052; /* content: I don\'t want to go back! */
UPDATE script_texts SET content_loc3='Was mache ich jetzt nur?' WHERE entry=-1564053; /* content: Now what do I do? */
UPDATE script_texts SET content_loc3='Ihr könnt alles haben, was Ihr wollt... doch es hat einen Preis.' WHERE entry=-1564055; /* content: You can have anything you desire... for a price. */
UPDATE script_texts SET content_loc3='Eure Gier ist Euer Untergang!' WHERE entry=-1564058; /* content: Your reach exceeds your grasp. */
UPDATE script_texts SET content_loc3='Seid auf der Hut, ich lebe!' WHERE entry=-1564062; /* content: Beware: I live! */
UPDATE script_texts SET content_loc3='So... dumm.' WHERE entry=-1564063; /* content: So... foolish. */
UPDATE script_texts SET content_loc3='Auf die Knie!' WHERE entry=-1564066; /* content: On your knees! */
UPDATE script_texts SET content_loc3='Wollt Ihr mich auf die Probe stellen?' WHERE entry=-1564069; /* content: You wish to test me? */
UPDATE script_texts SET content_loc3='Ich habe Besseres zu tun...' WHERE entry=-1564070; /* content: I have better things to do... */
UPDATE script_texts SET content_loc3='Flieht oder sterbt!' WHERE entry=-1564071; /* content: Flee or die! */
UPDATE script_texts SET content_loc3='Gemeinsprache... welch barbarische Zunge. Bandal!' WHERE entry=-1564072; /* content: Common... such a crude language. Bandal! */
UPDATE script_texts SET content_loc3='Genug gespielt!' WHERE entry=-1564073; /* content: Enough games! */
UPDATE script_texts SET content_loc3='Für Quel\'Thalas! Für den Sonnenbrunnen!' WHERE entry=-1564075; /* content: For Quel\'Thalas! For the Sunwell! */
UPDATE script_texts SET content_loc3='Es gibt keine zweite Chance!' WHERE entry=-1564079; /* content: No second chances! */
UPDATE script_texts SET content_loc3='Ihr gehört MIR!' WHERE entry=-1564081; /* content: You are mine! */
UPDATE script_texts SET content_loc3='Anar\'alah belore!' WHERE entry=-1564082; /* content: Anar\'alah belore! */
UPDATE script_texts SET content_loc3='Ich stecke voller Überraschungen!' WHERE entry=-1564083; /* content: I\'m full of surprises! */
UPDATE script_texts SET content_loc3='Sha\'amoor ara mashal?' WHERE entry=-1564084; /* content: Sha\'amoor ara mashal? */
UPDATE script_texts SET content_loc3='Selama am\'oronor!' WHERE entry=-1564085; /* content: Selama am\'oronor! */
UPDATE script_texts SET content_loc3='Meine Arbeit ist getan.' WHERE entry=-1564087; /* content: My work is done. */
UPDATE script_texts SET content_loc3='Shorel\'aran.' WHERE entry=-1564088; /* content: Shorel\'aran. */
UPDATE script_texts SET content_loc3='Gut gemacht!' WHERE entry=-1564089; /* content: Well done! */
UPDATE script_texts SET content_loc3='Vortrefflich erlegt!' WHERE entry=-1564090; /* content: A glorious kill! */
UPDATE script_texts SET content_loc3='Belesa menoor!' WHERE entry=-1564092; /* content: Belesa menoor! */
UPDATE script_texts SET content_loc3='Das Schicksal... erwartet mich.' WHERE entry=-1564095; /* content: Destiny... awaits. */
UPDATE script_texts SET content_loc3='Akama. Euer falsches Spiel überrascht mich nicht. Ich hätte Euch und Eure missgestalteten Brüder schon vor langer Zeit abschlachten sollen.' WHERE entry=-1564097; /* content: Akama. Your duplicity is hardly surprising. I should have slaughtered you and your malformed brethren long ago. */
UPDATE script_texts SET content_loc3='Die Zeit ist gekommen: der Augenblick ist endlich da!' WHERE entry=-1564100; /* content: The time has come! The moment is at hand! */
UPDATE script_texts SET content_loc3='Ihr wisst nicht, was Euch erwartet!' WHERE entry=-1564101; /* content: You are not prepared! */
UPDATE script_texts SET content_loc3='War\'s das schon, Sterbliche? Ist das alles, was Ihr zu bieten habt?' WHERE entry=-1564102; /* content: Is this it, mortals? Is this all the fury you can muster? */
UPDATE script_texts SET content_loc3='Ihr Zorn ist nichts im Vergleich zu dem meinen, Illidan. Wir beide haben noch eine Rechnung offen.' WHERE entry=-1564103; /* content: Their fury pales before mine, Illidan. We have some unsettled business between us. */
UPDATE script_texts SET content_loc3='Endlich ist meine lange Jagd zu Ende. Heute wird die Gerechtigkeit obsiegen!' WHERE entry=-1564105; /* content: My long hunt is finally over. Today, Justice will be done! */
UPDATE script_texts SET content_loc3='Spürt den Hass von zehntausend Jahren!' WHERE entry=-1564106; /* content: Feel the hatred of ten thousand years! */
UPDATE script_texts SET content_loc3='Es ist vollbracht. Ihr seid geschlagen.' WHERE entry=-1564107; /* content: It is finished. You are beaten. */
UPDATE script_texts SET content_loc3='Ihr habt gewonnen... Maiev. Aber die Jägerin... ist nichts ohne die Beute. Ihr... seid nichts... ohne mich.' WHERE entry=-1564108; /* content: You have won... Maiev. But the huntress... is nothing without the hunt. You... are nothing... without me. */
UPDATE script_texts SET content_loc3='Er hat Recht. Ich fühle nichts... Ich bin... nichts.' WHERE entry=-1564109; /* content: He\'s right. I feel nothing... I am... nothing. */
UPDATE script_texts SET content_loc3='Lebt Wohl, Ihr Helden.' WHERE entry=-1564110; /* content: Farewell, champions. */
UPDATE script_texts SET content_loc3='Ihr wisst nicht, was Macht bedeutet!' WHERE entry=-1564114; /* content: You know nothing of power! */
UPDATE script_texts SET content_loc3='Das ist für Naisha!' WHERE entry=-1564116; /* content: That is for Naisha! */
UPDATE script_texts SET content_loc3='Dieses Mal gibt es kein Gefängnis für Euch!' WHERE entry=-1564118; /* content: There shall be no prison for you this time! */
UPDATE script_texts SET content_loc3='Kommt, meine Diener. Gebt diesem Verräter, was er verdient.' WHERE entry=-1564121; /* content: Come, my minions. Deal with this traitor as he deserves! */
UPDATE script_texts SET content_loc3='Das ist viel zu einfach!' WHERE entry=-1564124; /* content: This is too easy! */
UPDATE script_texts SET content_loc3='Gesindel, keiner von Euch wird je Hand an mich legen!' WHERE entry=-1564125; /* content: I will not be touched by rabble such as you! */
UPDATE script_texts SET content_loc3='Spürt die Flammen von Azzinoth!' WHERE entry=-1564126; /* content: Behold the flames of Azzinoth! */
UPDATE script_texts SET content_loc3='Blickt in die Augen des Verräters!' WHERE entry=-1564127; /* content: Stare into the eyes of the Betrayer! */
UPDATE script_texts SET content_loc3='Erzittert vor der Macht des Dämonen!' WHERE entry=-1564128; /* content: Behold the power... of the demon within! */
UPDATE script_texts SET content_loc3='Zerschlagene der Aschenzungen, Euer Anführer spricht!' WHERE entry=-1564130; /* content: Broken of the Ashtongue tribe, your leader speaks! */
UPDATE script_texts SET content_loc3='Ich schaffe das nicht allein...' WHERE entry=-1564132; /* content: I cannot do this alone... */
UPDATE script_texts SET content_loc3='Ihr seid nicht allein, Akama.' WHERE entry=-1564133; /* content: You are not alone, Akama. */
UPDATE script_texts SET content_loc3='Euer Volk wird immer mit Euch sein!' WHERE entry=-1564134; /* content: Your people will always be with you! */
UPDATE script_texts SET content_loc3='Ihr werdet die Hand von Gruul nicht besiegen!' WHERE entry=-1565001; /* content: You will not defeat the hand of Gruul! */
UPDATE script_texts SET content_loc3='Pah! Das beweist gar nichts!' WHERE entry=-1565003; /* content: Pah! Does not prove anything! */
UPDATE script_texts SET content_loc3='Gut, nun kämpft gegen mich!' WHERE entry=-1565005; /* content: Good, now you fight me! */
UPDATE script_texts SET content_loc3='%s wird größer!' WHERE entry=-1565019; /* content: %s grows in size! */
UPDATE script_texts SET content_loc3='Die Geister der Winde besiegeln euer Schicksal!' WHERE entry=-1568000; /* content: Spirits of da wind be your doom! */
UPDATE script_texts SET content_loc3='Jetzt sollt ihr brennen!' WHERE entry=-1568001; /* content: I burn ya now! */
UPDATE script_texts SET content_loc3='Wo is\' meine Brut? Was ist mit den Eiern?' WHERE entry=-1568002; /* content: Where ma hatcha? Get to work on dem eggs! */
UPDATE script_texts SET content_loc3='Stärke... und zwar viel davon.' WHERE entry=-1568003; /* content: I show you strength... in numbers. */
UPDATE script_texts SET content_loc3='Eure Zeit ist abgelaufen!' WHERE entry=-1568004; /* content: You done run outta time! */
UPDATE script_texts SET content_loc3='Alles gleich vorbei, Mann!' WHERE entry=-1568005; /* content: It all be over now, mon! */
UPDATE script_texts SET content_loc3='Tazaga-choo!' WHERE entry=-1568006; /* content: Tazaga-choo! */
UPDATE script_texts SET content_loc3='Vorwärts, Wachen! Zeit zum Töt\'n!' WHERE entry=-1568010; /* content: Get da move on, guards! It be killin\' time! */
UPDATE script_texts SET content_loc3='Los jetzt, Wachen! Vor wem fürchtet ihr euch mehr? Vor denen oder vor mir?' WHERE entry=-1568011; /* content: Guards, go already! Who you more afraid of, dem... or me? */
UPDATE script_texts SET content_loc3='Reitet los! Reitet los und bringt mir ein paar Köpfe!' WHERE entry=-1568012; /* content: Ride now! Ride out dere and bring me back some heads! */
UPDATE script_texts SET content_loc3='Ich verliere die Geduld! Los jetzt: auf dass sie sich wünschen, sie wären niemals geboren!' WHERE entry=-1568013; /* content: I be losin\' me patience! Go on: make dem wish dey was never born! */
UPDATE script_texts SET content_loc3='Was könnte es Schöneres geben, als dem Bärengeist auf ewig zu dienen? Tretet näher. Bringt mir eure Seelen!' WHERE entry=-1568014; /* content: What could be better than servin\' da bear spirit for eternity? Come closer now. Bring your souls to me! */
UPDATE script_texts SET content_loc3='Ihr sterbt noch schnell genug!' WHERE entry=-1568016; /* content: You be dead soon enough! */
UPDATE script_texts SET content_loc3='Ich bringe Schmerz!' WHERE entry=-1568017; /* content: I bring da pain! */
UPDATE script_texts SET content_loc3='Ihr provoziert die Bestie, jetzt werdet ihr sie kennenlernen!' WHERE entry=-1568018; /* content: You call on da beast, you gonna get more dan you bargain for! */
UPDATE script_texts SET content_loc3='Die Amani werd\'n wieder herrsch\'n!' WHERE entry=-1568022; /* content: Da Amani gonna rule again! */
UPDATE script_texts SET content_loc3='Ich... erwarte euch auf der anderen Seite...' WHERE entry=-1568023; /* content: I... be waitin\' on da udda side.... */
UPDATE script_texts SET content_loc3='Ich bin der Jäger! Ihr seid die Beute...' WHERE entry=-1568026; /* content: I be da predator! You da prey... */
UPDATE script_texts SET content_loc3='Labt euch, meine Brüder!' WHERE entry=-1568027; /* content: Feed, me bruddahs! */
UPDATE script_texts SET content_loc3='Flasche!' WHERE entry=-1568030; /* content: Ya got nothin\'! */
UPDATE script_texts SET content_loc3='Jammern hilft nicht!' WHERE entry=-1568031; /* content: Stop your cryin\'! */
UPDATE script_texts SET content_loc3='Ihr könnt... meinen Geist... nicht töten!' WHERE entry=-1568032; /* content: You can\'t... kill... me spirit! */
UPDATE script_texts SET content_loc3='Ein Gewitter zieht auf!' WHERE entry=-1568033; /* content: An Electrical Storm Appears! */
UPDATE script_texts SET content_loc3='Auf die Knie und verneigt euch... vor den Reißzähnen und der Klaue!' WHERE entry=-1568034; /* content: Get on ya knees and bow.... to da fang and claw! */
UPDATE script_texts SET content_loc3='Ich kämpfe mit wildem Geist...' WHERE entry=-1568035; /* content: I fight wit\' untamed spirit.... */
UPDATE script_texts SET content_loc3='Geist, zurück zu mir!' WHERE entry=-1568036; /* content: Spirit, come back to me! */
UPDATE script_texts SET content_loc3='Ich werde euch zerfetzen!' WHERE entry=-1568037; /* content: Me gonna carve ya now! */
UPDATE script_texts SET content_loc3='Diesen Ort verlasst Ihr nicht in einem Stück!' WHERE entry=-1568038; /* content: You gonna leave in pieces! */
UPDATE script_texts SET content_loc3='Chaga... choka\'jinn.' WHERE entry=-1568042; /* content: Chaga... choka\'jinn. */
UPDATE script_texts SET content_loc3='Ich werde warten, Fremde. Euer Tod wird mich stärken!' WHERE entry=-1568044; /* content: I be waitin, strangers. Your deaths gonna make me stronger! */
UPDATE script_texts SET content_loc3='Aus diesem Alptraum gibt es kein Erwachen!' WHERE entry=-1568047; /* content: Dis a nightmare ya don\' wake up from! */
UPDATE script_texts SET content_loc3='Azzaga choogo zinn!' WHERE entry=-1568048; /* content: Azzaga choogo zinn! */
UPDATE script_texts SET content_loc3='Ihr gehört jetzt mir!' WHERE entry=-1568049; /* content: Your will belong ta me now! */
UPDATE script_texts SET content_loc3='Eure Seele wird bluten!' WHERE entry=-1568051; /* content: Your soul gonna bleed! */
UPDATE script_texts SET content_loc3='Das wird nichts ändern!' WHERE entry=-1568052; /* content: It not gonna make no difference. */
UPDATE script_texts SET content_loc3='Euer Tod wird noch grausamer.' WHERE entry=-1568053; /* content: You gonna die worse dan him. */
UPDATE script_texts SET content_loc3='Das lässt mich kalt.' WHERE entry=-1568054; /* content: Dat no bodda me. */
UPDATE script_texts SET content_loc3='Oh, ich hab ein paar neue Tricks. Sagt \'Hallo\' zu Bruder Bär...' WHERE entry=-1568058; /* content: Got me some new tricks... like me brudda bear.... */
UPDATE script_texts SET content_loc3='Niemand versteckt sich vor dem Adler!' WHERE entry=-1568059; /* content: Dere be no hidin\' from da eagle! */
UPDATE script_texts SET content_loc3='Lernt meine Brüder kennen: Reißzahn und Klaue!' WHERE entry=-1568060; /* content: Let me introduce you to me new bruddas: fang and claw! */
UPDATE script_texts SET content_loc3='Was starrt ihr in die Luft? Der Drachenfalke steht schon vor euch!' WHERE entry=-1568061; /* content: Ya don\' have to look to da sky to see da dragonhawk! */
UPDATE script_texts SET content_loc3='Das Feuer wird Euch den Garaus mach\'n!' WHERE entry=-1568062; /* content: Fire kill you just as quick! */
UPDATE script_texts SET content_loc3='Die Geister werden sich heute gütlich tun! Beginnt mit der Zeremonie, opfert die Gefangenen... macht Platz für unsere neuen Gäste!' WHERE entry=-1568068; /* content: Da spirits gonna feast today! Begin da ceremonies, sacrifice da prisoners... make room for our new guests! */
UPDATE script_texts SET content_loc3='Ihr habt die Wahl, Eindringlinge! Ein jeder meiner Priester wird Euch mit Freuden entgegenkommen...' WHERE entry=-1568069; /* content: Take your pick, trespassers! Any of ma priests be happy to accommodate ya. */
UPDATE script_texts SET content_loc3='Nicht so schüchtern. Vor euch waren schon Tausende hier. Ihr werdet nicht allein sein.' WHERE entry=-1568070; /* content: Don\'t be shy. Thousands have come before you. Ya not be alone in your service. */
UPDATE script_texts SET content_loc3='Ihr werdet versagen, Fremde. Viele haben es vor euch versucht, aber dadurch werden wir nur noch stärker!' WHERE entry=-1568071; /* content: Ya gonna fail, strangers. Many try before you, but dey only make us stronger! */
UPDATE script_texts SET content_loc3='Ihr habt Euch umsonst bemüht, Eindringlinge. Die Rituale sind fast abgeschlossen.' WHERE entry=-1568072; /* content: Your efforts was in vain, trespassers. The rituals nearly be complete. */
UPDATE script_texts SET content_loc3='Bald werden die Käfige leer und die Opfer vollbracht sein. Dann werdet Ihr an ihre Stelle treten.' WHERE entry=-1568073; /* content: Soon da cages gonna be empty, da sacrifices be complete, and you gonna take dere places. */
UPDATE script_texts SET content_loc3='Euch bleibt nicht mehr viel Zeit, Fremde. Bald werdet Ihr Euch zu den Seelen gesellen, die Ihr nicht zu retten vermochtet.' WHERE entry=-1568074; /* content: Time be running low, strangers. Soon you gonna join da souls of dem ya failed to save. */
UPDATE script_texts SET content_loc3='Beeilt Euch, meine Priester! Nichts darf die Rituale unterbrechen!' WHERE entry=-1568075; /* content: Make haste, ma priests! Da rituals must not be interrupted! */
UPDATE script_texts SET content_loc3='Netter Versuch... aber jetzt folgt Ihr denen, die schon vor Euch versagt haben.' WHERE entry=-1568076; /* content: Ya make a good try... but now you gonna join da ones who already fall. */
UPDATE script_texts SET content_loc3='Seht genau hin: Unsere Verbindung zur Geisterwelt wird mit jeder Opferung stärker. Schon bald wird man uns nicht mehr aufhalten können!' WHERE entry=-1568078; /* content: Watch now. Every offering gonna strengthen our ties to da spirit world. Soon, we gonna be unstoppable! */
UPDATE script_texts SET content_loc3='Laut meinen Berechnungen wird das Siegel auf diesen Türen aufbrechen, wenn genug von uns gleichzeitig den Gong schlagen. Danach können wir eintreten.' WHERE entry=-1568080; /* content: According to my calculations, if enough of us bang the gong at once the seal on these doors will break and we can enter. */
UPDATE script_texts SET content_loc3='%s absorbiert die Essenz des Bärengeistes!' WHERE entry=-1568082; /* content: %s absorbs the essence of the bear spirit! */
UPDATE script_texts SET content_loc3='%s absorbiert die Essenz des Adlergeistes!' WHERE entry=-1568083; /* content: %s absorbs the essence of the eagle spirit! */
UPDATE script_texts SET content_loc3='%s absorbiert die Essenz des Luchsgeistes!' WHERE entry=-1568084; /* content: %s absorbs the essence of the lynx spirit! */
UPDATE script_texts SET content_loc3='%s absorbiert die Essenz des Drachenfalkengeistes!' WHERE entry=-1568085; /* content: %s absorbs the essence of the dragonhawk spirit! */
UPDATE script_texts SET content_loc3='Euer Blut gehört mir!' WHERE entry=-1574000; /* content: Your blood is mine! */
UPDATE script_texts SET content_loc3='Nicht so schnell.' WHERE entry=-1574001; /* content: Not so fast. */
UPDATE script_texts SET content_loc3='Die Nacht... bricht herein.' WHERE entry=-1574004; /* content: I join... the night. */
UPDATE script_texts SET content_loc3='Ich werde mein Gesicht mit Eurem Blut bemalen!' WHERE entry=-1574005; /* content: I\'ll paint my face with your blood! */
UPDATE script_texts SET content_loc3='Mein Leben für... den Todesgott!' WHERE entry=-1574007; /* content: My life for the... death god! */
UPDATE script_texts SET content_loc3='Nein! Ich bin... besser, Ich bin...' WHERE entry=-1574008; /* content: No! I can do... better! I can... */
UPDATE script_texts SET content_loc3='Mjul orm agn gjor!' WHERE entry=-1574009; /* content: Mjul orm agn gjor! */
UPDATE script_texts SET content_loc3='Ich bin ein geborener Krieger!' WHERE entry=-1574010; /* content: I am a warrior born! */
UPDATE script_texts SET content_loc3='Dalronn! Versucht, den Mut aufzubringen, an meiner Seite zu kämpfen!' WHERE entry=-1574011; /* content: Dalronn! See if you can muster the nerve to join my attack! */
UPDATE script_texts SET content_loc3='Noch... nicht... vorbei.' WHERE entry=-1574012; /* content: Not... over... yet. */
UPDATE script_texts SET content_loc3='Der Tod eines Kriegers.' WHERE entry=-1574013; /* content: A warrior\'s death. */
UPDATE script_texts SET content_loc3='Um Himmels Willen, hör auf zu quasseln, du Schwachkopf! Hau einfach drauf!' WHERE entry=-1574016; /* content: By all means, don\'t assess the situation, you halfwit! Just jump into the fray! */
UPDATE script_texts SET content_loc3='Bis... bald.' WHERE entry=-1574017; /* content: See... you... soon. */
UPDATE script_texts SET content_loc3='Die höchste... aller... Ehren.' WHERE entry=-1574018; /* content: There\'s no... greater... glory. */
UPDATE script_texts SET content_loc3='Ihr könntet mir noch nützlich sein.' WHERE entry=-1574019; /* content: You may serve me yet. */
UPDATE script_texts SET content_loc3='Skarvald, du unfähiger Klotz! Steh wieder auf und mach dich nützlich!' WHERE entry=-1574020; /* content: Skarvald, you incompetent slug! Return and make yourself useful! */
UPDATE script_texts SET content_loc3='%s brüllt!' WHERE entry=-1574022; /* content: %s roars! */
UPDATE script_texts SET content_loc3='Mein Meister! Ich tat, was Ihr verlangtet, und ersuche Euch um Euren Segen!' WHERE entry=-1575000; /* content: My liege! I have done as you asked, and now beseech you for your blessing! */
UPDATE script_texts SET content_loc3='Das Gefühl ist... unvorstellbar. Ich stehe Euch zu Befehl, mein König.' WHERE entry=-1575002; /* content: The sensation is... beyond my imagining. I am yours to command, my king. */
UPDATE script_texts SET content_loc3='Eure erste Prüfung wartet. Vernichtet die Eindringlinge.' WHERE entry=-1575003; /* content: Your first test awaits you. Destroy our uninvited guests. */
UPDATE script_texts SET content_loc3='Mit Vergnügen töte ich sie in Eurem Namen! Kommt, Feinde der Geißel! Ich zeige Euch die Macht des Lichkönigs!' WHERE entry=-1575004; /* content: I will be happy to slaughter them in your name! Come, enemies of the Scourge! I will show you the might of the Lich King! */
UPDATE script_texts SET content_loc3='Ich werde Eure Seelen unterjochen!' WHERE entry=-1575005; /* content: I will vanquish your soul! */
UPDATE script_texts SET content_loc3='Ihr wart ein Narr, die Macht des Lichkönigs herauszufordern!' WHERE entry=-1575006; /* content: You were a fool to challenge the power of the Lich King! */
UPDATE script_texts SET content_loc3='Euer Ende ist unvermeidlich.' WHERE entry=-1575011; /* content: Your end is inevitable. */
UPDATE script_texts SET content_loc3='Yor-guul mak!' WHERE entry=-1575012; /* content: Yor-guul mak! */
UPDATE script_texts SET content_loc3='Noch ein letztes Wort?' WHERE entry=-1575013; /* content: Any last words? */
UPDATE script_texts SET content_loc3='Ich bin nicht so weit ge... kommen... um...' WHERE entry=-1575014; /* content: Nooo! I did not come this far... to... */
UPDATE script_texts SET content_loc3='Was... dieser Ort? Ich... werde... dich vernichten!' WHERE entry=-1575015; /* content: What this place? I will destroy you! */
UPDATE script_texts SET content_loc3='Eine leichte Aufgabe!' WHERE entry=-1575017; /* content: An easy task! */
UPDATE script_texts SET content_loc3='Nun seid ihr nicht mehr so vorlaut, was?' WHERE entry=-1575026; /* content: Not so brash now, are you? */
UPDATE script_texts SET content_loc3='Ihr räudigen Halunken! Eure Leichen werden feine Appetithappen für meinen neuen Drachen abgeben!' WHERE entry=-1575029; /* content: You motherless knaves! Your corpses will make fine morsels for my new drake! */
UPDATE script_texts SET content_loc3='Björn vom Schwarzen Sturm! Beehrt mich nun mit Eurer Anwesenheit!' WHERE entry=-1575032; /* content: Bjorn of the Black Storm! Honor me now with your presence! */
UPDATE script_texts SET content_loc3='Was... erwartet... mich... jetzt?' WHERE entry=-1575040; /* content: What... awaits me... now? */
UPDATE script_texts SET content_loc3='%s holt tief Luft.' WHERE entry=-1575041; /* content: %s takes a deep breath. */
UPDATE script_texts SET content_loc3='Es ist genug von mir für alle da.' WHERE entry=-1576002; /* content: There\'s plenty of me to go around. */
UPDATE script_texts SET content_loc3='Nun bringen wir\'s zu Ende!' WHERE entry=-1576003; /* content: Now to finish the job! */
UPDATE script_texts SET content_loc3='Verdammtes... Glück.' WHERE entry=-1576005; /* content: Damn the... luck. */
UPDATE script_texts SET content_loc3='Das Chaos wartet.' WHERE entry=-1576006; /* content: Chaos beckons. */
UPDATE script_texts SET content_loc3='Realität... auflösen.' WHERE entry=-1576007; /* content: Reality... unwoven. */
UPDATE script_texts SET content_loc3='Unzerstörbar.' WHERE entry=-1576008; /* content: Indestructible. */
UPDATE script_texts SET content_loc3='Natürlich.' WHERE entry=-1576010; /* content: Of course. */
UPDATE script_texts SET content_loc3='Neeiin!' WHERE entry=-1576011; /* content: Noo! */
UPDATE script_texts SET content_loc3='Aarrhhh!' WHERE entry=-1576015; /* content: Aaggh! */
UPDATE script_texts SET content_loc3='Bewahren? Warum? Darin liegt keine Wahrheit. Nein, nein! <Sie lacht.>... Nur in Zerstörung! Das habe ich jetzt verstanden!' WHERE entry=-1576016; /* content: Preserve? Why? There\'s no truth in it. No no no... only in the taking! I see that now! */
UPDATE script_texts SET content_loc3='Bleibt. Genießt Eure letzten Atemzüge.' WHERE entry=-1576017; /* content: Stay. Enjoy your final moments. */
UPDATE script_texts SET content_loc3='Tötet mich! Tötet mich! Oder ich schwöre bei der Drachenkönigin, Ihr erblickt das Licht nie wieder!' WHERE entry=-1576018; /* content: Finish it! Finish it! Kill me, or I swear by the Dragonqueen you\'ll never see daylight again! */
UPDATE script_texts SET content_loc3='So sieht die Wahrheit aus!' WHERE entry=-1576019; /* content: Now we\'ve come to the truth! */
UPDATE script_texts SET content_loc3='Drachenkönigin... Lebensbinderin... erhaltet... mich.' WHERE entry=-1576020; /* content: Dragonqueen... Life-Binder... preserve... me. */
UPDATE script_texts SET content_loc3='%s öffnet einen chaotischen Riss!' WHERE entry=-1576021; /* content: %s opens a Chaotic Rift! */
UPDATE script_texts SET content_loc3='Meine Begleiter haben versagt. Vielleicht ist eine andere Demonstration angebracht.' WHERE entry=-1578001; /* content: Clearly my pets failed. Perhaps another demonstration is in order. */
UPDATE script_texts SET content_loc3='Ihr kämpft noch immer. Noch immer haltet ihr an falschen Prinzipien fest. Wenn ihr überlebt, findet ihr mich im mittleren Ring.' WHERE entry=-1578002; /* content: Still you fight. Still you cling to misguided principles. If you survive, you\'ll find me in the center ring. */
UPDATE script_texts SET content_loc3='Arme, blinde Narren!' WHERE entry=-1578003; /* content: Poor blind fools! */
UPDATE script_texts SET content_loc3='Ein kleiner... nur ein kleiner Vorgeschmack der Macht des Spruchwirkers!' WHERE entry=-1578004; /* content: A taste... just a small taste... of the Spell-Weaver\'s power! */
UPDATE script_texts SET content_loc3='So viel instabile Energie... doch um euch zu vernichten, ist es das Risiko wert!' WHERE entry=-1578005; /* content: So much unstable energy... but worth the risk to destroy you! */
UPDATE script_texts SET content_loc3='Wenn Ihr nur verstehen würdet!' WHERE entry=-1578006; /* content: If only you understood! */
UPDATE script_texts SET content_loc3='Begreift Ihr jetzt? Begreift Ihr?' WHERE entry=-1578007; /* content: Now do you see? Do you?! */
UPDATE script_texts SET content_loc3='Bedauerlich, aber notwendig.' WHERE entry=-1578008; /* content: Unfortunate, but necessary. */
UPDATE script_texts SET content_loc3='Einfaltspinsel! Ihr könnt die Kräfte nicht verstehen, die ihr entfesselt habt. Der Leylinienstrom wird nicht unterbrochen! Eure Niederlage wird vernichtend sein!' WHERE entry=-1578010; /* content: Simpletons! You cannot comprehend the forces you have set in motion. The ley line conduit will not be disrupted! Your defeat shall be absolute! */
UPDATE script_texts SET content_loc3='Ihr dreisten Eindringlinge gehört nicht hierher! Ich werde euch ein Ende bereiten!' WHERE entry=-1578011; /* content: You brash interlopers are out of your element! I will ground you! */
UPDATE script_texts SET content_loc3='Es ist ein Kinderspiel, eure Flammen auszulöschen.' WHERE entry=-1578013; /* content: It is trivial to extinguish your fire! */
UPDATE script_texts SET content_loc3='Jetzt hilft euch keine Naturmagie mehr!' WHERE entry=-1578014; /* content: No magic of nature will help you now! */
UPDATE script_texts SET content_loc3='Solch Frevel... solch Arroganz... muss BESTRAFT WERDEN!' WHERE entry=-1578015; /* content: Such insolence... such arrogance... must be PUNISHED! */
UPDATE script_texts SET content_loc3='ZURÜCK in die Erde mit Euch!' WHERE entry=-1578017; /* content: Back to the earth with you! */
UPDATE script_texts SET content_loc3='Genießt den Flug!' WHERE entry=-1578018; /* content: Enjoy the fall! */
UPDATE script_texts SET content_loc3='Erwartet keine Gnade!' WHERE entry=-1578020; /* content: There will be no mercy! */
UPDATE script_texts SET content_loc3='Pustet sie weg! Vernichtet sie!' WHERE entry=-1578021; /* content: Blast them! Destroy them! */
UPDATE script_texts SET content_loc3='Macht keine Gefangenen! Angriff!' WHERE entry=-1578022; /* content: Take no prisoners! Attack! */
UPDATE script_texts SET content_loc3='Schlagt zu! Löscht sie aus!' WHERE entry=-1578023; /* content: Strike now! Obliterate them! */
UPDATE script_texts SET content_loc3='Anomalien bilden sich, als %s auf die Astralebene wechselt!' WHERE entry=-1578024; /* content: Anomalies form as %s shifts into the Astral Plane! */
UPDATE script_texts SET content_loc3='%s beginnt, eine mächtige arkane Explosion zu wirken!' WHERE entry=-1578025; /* content: %s begins to cast Empowered Arcane Explosion! */
UPDATE script_texts SET content_loc3='Ihr wurdet gewarnt!' WHERE entry=-1578026; /* content: You were warned! */
UPDATE script_texts SET content_loc3='Das Oculus ist unser!' WHERE entry=-1578027; /* content: The Oculus is ours! */
UPDATE script_texts SET content_loc3='Sie sind... zu stark! Habe ihre... Kräfte... unterschätzt.' WHERE entry=-1578028; /* content: They are... too strong! Underestimated their... fortitude. */
UPDATE script_texts SET content_loc3='%s ruft einen azurblauen Ringhauptmann!' WHERE entry=-1578029; /* content: %s calls an Azure Ring Captain! */
UPDATE script_texts SET content_loc3='%s fliegt davon.' WHERE entry=-1578030; /* content: %s flies away. */
UPDATE script_texts SET content_loc3='Ich werde Euch auslöschen!' WHERE entry=-1580001; /* content: I will purge you! */
UPDATE script_texts SET content_loc3='Dies ist nur der Anfang Eurer Qualen!' WHERE entry=-1580002; /* content: Your pain has only begun! */
UPDATE script_texts SET content_loc3='Im Namen Kil\'jaedens!' WHERE entry=-1580003; /* content: In the name of Kil\'jaeden! */
UPDATE script_texts SET content_loc3='Ihr wurdet gewarnt!' WHERE entry=-1580004; /* content: You were warned! */
UPDATE script_texts SET content_loc3='Es ist vollbracht - ich bin erwacht! Sterben sollt Ihr alle!' WHERE entry=-1580005; /* content: My awakening is complete! You shall all perish! */
UPDATE script_texts SET content_loc3='Beeilt Euch! Es ist nicht mehr viel von mir da!' WHERE entry=-1580008; /* content: Hurry! There is not much of me left! */
UPDATE script_texts SET content_loc3='Ich stehe für immer in Eurer Schuld. Sobald wir über Kil\'jaeden triumphiert haben, wird diese gesamte Welt in Eurer Schuld stehen.' WHERE entry=-1580009; /* content: I am forever in your debt. Once we have triumphed over Kil\'jaeden, this entire world will be in your debt as well. */
UPDATE script_texts SET content_loc3='Ich schwelge in eurem Elend!' WHERE entry=-1580012; /* content: Your misery is my delight! */
UPDATE script_texts SET content_loc3='Ich werde euch beim Bluten zusehen!' WHERE entry=-1580013; /* content: I will watch you bleed! */
UPDATE script_texts SET content_loc3='Habt ihr es noch nicht gehört? Ich gewinne immer!' WHERE entry=-1580015; /* content: Haven\'t you heard? I always win! */
UPDATE script_texts SET content_loc3='Ich habe lange genug mit euch herumgespielt!' WHERE entry=-1580016; /* content: I have toyed with you long enough! */
UPDATE script_texts SET content_loc3='Mickrige Echse! Tod ist die einzige Antwort, die du hier finden wirst!' WHERE entry=-1580017; /* content: Puny lizard! Death is the only answer you\'ll find here! */
UPDATE script_texts SET content_loc3='Ich werde Euch zerquetschen!' WHERE entry=-1580019; /* content: I will crush you! */
UPDATE script_texts SET content_loc3='Das hat Spaß gemacht, aber ich warte immer noch auf eine echte Herausforderung!' WHERE entry=-1580020; /* content: That was fun, but I still await a true challenge! */
UPDATE script_texts SET content_loc3='Stirb, Insekt!' WHERE entry=-1580023; /* content: Perish, insect! */
UPDATE script_texts SET content_loc3='Du bist Hackfleisch!' WHERE entry=-1580024; /* content: You are meat! */
UPDATE script_texts SET content_loc3='Zu einfach!' WHERE entry=-1580025; /* content: Too easy! */
UPDATE script_texts SET content_loc3='Bringt den Kampf zu mir!' WHERE entry=-1580026; /* content: Bring the fight to me! */
UPDATE script_texts SET content_loc3='Ein neuer Tag, eine neue glorreiche Schlacht!' WHERE entry=-1580027; /* content: Another day, another glorious battle! */
UPDATE script_texts SET content_loc3='Dafür lebe ich!' WHERE entry=-1580028; /* content: I live for this! */
UPDATE script_texts SET content_loc3='Haltet ein, Freunde! Es gibt hier noch etwas zu erfahren, bevor dieser Teufel seinem Schicksal ins Auge blickt!' WHERE entry=-1580031; /* content: Hold, friends! There is information to be had before this devil meets his fate! */
UPDATE script_texts SET content_loc3='Wo ist Anveena, Dämon? Was ist mit Kalec geschehen?' WHERE entry=-1580032; /* content: Where is Anveena, demon? What has become of Kalec? */
UPDATE script_texts SET content_loc3='Du wirst mir sagen, wo sie sind!' WHERE entry=-1580033; /* content: You will tell me where they are! */
UPDATE script_texts SET content_loc3='Ehre sei Kil\'jaeden! Tod allen, die sich ihm widersetzen!' WHERE entry=-1580036; /* content: Glory to Kil\'jaeden! Death to all who oppose! */
UPDATE script_texts SET content_loc3='Ich töte für den Meister!' WHERE entry=-1580037; /* content: I kill for the master! */
UPDATE script_texts SET content_loc3='Chaos...' WHERE entry=-1580049; /* content: Chaos... */
UPDATE script_texts SET content_loc3='Schatten, dem Feuer zu Hilfe!' WHERE entry=-1580052; /* content: Shadow to the aid of fire! */
UPDATE script_texts SET content_loc3='Alythess! Dein Feuer brennt in mir!' WHERE entry=-1580053; /* content: Alythess! Your fire burns within me! */
UPDATE script_texts SET content_loc3='Ee-nok Kryul!' WHERE entry=-1580055; /* content: Ee-nok Kryul! */
UPDATE script_texts SET content_loc3='Ich... vergehe.' WHERE entry=-1580056; /* content: I... fade. */
UPDATE script_texts SET content_loc3='Zeit ist ein Luxus, den ihr euch nicht mehr leisten könnt!' WHERE entry=-1580057; /* content: Time is a luxury you no longer possess! */
UPDATE script_texts SET content_loc3='Feuer dem Schatten zu Hilfe!' WHERE entry=-1580058; /* content: Fire to the aid of shadow! */
UPDATE script_texts SET content_loc3='Sacrolash!' WHERE entry=-1580059; /* content: Sacrolash! */
UPDATE script_texts SET content_loc3='Euer Glück ist verbraucht!' WHERE entry=-1580063; /* content: Your luck has run its course! */
UPDATE script_texts SET content_loc3='Dies ist der Zweck all meiner Pläne!' WHERE entry=-1580064; /* content: All my plans have led to this! */
UPDATE script_texts SET content_loc3='Ich habe lange genug gewartet!' WHERE entry=-1580066; /* content: I have waited long enough! */
UPDATE script_texts SET content_loc3='Enttäuscht mich und ihr werdet in alle Ewigkeit leiden!' WHERE entry=-1580067; /* content: Fail me and suffer for eternity! */
UPDATE script_texts SET content_loc3='Entzieht dem Mädchen die Kraft! Entzieht sie ihr, bis sie nichts weiter als eine leere Hülle ist!' WHERE entry=-1580068; /* content: Drain the girl! Drain her power until there is nothing but a vacant shell! */
UPDATE script_texts SET content_loc3='CHAOS!' WHERE entry=-1580074; /* content: Chaos! */
UPDATE script_texts SET content_loc3='ZERSTÖRUNG!' WHERE entry=-1580075; /* content: Destruction! */
UPDATE script_texts SET content_loc3='AUSLÖSCHUNG!' WHERE entry=-1580076; /* content: Oblivion! */
UPDATE script_texts SET content_loc3='Nichts wird mich aufhalten! Diese Welt wird fallen!' WHERE entry=-1580077; /* content: I will not be denied! This world shall fall! */
UPDATE script_texts SET content_loc3='Arrrghh! Die Energie des Sonnenbrunnens... richtet sich... gegen mich! Was habt ihr getan? Was habt ihr getan?!!' WHERE entry=-1580079; /* content: Aggghh! The powers of the Sunwell... turn... against me! What have you done? What have you done??? */
UPDATE script_texts SET content_loc3='Ich diene jetzt nur noch dem Meister.' WHERE entry=-1580082; /* content: I serve only the Master now. */
UPDATE script_texts SET content_loc3='Ich bin... verloren. Ich finde nicht mehr zurück.' WHERE entry=-1580084; /* content: But I\'m... lost. I cannot find my way back. */
UPDATE script_texts SET content_loc3='Kalec... Kalec?' WHERE entry=-1580086; /* content: Kalec... Kalec? */
UPDATE script_texts SET content_loc3='Eine weitere Kugel ist von meiner Macht erfüllt! Benutzt sie, schnell!' WHERE entry=-1580092; /* content: I have empowered another orb! Use it quickly! */
UPDATE script_texts SET content_loc3='Eine weitere Kugel ist bereit! Sputet Euch!' WHERE entry=-1580093; /* content: Another orb is ready! Make haste! */
UPDATE script_texts SET content_loc3='Ich habe getan, was ich konnte! Die Macht liegt in Euren Händen!' WHERE entry=-1580094; /* content: I have channeled all I can! The power is in your hands! */
UPDATE script_texts SET content_loc3='Sterbliche - Euer Sieg wurde schon vor langer Zeit vorhergesagt. Der qualvolle Schrei der Niederlage meines Bruders wird durch das Universum schallen und all jenen Hoffnung spenden, die sich der Legion entgegenstellen.' WHERE entry=-1580095; /* content: Mortal heroes - your victory here today was foretold long ago. My brother\'s anguished cry of defeat will echo across the universe - bringing renewed hope to all those who still stand against the Burning Crusade. */
UPDATE script_texts SET content_loc3='Entropius, das Wesen, welches Ihr zerstören musstet, war der Naaru M\'uru, der einst unermessliche Energien des LICHTS und der HOFFNUNG spendete. Es gab eine Zeit, da versuchten Fehlgeleitete, diese Energie zu stehlen...' WHERE entry=-1580098; /* content: The creature Entropius, whom you were forced to destroy, was once the noble naaru, M\'uru. In life, M\'uru channeled vast energies of LIGHT and HOPE. For a time, a misguided few sought to steal those energies... */
UPDATE script_texts SET content_loc3='Unsere Arroganz ist unverzeihlich. Verdammnis brachten wir über eines der edelsten Wesen - eine Schuld, die ewig währt.' WHERE entry=-1580099; /* content: Our arrogance was unpardonable. We damned one of the most noble beings of all. We may never atone for this sin. */
UPDATE script_texts SET content_loc3='Gesegnete Vorfahren! Ich fühle es... so viel Liebe... so viel Gnade... keine Worte... unmöglich zu beschreiben...' WHERE entry=-1580104; /* content: Blessed ancestors! I feel it... so much love... so much grace... there are... no words... impossible to describe... */
UPDATE script_texts SET content_loc3='Erlösung, mein Kind. Sie erwartet uns alle.' WHERE entry=-1580105; /* content: Salvation, young one. It waits for us all. */
UPDATE script_texts SET content_loc3='%s holt tief Luft.' WHERE entry=-1580107; /* content: %s takes a deep breath. */
UPDATE script_texts SET content_loc3='Ihr verschwendet nur meine Zeit!' WHERE entry=-1585000; /* content: You only waste my time! */
UPDATE script_texts SET content_loc3='Mein Hunger kennt keine Grenzen!' WHERE entry=-1585001; /* content: My hunger knows no bounds! */
UPDATE script_texts SET content_loc3='Genug gespielt!' WHERE entry=-1585003; /* content: Enough distractions! */
UPDATE script_texts SET content_loc3='Ich bin unbesiegbar!' WHERE entry=-1585004; /* content: I am invincible! */
UPDATE script_texts SET content_loc3='Un... kon... trollierbar.' WHERE entry=-1585008; /* content: Un...con...tainable. */
UPDATE script_texts SET content_loc3='Ver... zehren.' WHERE entry=-1585010; /* content: Con...sume. */
UPDATE script_texts SET content_loc3='Jetzt werde ich wütend.' WHERE entry=-1585015; /* content: Now I\'m getting annoyed. */
UPDATE script_texts SET content_loc3='Das nenne ich einen guten Anfang.' WHERE entry=-1585017; /* content: I call that a good start. */
UPDATE script_texts SET content_loc3='Ach, da war es nur noch einer.' WHERE entry=-1585020; /* content: One is such a lonely number. */
UPDATE script_texts SET content_loc3='Das war ein wahres Vergnügen.' WHERE entry=-1585021; /* content: It\'s been a kick, really. */
UPDATE script_texts SET content_loc3='Vergeltung brennt!' WHERE entry=-1585024; /* content: Vengeance burns! */
UPDATE script_texts SET content_loc3='Alle Soldaten aus Lordaeron sind aufgefordert, sich sofort am Eingang von Stratholme zu melden und auf weitere Anordnungen von Prinz Arthas zu warten.' WHERE entry=-1595000; /* content: All soldiers of Lordaeron should immediately report to the entrance of Stratholme, and await further orders from Prince Arthas. */
UPDATE script_texts SET content_loc3='Gute Arbeit mit den Kisten! Holt Euch bei mir vor den Toren Stratholmes Euren nächsten Auftrag ab!' WHERE entry=-1595001; /* content: Good work with the crates! Come talk to me in front of Stratholme for your next assignment! */
UPDATE script_texts SET content_loc3='Truppen der Geißel sind in der Nähe des Feststraßentors gesehen worden!' WHERE entry=-1595003; /* content: Scourge forces have been spotted near the Festival Lane Gate! */
UPDATE script_texts SET content_loc3='Truppen der Geißel sind in der Nähe des Brunnens des Königsplatzes gesehen worden!' WHERE entry=-1595004; /* content: Scourge forces have been spotted near the King\'s Square fountain! */
UPDATE script_texts SET content_loc3='Truppen der Geißel sind in der Nähe des Marktgassentors gesehen worden!' WHERE entry=-1595005; /* content: Scourge forces have been spotted near the Market Row Gate! */
UPDATE script_texts SET content_loc3='Truppen der Geißel sind in der Nähe des Rathauses gesehen worden!' WHERE entry=-1595006; /* content: Scourge forces have been spotted near the Town Hall! */
UPDATE script_texts SET content_loc3='Truppen der Geißel sind in der Nähe des Tors des Ältestenplatzes gesehen worden!' WHERE entry=-1595007; /* content: Scourge forces have been spotted near the Elder\'s Square Gate! */
UPDATE script_texts SET content_loc3='Recken, trefft mich sofort am Rathaus. Wir müssen Mal\'Ganis in den Kampf verwickeln.' WHERE entry=-1595008; /* content: Champions, meet me at the Town Hall at once. We must take the fight to Mal\'Ganis. */
UPDATE script_texts SET content_loc3='Folgt mir, ich kenne den Weg.' WHERE entry=-1595009; /* content: Follow me, I know the way through. */
UPDATE script_texts SET content_loc3='Ah, Ihr seid endlich da, Prinz Arthas. Ihr kommt gerade noch rechtzeitig.' WHERE entry=-1595010; /* content: Ah, you\'ve finally arrived Prince Arthas. You\'re here just in the nick of time. */
UPDATE script_texts SET content_loc3='Ja, ich bin froh, dass ich vor der Seuche zu Euch gelangt bin.' WHERE entry=-1595011; /* content: Yes, I\'m glad I could get to you before the plague. */
UPDATE script_texts SET content_loc3='Was ist dies für ein Zauber?' WHERE entry=-1595012; /* content: What is this sorcery? */
UPDATE script_texts SET content_loc3='Es gibt nichts, was Ihr verstehen müsstet, Arthas. Alles, was Ihr tun müsst, ist sterben.' WHERE entry=-1595013; /* content: There\'s no need for you to understand, Arthas. All you need to do is die. */
UPDATE script_texts SET content_loc3='Mal\'Ganis scheint mehr als nur die Geißel in seinem Arsenal zu haben. Wir sollten uns beeilen.' WHERE entry=-1595014; /* content: Mal\'Ganis appears to have more than Scourge in his arsenal. We should make haste. */
UPDATE script_texts SET content_loc3='Mehr üble Zauberei! Seid auf alles gefasst!' WHERE entry=-1595015; /* content: More vile sorcery! Be ready for anything! */
UPDATE script_texts SET content_loc3='Lasst uns weitermachen.' WHERE entry=-1595016; /* content: Let\'s move on. */
UPDATE script_texts SET content_loc3='Achtet auf Eure Deckung, sie haben uns in dieser Halle umstellt.' WHERE entry=-1595017; /* content: Watch your backs: they have us surrounded in this hall. */
UPDATE script_texts SET content_loc3='Mal\'Ganis macht es uns nicht leicht.' WHERE entry=-1595018; /* content: Mal\'Ganis is not making this easy. */
UPDATE script_texts SET content_loc3='Sie sind sehr beharrlich.' WHERE entry=-1595019; /* content: They\'re very persistent. */
UPDATE script_texts SET content_loc3='Was kann er mir noch in den Weg legen?' WHERE entry=-1595020; /* content: What else can he put in my way? */
UPDATE script_texts SET content_loc3='Prinz Arthas Menethil, eine große Finsternis hat sich heute Eurer Seele bemächtigt. Der Tod, den Ihr über andere bringen wolltet, wird Euch heute selbst ereilen.' WHERE entry=-1595021; /* content: Prince Arthas Menethil, on this day, a powerful darkness has taken hold of your soul. The death you are destined to visit upon others will this day be your own. */
UPDATE script_texts SET content_loc3='Für Lordaeron tue ich, was getan werden muss, und weder Eure Worte noch Eure Taten halten mich auf.' WHERE entry=-1595022; /* content: I do what I must for Lordaeron, and neither your words nor your actions will stop me. */
UPDATE script_texts SET content_loc3='Der schnellste Weg zu Mal\'Ganis liegt hinter dem Bücherregal da vorn.' WHERE entry=-1595023; /* content: The quickest path to Mal\'Ganis lies behind that bookshelf ahead. */
UPDATE script_texts SET content_loc3='Es geht ganz schnell.' WHERE entry=-1595024; /* content: This will only take a moment. */
UPDATE script_texts SET content_loc3='Wie gut, dass dieser Geheimgang noch existiert.' WHERE entry=-1595025; /* content: I\'m relieved this secret passage still works. */
UPDATE script_texts SET content_loc3='Lasst uns diese Stelle so schnell wie möglich passieren. Falls uns die Untoten nicht kriegen, dann vielleicht das Feuer.' WHERE entry=-1595026; /* content: Let\'s move through here as quickly as possible. If the undead don\'t kill us, the fires might. */
UPDATE script_texts SET content_loc3='Ruht Euch einen Moment aus und atmet durch, aber wir müssen bald weiter.' WHERE entry=-1595027; /* content: Rest a moment and clear your lungs, but we must move again soon. */
UPDATE script_texts SET content_loc3='Das ist genug, wir müssen weiter. Mal\'Ganis wartet.' WHERE entry=-1595028; /* content: That\'s enough; we must move again. Mal\'Ganis awaits. */
UPDATE script_texts SET content_loc3='Endlich etwas Glück. Die Marktgasse brennt noch nicht. Mal\'Ganis sollte am Kreuzzüglerplatz sein. Sagt mir, wenn Ihr so weit seid.' WHERE entry=-1595029; /* content: At last some good luck. Market Row has not caught fire yet. Mal\'Ganis is supposed to be in Crusaders\' Square, which is just ahead. Tell me when you\'re ready to move forward. */
UPDATE script_texts SET content_loc3='Die Gerechtigkeit wird obsiegen.' WHERE entry=-1595030; /* content: Justice will be done. */
UPDATE script_texts SET content_loc3='Wir beenden das hier und jetzt, Mal\'Ganis. Nur wir beide, sonst niemand.' WHERE entry=-1595031; /* content: We\'re going to finish this right now, Mal\'Ganis. Just you... and me. */
UPDATE script_texts SET content_loc3='Eure Reise hat erst begonnen, junger Prinz. Sammelt Eure Streitmacht und folgt mir in das arktische Land Nordend. Dort werden wir unsere Rechnung begleichen. Dort wird sich Euer wahres Schicksal offenbaren.' WHERE entry=-1595032; /* content: Your journey has just begun, young prince. Gather your forces and meet me in the arctic land of Northrend. It is there that we shall settle the score between us. It is there that your true destiny will unfold. */
UPDATE script_texts SET content_loc3='Wenn es sein muss, jage ich Euch bis ans Ende der Welt! Habt Ihr gehört? BIS ANS ENDE DER WELT!' WHERE entry=-1595033; /* content: I\'ll hunt you to the ends of the earth if I have to! Do you hear me? To the ends of the earth! */
UPDATE script_texts SET content_loc3='Ihr habt Euch gut geschlagen. Alles, was Mal\'Ganis zurückgelassen hat, gehört Euch. Es ist Eure Belohnung. Ich muss jetzt die Expedition nach Nordend planen.' WHERE entry=-1595034; /* content: You performed well this day. Anything that Mal\'Ganis has left behind is yours. Take it as your reward. I must now begin plans for an expedition to Northrend. */
UPDATE script_texts SET content_loc3='Schützt Euren Prinzen, Soldaten von Lordaeron! Ich brauche Unterstützung.' WHERE entry=-1595035; /* content: Protect your prince, soldiers of Lordaeron! I am in need of aid! */
UPDATE script_texts SET content_loc3='Mal\'Ganis wird dafür bezahlen.' WHERE entry=-1595037; /* content: Mal\'Ganis will pay for this. */
UPDATE script_texts SET content_loc3='Ihr dürft nicht davonkommen.' WHERE entry=-1595038; /* content: I can\'t afford to spare you. */
UPDATE script_texts SET content_loc3='Ein Hindernis weniger.' WHERE entry=-1595039; /* content: One less obstacle to deal with. */
UPDATE script_texts SET content_loc3='Fleisch ist Eisen unterlegen!' WHERE entry=-1599002; /* content: Flesh is no match for iron! */
UPDATE script_texts SET content_loc3='Armeen aus Eisen werden die Welt ersticken!' WHERE entry=-1599003; /* content: Armies of iron will smother the world! */
UPDATE script_texts SET content_loc3='Warum musste es soweit kommen?' WHERE entry=-1599006; /* content: Why must it be this way? */
UPDATE script_texts SET content_loc3='Ihr wolltet es nicht anders!' WHERE entry=-1599007; /* content: You had it coming! */
UPDATE script_texts SET content_loc3='So viel vergeudete Zeit... die ihr niemals zurückbekommt!' WHERE entry=-1599010; /* content: So much lost time... that you\'ll never get back! */
UPDATE script_texts SET content_loc3='Hoffentlich ist es das alles wert!' WHERE entry=-1599019; /* content: I hope this is all worth it! */
UPDATE script_texts SET content_loc3='Zeit für ein paar Antworten! Lasst uns loslegen!' WHERE entry=-1599020; /* content: Time to get some answers! Let\'s get this show on the road! */
UPDATE script_texts SET content_loc3='Was in Madorans Namen hat DAS gemacht? Oh, wartet, Ich hab\'s gleich...' WHERE entry=-1599023; /* content: What in the name o\' Madoran did THAT do? Oh! Wait: I just about got it... */
UPDATE script_texts SET content_loc3='Haltet jetzt die Augen offen! Ich werde die Sache hier im Handumdrehen...' WHERE entry=-1599026; /* content: Now keep an eye out! I\'ll have this licked in two shakes of a-- */
UPDATE script_texts SET content_loc3='Oh, das hört sich nicht gut an. Wir haben vielleicht ein Problem oder zwei...' WHERE entry=-1599028; /* content: Oh, that doesn\'t sound good. We might have a complication or two... */
UPDATE script_texts SET content_loc3='Ihr wollt es also auf die harte Tour, was? Die hab ich am liebsten!' WHERE entry=-1599030; /* content: Ah, you want to play hardball, eh? That\'s just my game! */
UPDATE script_texts SET content_loc3='Noch\'n paar Minuten und ich werde...' WHERE entry=-1599031; /* content: Couple more minutes and I\'ll-- */
UPDATE script_texts SET content_loc3='Erhöht? Und was ist die gute Nachricht?' WHERE entry=-1599033; /* content: Heightened? What\'s the good news? */
UPDATE script_texts SET content_loc3='Das war also das Problem! Jetzt komm\' ich voran...' WHERE entry=-1599034; /* content: So that was the problem? Now I\'m makin\' progress... */
UPDATE script_texts SET content_loc3='Kritischer Bedrohungsindex. Leerenanalyse umgeleitet. Initialisiere Säuberungsprotokoll.' WHERE entry=-1599035; /* content: Critical threat index. Void analysis diverted. Initiating sanitization protocol. */
UPDATE script_texts SET content_loc3='Sekunde! Hier wird niemand gesäubert, solange ich ein Wörtchen mitzureden hab\'!' WHERE entry=-1599036; /* content: Hang on! Nobody\'s gonna\' be sanitized as long as I have a say in it! */
UPDATE script_texts SET content_loc3='Welche Beschützer?' WHERE entry=-1599052; /* content: What protectors? */
UPDATE script_texts SET content_loc3='Moment mal! Die Aesir und Vanir zogen in den Krieg? Warum?' WHERE entry=-1599056; /* content: Hold everything! The Aesir and Vanir went to war? Why? */
UPDATE script_texts SET content_loc3='Im Wesentlichen ist das korrekt.' WHERE entry=-1599059; /* content: Essentially that is correct. */
UPDATE script_texts SET content_loc3='Loken?! Das ist doch einfach lästig... Wir haben vielleicht die Eisenzwerge ausgeschaltet, doch ich möchte wetten, dass es irgendwo noch eine weitere Maschine gibt, die am laufenden Band massenweise Eisenvrykul ausspuckt.' WHERE entry=-1599062; /* content: Loken?! That\'s downright bothersome... We might\'ve neutralized the iron dwarves, but I\'d lay odds there\'s another machine somewhere else churnin\' out a whole mess o\' these iron vrykul! */
UPDATE script_texts SET content_loc3='Ich denke, es ist an der Zeit herauszufinden, was sich hinter der Tür in der Nähe des Eingangs versteckt. Ich werde mich hübsch leise rüberschleichen. Wir treffen uns an der Tür und ich werde uns hineinlassen.' WHERE entry=-1599064; /* content: I think it\'s time to see what\'s behind the door near the entrance. I\'m going to sneak over there, nice and quiet. Meet me at the door and I\'ll get us in. */
UPDATE script_texts SET content_loc3='So Hunger! Muss fressen!' WHERE entry=-1600001; /* content: So hungry! Must feed! */
UPDATE script_texts SET content_loc3='Leiche macht Bumm!' WHERE entry=-1600003; /* content: Corpse go boom! */
UPDATE script_texts SET content_loc3='Eure Mühen... sind umsonst.' WHERE entry=-1600006; /* content: Your efforts... are in vain. */
UPDATE script_texts SET content_loc3='Verstärkt meine Verteidigung! Schnell, verdammt!' WHERE entry=-1600008; /* content: Bolster my defenses! Hurry, curse you! */
UPDATE script_texts SET content_loc3='Sicher seht ihr, dass alles vergebens ist!' WHERE entry=-1600009; /* content: Surely you can see the futility of it all! */
UPDATE script_texts SET content_loc3='Gebt endlich auf und sterbt!' WHERE entry=-1600010; /* content: Just give up and die already! */
UPDATE script_texts SET content_loc3='%s ruft nach Verstärkung.' WHERE entry=-1600011; /* content: %s calls for assistance. */
UPDATE script_texts SET content_loc3='Tharon\'ja sieht alles! Sterbliche werden der ewigen Dynastie niemals ein Ende bereiten!' WHERE entry=-1600012; /* content: Tharon\'ja sees all! The work of mortals shall not end the eternal dynasty! */
UPDATE script_texts SET content_loc3='Euer Fleisch dient jetzt Tharon\'ja!' WHERE entry=-1600015; /* content: Your flesh serves Tharon\'ja now! */
UPDATE script_texts SET content_loc3='Tharon\'ja kann Eure sterbliche Hülle gebrauchen!' WHERE entry=-1600016; /* content: Tharon\'ja has a use for your mortal shell! */
UPDATE script_texts SET content_loc3='Nein! Ein Hauch... allzu kurz!' WHERE entry=-1600017; /* content: No! A taste... all too brief! */
UPDATE script_texts SET content_loc3='Es war töricht, herzukommen.' WHERE entry=-1601002; /* content: You were foolish to come. */
UPDATE script_texts SET content_loc3='Wie Anub\'arak befiehlt!' WHERE entry=-1601003; /* content: As Anub\'Arak commands! */
UPDATE script_texts SET content_loc3='Wir müssen das Tor halten. Angriff! Reißt sie in Stücke!' WHERE entry=-1601005; /* content: We must hold the gate. Attack! Tear them limb from limb! */
UPDATE script_texts SET content_loc3='Das Tor muss um jeden Preis geschützt werden. Zerfleischt sie!' WHERE entry=-1601006; /* content: The gate must be protected at all costs. Rip them to shreds! */
UPDATE script_texts SET content_loc3='Ich höre Schritte. Seid wachsam.' WHERE entry=-1601008; /* content: I hear footsteps. Be on your guard. */
UPDATE script_texts SET content_loc3='Ich spüre Lebende. Seid bereit.' WHERE entry=-1601009; /* content: I sense the living. Be ready. */
UPDATE script_texts SET content_loc3='%s kommt durch den Tunnel!' WHERE entry=-1601013; /* content: %s moves up the tunnel! */
UPDATE script_texts SET content_loc3='Ewige Pein erwartet euch!' WHERE entry=-1601015; /* content: Eternal agony awaits you! */
UPDATE script_texts SET content_loc3='Ihr habt eure Wahl getroffen.' WHERE entry=-1601017; /* content: You have made your choice. */
UPDATE script_texts SET content_loc3='Auum na-l ak-k-k-k, isshhh.' WHERE entry=-1601020; /* content: Auum na-l ak-k-k-k, isshhh. */
UPDATE script_texts SET content_loc3='Eure Rüstung hilft nicht gegen meine Heuschrecken!' WHERE entry=-1601021; /* content: Your armor is useless against my locusts! */
UPDATE script_texts SET content_loc3='Uunak-hissss tik-k-k-k-k!' WHERE entry=-1601023; /* content: Uunak-hissss tik-k-k-k-k! */
UPDATE script_texts SET content_loc3='Das Tor wurde durchbrochen! Schnell, ein Teil der Truppen muss sich um diese Eindringlinge kümmern!' WHERE entry=-1601025; /* content: The gate has been breached! Quickly, divert forces to deal with these invaders! */
UPDATE script_texts SET content_loc3='Wir haben keine Zeit mehr! An alle Streitkräfte: Greift die Eindringlinge an!' WHERE entry=-1601026; /* content: There\'s no time left! All remaining forces, attack the invaders! */
UPDATE script_texts SET content_loc3='Ich bin der Stärkste unter den Söhnen meines Vaters! Euer Ende ist gekommen!' WHERE entry=-1602000; /* content: I am the greatest of my father\'s sons! Your end has come! */
UPDATE script_texts SET content_loc3='Das Fleisch... ist... schwach!' WHERE entry=-1602002; /* content: Flesh... is... weak! */
UPDATE script_texts SET content_loc3='...' WHERE entry=-1602003; /* content: ... */
UPDATE script_texts SET content_loc3='Wie kann das sein...? Fleisch ist nicht... stärker!' WHERE entry=-1602004; /* content: How can it be...? Flesh is not... stronger! */
UPDATE script_texts SET content_loc3='Verteidigt euch nur, es ist ohnehin zwecklos!' WHERE entry=-1602005; /* content: Defend yourself, for all the good it will do! */
UPDATE script_texts SET content_loc3='%s wechselt in die Kampfhaltung!' WHERE entry=-1602006; /* content: %s switches to Battle Stance! */
UPDATE script_texts SET content_loc3='GRAAAAH! Hütet euch vor dem Zorn von Eisen und Stahl!' WHERE entry=-1602007; /* content: GRAAAAAH! Behold the fury of iron and steel! */
UPDATE script_texts SET content_loc3='%s wechselt in die Berserkerhaltung!' WHERE entry=-1602008; /* content: %s switches to Berserker Stance! */
UPDATE script_texts SET content_loc3='Zeigt mir, was ihr könnt!' WHERE entry=-1602009; /* content: Give me your worst! */
UPDATE script_texts SET content_loc3='%s wechselt in die Verteidigungshaltung!' WHERE entry=-1602010; /* content: %s switches to Defensive Stance! */
UPDATE script_texts SET content_loc3='Meister... Ihr habt Besuch.' WHERE entry=-1602015; /* content: Master... you have guests. */
UPDATE script_texts SET content_loc3='Der kleinste Funke bedeutet euren Untergang.' WHERE entry=-1602016; /* content: The slightest spark shall be your undoing. */
UPDATE script_texts SET content_loc3='Keiner ist sicher!' WHERE entry=-1602017; /* content: No one is safe! */
UPDATE script_texts SET content_loc3='Welche Hoffnung es für euch gibt? Keine!' WHERE entry=-1602018; /* content: What hope is there for you? None! */
UPDATE script_texts SET content_loc3='Mein Meister hat mir die Zukunft gezeigt und für euch ist dort kein Platz. Azeroth wird in Dunkelheit wiedergeboren. Yogg-Saron wird befreit. Das Pantheon wird fallen!' WHERE entry=-1602020; /* content: My master has shown me the future, and you have no place in it. Azeroth will be reborn in darkness. Yogg-Saron shall be released! The Pantheon shall fall! */
UPDATE script_texts SET content_loc3='Nur sterblich...' WHERE entry=-1602021; /* content: Only mortal... */
UPDATE script_texts SET content_loc3='Ich... währe... IN EWIGKEIT!' WHERE entry=-1602022; /* content: I... am... FOREVER! */
UPDATE script_texts SET content_loc3='Die kurze Zeit, die Ihr hattet... verschwendet!' WHERE entry=-1602023; /* content: What little time you had, you wasted! */
UPDATE script_texts SET content_loc3='Mein Tod... kündet vom Ende dieser Welt.' WHERE entry=-1602024; /* content: My death... heralds the end of this world. */
UPDATE script_texts SET content_loc3='Ihr könnt euch vor dem Schicksal nicht verstecken!' WHERE entry=-1602025; /* content: You cannot hide from fate! */
UPDATE script_texts SET content_loc3='Kommt näher. Ich mach\' es kurz.' WHERE entry=-1602026; /* content: Come closer. I will make it quick. */
UPDATE script_texts SET content_loc3='Euer Fleisch kann nicht lange bestehen.' WHERE entry=-1602027; /* content: Your flesh cannot hold out for long. */
UPDATE script_texts SET content_loc3='Ihr starrt blind in den Abgrund!' WHERE entry=-1602028; /* content: You stare blindly into the abyss! */
UPDATE script_texts SET content_loc3='Eure Ignoranz reicht tief. Seht ihr nicht, wohin das führt?' WHERE entry=-1602029; /* content: Your ignorance is profound. Can you not see where this path leads? */
UPDATE script_texts SET content_loc3='Ihr stürzt euch ins Nichts!' WHERE entry=-1602030; /* content: You cross the precipice of oblivion! */
UPDATE script_texts SET content_loc3='%s beginnt, \'Blitznova\' zu wirken!' WHERE entry=-1602031; /* content: %s begins to cast Lightning Nova! */
UPDATE script_texts SET content_loc3='Ihr habt also meine Kinder getötet? Ihr... werdet... bezahlen!' WHERE entry=-1602032; /* content: It is you who have destroyed my children? You... shall... pay! */
UPDATE script_texts SET content_loc3='Die Armeen aus Eisen werden alles erobern!' WHERE entry=-1602033; /* content: The armies of iron will conquer all! */
UPDATE script_texts SET content_loc3='Ihr habt mich zu viel Arbeit gekostet!' WHERE entry=-1602035; /* content: You have cost me too much work! */
UPDATE script_texts SET content_loc3='Der Meister tat recht daran… besorgt zu sein.' WHERE entry=-1602036; /* content: The master was right... to be concerned. */
UPDATE script_texts SET content_loc3='Ich zerquetsche euch unter meinem Stiefeln!' WHERE entry=-1602037; /* content: I will crush you beneath my boots! */
UPDATE script_texts SET content_loc3='Meine ganze Arbeit... vergebens!' WHERE entry=-1602038; /* content: All my work... undone! */
UPDATE script_texts SET content_loc3='Nichts geht dabei verloren. Ihr werdet sehen...' WHERE entry=-1602040; /* content: Nothing is wasted in the process. You will see.... */
UPDATE script_texts SET content_loc3='%s rennt zu seinem Amboss!' WHERE entry=-1602041; /* content: %s runs to his anvil! */
UPDATE script_texts SET content_loc3='%s macht sich bereit, seine brüchigen Golems zu zerschlagen!' WHERE entry=-1602042; /* content: %s prepares to shatter his Brittle Golems! */
UPDATE script_texts SET content_loc3='Das Konservatorium muss verteidigt werden!' WHERE entry=-1603000; /* content: The Conservatory must be protected! */
UPDATE script_texts SET content_loc3='Eonar, Eure Dienerin braucht Hilfe!' WHERE entry=-1603002; /* content: Eonar, your servant requires aid! */
UPDATE script_texts SET content_loc3='Helft mir, Kinder!' WHERE entry=-1603003; /* content: Children, assist me! */
UPDATE script_texts SET content_loc3='Der Schwarm der Elemente soll über Euch kommen!' WHERE entry=-1603004; /* content: The swarm of the elements shall overtake you! */
UPDATE script_texts SET content_loc3='Vergebt mir.' WHERE entry=-1603005; /* content: Forgive me. */
UPDATE script_texts SET content_loc3='Aus Eurem Tod entspringt neues Leben!' WHERE entry=-1603006; /* content: From your death springs life anew! */
UPDATE script_texts SET content_loc3='Seine Macht über mich beginnt zu schwinden. Endlich kann ich wieder klar sehen. Ich danke Euch, Helden.' WHERE entry=-1603007; /* content: His hold on me dissipates. I can see clearly once more. Thank you, heroes. */
UPDATE script_texts SET content_loc3='Eonar, Eure Dienerin bittet um Euren Segen!' WHERE entry=-1603009; /* content: Eonar, your servant calls for your blessing! */
UPDATE script_texts SET content_loc3='Verbündete der Natur erscheinen!' WHERE entry=-1603010; /* content: Allies of Nature have appeared! */
UPDATE script_texts SET content_loc3='%s verschwindet in der Erde und beginnt zu regenerieren.' WHERE entry=-1603011; /* content: The %s withers into the earth and begins to regenerate. */
UPDATE script_texts SET content_loc3='Wie Ihr wünscht, $n.' WHERE entry=-1603012; /* content: As you wish, $N. */
