UPDATE script_texts SET content_loc3='Dünger.' WHERE entry=-1603015; /* content: Fertilizer. */
UPDATE script_texts SET content_loc3='Matrone, einer ist gefallen!' WHERE entry=-1603017; /* content: Matron, one has fallen! */
