UPDATE script_texts SET content_loc3='Zurück mit Euch, wo Ihr herkommt!' WHERE entry=-1603019; /* content: I return you whence you came! */
UPDATE script_texts SET content_loc3='Fort mit Euch!' WHERE entry=-1603020; /* content: BEGONE! */
UPDATE script_texts SET content_loc3='Freya! Sie sind hinter Euch her!' WHERE entry=-1603021; /* content: Freya! They come for you. */
UPDATE script_texts SET content_loc3='Dieser Ort wird zu Eurem Grab werden.' WHERE entry=-1603022; /* content: This place will serve as your graveyard. */
