UPDATE script_texts SET content_loc3='Ihr anmaßenden Wichte! Euer Blut wird die Waffen härten, mit denen diese Welt erobert wird!' WHERE entry=-1603026; /* content: Insolent whelps! Your blood will temper the weapons used to reclaim this world! */
UPDATE script_texts SET content_loc3='Das Inferno wird Euch verschlingen!' WHERE entry=-1603027; /* content: Let the inferno consume you! */
UPDATE script_texts SET content_loc3='Ich werde Euch Eure Unreinheiten ausbrennen!' WHERE entry=-1603029; /* content: I will burn away your impurities! */
UPDATE script_texts SET content_loc3='JETZT GEHT ES ZU ENDE!' WHERE entry=-1603033; /* content: Let it be finished! */
UPDATE script_texts SET content_loc3='Ich. Habe. Versagt.' WHERE entry=-1603034; /* content: I. Have. Failed. */
UPDATE script_texts SET content_loc3='Gebt uns einen Moment, damit wir uns auf den Bau der Geschütze vorbereiten können.' WHERE entry=-1603037; /* content: Give us a moment to prepare to build the turrets. */
UPDATE script_texts SET content_loc3='Haltet die Augen offen! Bald werden Maulwurfmaschinen mit diesen widerlichen Eisenzwergen auftauchen!' WHERE entry=-1603038; /* content: Be on the lookout! Mole machines will be surfacing soon with those nasty Iron dwarves aboard! */
UPDATE script_texts SET content_loc3='Bereit, rauszugehen, haltet uns diese Zwerge vom Leib!' WHERE entry=-1603039; /* content: Ready to move out, keep those dwarves off of our backs! */
UPDATE script_texts SET content_loc3='Beeilt Euch! Sie wird nicht lange am Boden bleiben!' WHERE entry=-1603040; /* content: Move quickly! She won\'t remain grounded for long! */
UPDATE script_texts SET content_loc3='Feuer einstellen! Lasst uns diese Geschütze reparieren!' WHERE entry=-1603042; /* content: Fires out! Let\'s rebuild those turrets! */
UPDATE script_texts SET content_loc3='Harpunengeschütz ist einsatzbereit!' WHERE entry=-1603043; /* content: Harpoon Turret is ready for use! */
UPDATE script_texts SET content_loc3='Neues Spielzeug? Für mich? Oh, ich verspreche, sie dieses Mal nicht zu zerbrechen!' WHERE entry=-1603045; /* content: New toys? For me? I promise I won\'t break them this time! */
UPDATE script_texts SET content_loc3='Ich... glaube, ich hab\' es kaputt gemacht.' WHERE entry=-1603046; /* content: I... I think I broke it. */
UPDATE script_texts SET content_loc3='Hm, so rum soll man es wohl nicht knicken.' WHERE entry=-1603047; /* content: I guess it doesn\'t bend that way. */
UPDATE script_texts SET content_loc3='Diese Spielzeuge langweilen mich. Ich will jetzt nicht mehr spielen!' WHERE entry=-1603048; /* content: I\'m tired of these toys. I don\'t want to play anymore! */
UPDATE script_texts SET content_loc3='So müde... Ich will mich nur einen Moment ausruhen.' WHERE entry=-1603051; /* content: So tired. I will rest for just a moment! */
UPDATE script_texts SET content_loc3='Wir können weiterspielen!' WHERE entry=-1603052; /* content: I\'m ready to play! */
UPDATE script_texts SET content_loc3='NEIN! NEIN! NEIN! NEIN! NEIN! NEIN! NEIN!' WHERE entry=-1603053; /* content: NO! NO! NO! NO! NO! */
