UPDATE script_texts SET content_loc3='Steht still und starrt in das Licht!' WHERE entry=-1603057; /* content: Stand still and stare into the light! */
UPDATE script_texts SET content_loc3='Ihr lauft geradewegs in den Schlund des Wahnsinns!' WHERE entry=-1603059; /* content: You rush headlong into the maw of madness! */
UPDATE script_texts SET content_loc3='Ein Gnadenstoß!' WHERE entry=-1603060; /* content: A merciful kill! */
UPDATE script_texts SET content_loc3='HAH!' WHERE entry=-1603061; /* content: HAH! */
UPDATE script_texts SET content_loc3='Diese Sitzung der Versammlung des Eisens wird vertagt!' WHERE entry=-1603062; /* content: This meeting of the Assembly of Iron is adjourned! */
UPDATE script_texts SET content_loc3='Lasst Sturmwolken aufziehen und den Tod herabregnen!' WHERE entry=-1603063; /* content: Let the storm clouds rise and rain down death from above! */
UPDATE script_texts SET content_loc3='Entziffert das hier!' WHERE entry=-1603067; /* content: Decipher this! */
UPDATE script_texts SET content_loc3='Der Preis für Eure Arroganz ist der Tod!' WHERE entry=-1603070; /* content: Death is the price of your arrogance. */
UPDATE script_texts SET content_loc3='Diese Sitzung der Versammlung des Eisens wird vertagt!' WHERE entry=-1603071; /* content: This meeting of the Assembly of Iron is adjourned! */
UPDATE script_texts SET content_loc3='So leicht werdet Ihr die Versammlung des Eisens nicht bezwingen, Eindringlinge!' WHERE entry=-1603072; /* content: You will not defeat the Assembly of Iron so easily, invaders! */
UPDATE script_texts SET content_loc3='Mein Tod wird Euren Untergang nur beschleunigen.' WHERE entry=-1603073; /* content: My death only serves to hasten your demise. */
UPDATE script_texts SET content_loc3='Unmöglich!' WHERE entry=-1603074; /* content: Impossible! */
UPDATE script_texts SET content_loc3='So schwach und zerbrechlich!' WHERE entry=-1603075; /* content: So fragile and weak! */
UPDATE script_texts SET content_loc3='Fleisch... welch unnützes Hindernis.' WHERE entry=-1603076; /* content: Flesh... such a hindrance. */
UPDATE script_texts SET content_loc3='Ihr sucht die Geheimnisse Ulduars? Dann nehmt sie Euch!' WHERE entry=-1603077; /* content: You seek the secrets of Ulduar? Then take them! */
UPDATE script_texts SET content_loc3='Diese Sitzung der Versammlung des Eisens wird vertagt!' WHERE entry=-1603078; /* content: This meeting of the Assembly of Iron is adjourned! */
UPDATE script_texts SET content_loc3='In manche Dinge mischt man sich besser nicht ein!' WHERE entry=-1603079; /* content: Some things are better left alone! */
UPDATE script_texts SET content_loc3='Es gibt kein Entkommen!' WHERE entry=-1603080; /* content: There is no escape! */
UPDATE script_texts SET content_loc3='Ihr verschwendet meine Zeit!' WHERE entry=-1603082; /* content: You waste my time! */
UPDATE script_texts SET content_loc3='Für Euer Eindringen werdet Ihr bezahlen!' WHERE entry=-1603086; /* content: You will suffer for this trespass! */
UPDATE script_texts SET content_loc3='Tragisch. So weit zu kommen, nur um zu scheitern.' WHERE entry=-1603087; /* content: Tragic. To come so far, only to fail. */
UPDATE script_texts SET content_loc3='Willkommen im endlosen Winter!' WHERE entry=-1603088; /* content: Welcome to the endless winter. */
UPDATE script_texts SET content_loc3='Winde des Nordens, verschlingt sie!' WHERE entry=-1603089; /* content: Winds of the north consume you! */
UPDATE script_texts SET content_loc3='Ich... bin von ihm befreit... endlich.' WHERE entry=-1603091; /* content: I... I am released from his grasp... at last. */
UPDATE script_texts SET content_loc3='GENUG! Dies endet jetzt!' WHERE entry=-1603092; /* content: Enough! This ends now! */
UPDATE script_texts SET content_loc3='Der Schleier des Winter wird Euch beschützen, Helden!' WHERE entry=-1603093; /* content: The veil of winter will protect you, champions! */
UPDATE script_texts SET content_loc3='Eure Vernichtung wird ein neues Zeitalter des Leids einläuten!' WHERE entry=-1603096; /* content: Your destruction will herald a new age of suffering! */
UPDATE script_texts SET content_loc3='Aufsässigkeit... eine Schwäche der Sterblichen.' WHERE entry=-1603098; /* content: Defiance... a flaw of mortality. */
UPDATE script_texts SET content_loc3='Mich durchströmt das schwarze Blut von Yogg-Saron! ICH - BIN - NICHT - AUFZUHALTEN!' WHERE entry=-1603099; /* content: The black blood of Yogg-Saron courses through me! I. AM. UNSTOPPABLE! */
UPDATE script_texts SET content_loc3='Oh, welche Gräuel warten...' WHERE entry=-1603100; /* content: Oh, what horrors await.... */
UPDATE script_texts SET content_loc3='Eine Wolke Saronitdämpfe bildet sich in der Nähe!' WHERE entry=-1603103; /* content: A cloud of saronite vapors coalesces nearby! */
UPDATE script_texts SET content_loc3='Unter fürchterlichem Gebrüll schlägt General Vezax mit dunkler Macht zu!' WHERE entry=-1603104; /* content: General Vezax roars and surges with dark might! */
UPDATE script_texts SET content_loc3='Die Saronitdämpfe sammeln sich, wirbeln heftig herum und verschmelzen zu einer monströsen Form!' WHERE entry=-1603105; /* content: The saronite vapors mass and swirl violently, merging into a monstrous form! */
UPDATE script_texts SET content_loc3='Translokation abgeschlossen. Fahre mit planetarer Analyse von Azeroth fort.' WHERE entry=-1603106; /* content: Translocation complete. Commencing planetary analysis of Azeroth. */
UPDATE script_texts SET content_loc3='Zurück, Sterbliche. Ich bin nicht hier, um mit Euch zu kämpfen.' WHERE entry=-1603107; /* content: Stand back, mortals. I\'m not here to fight you. */
UPDATE script_texts SET content_loc3='Es dient dem Wohle des Universums, diesen Planeten neu zu erschaffen, sollte meine Analyse eine systemische Korrumpierung ergeben. Mischt Euch nicht ein.' WHERE entry=-1603108; /* content: It is in the universe\'s best interest to re-originate this planet should my analysis find systemic corruption. Do not interfere. */
UPDATE script_texts SET content_loc3='Ich tue, was ich muss.' WHERE entry=-1603112; /* content: I do what I must. */
UPDATE script_texts SET content_loc3='Erblicket die Instrumente der Schöpfung!' WHERE entry=-1603115; /* content: Behold the tools of creation! */
UPDATE script_texts SET content_loc3='HÜTET EUCH!' WHERE entry=-1603116; /* content: Beware! */
UPDATE script_texts SET content_loc3='Lebt wohl, Sterbliche. Eure Tapferkeit ist bewundernswert, für solch fehlerhafte Kreaturen.' WHERE entry=-1603120; /* content: Farewell, mortals. Your bravery is admirable, for such flawed creatures. */
UPDATE script_texts SET content_loc3='Ich sah Welten umhüllt von den Flammen der Schöpfer, sah ohne einen Hauch von Trauer ihre Bewohner vergehen. Ganze Planetensysteme geboren und vernichtet, während Eure sterblichen Herzen nur einmal schlagen. Doch immer war mein Herz kalt... ohne Mitgefühl. Ich - habe - nichts - gefühlt. Millionen, Milliarden Leben verschwendet. Trugen sie alle dieselbe Beharrlichkeit in sich, wie Ihr? Liebten sie alle das Leben so sehr, wie Ihr es tut?' WHERE entry=-1603121; /* content: I have seen worlds bathed in the Makers\' flames, their denizens fading without as much as a whimper. Entire planetary systems born and razed in the time that it takes your mortal hearts to beat once. Yet all throughout, my own heart devoid of emotion... of empathy. I. Have. Felt. Nothing. A million-million lives wasted. Had they all held within them your tenacity? Had they all loved life as you do? */
UPDATE script_texts SET content_loc3='Vielleicht ist es Eure Unvollkommenheit - die Quelle Eures freien Willens -, die Euch entgegen allen berechneten kosmischen Erwartungen durchhalten lässt. Ihr obsiegt dort, wo die perfekten Schöpfungen der Titanen versagt haben.' WHERE entry=-1603122; /* content: Perhaps it is your imperfections... that which grants you free will... that allows you to persevere against all cosmically calculated odds. You prevail where the Titan\'s own perfect creations have failed. */
UPDATE script_texts SET content_loc3='Ich habe den Antwortcode neu definiert: Euer Planet wird verschont werden. Ich kann mir meiner eigenen Berechnungen nicht mehr sicher sein.' WHERE entry=-1603123; /* content: I\'ve rearranged the reply code - your planet will be spared. I cannot be certain of my own calculations anymore. */
UPDATE script_texts SET content_loc3='Mir fehlt die Stärke zur Signalübertragung. Ihr müsst Euch... beeilen... findet eine Energiequelle... nah am Himmelszelt.' WHERE entry=-1603124; /* content: I lack the strength to transmit the signal. You must... hurry... find a place of power... close to the skies. */
UPDATE script_texts SET content_loc3='Macht Euch um mein Schicksal keine Sorgen, Brann Bronze. Wird das Signal nicht rechtzeitig übertragen, wird die Neuerschaffung fortgesetzt. Rettet... Eure Welt…' WHERE entry=-1603125; /* content: Do not worry about my fate, Bronzen. If the signal is not transmitted in time, re-origination will proceed regardless. Save... your world... */
UPDATE script_texts SET content_loc3='Keiner darf passieren!' WHERE entry=-1603126; /* content: None shall pass! */
UPDATE script_texts SET content_loc3='AUSLÖSCHUNG!' WHERE entry=-1603127; /* content: Oblivion! */
UPDATE script_texts SET content_loc3='Ich werde das Leben aus Euch herausquetschen!' WHERE entry=-1603128; /* content: I will squeeze the life from you! */
