UPDATE script_texts SET content_loc3='Der rechte Arm ist nachgewachsen!' WHERE entry=-1603135; /* content: The Right Arm has regrown! */
UPDATE script_texts SET content_loc3='Der linke Arm ist nachgewachsen!' WHERE entry=-1603136; /* content: The Left Arm has regrown! */
UPDATE script_texts SET content_loc3='Kologarn wirkt \'Steinerner Griff\'!' WHERE entry=-1603137; /* content: Kologarn casts Stone Grip! */
UPDATE script_texts SET content_loc3='Zögert nicht! Vernichtet sie!' WHERE entry=-1603141; /* content: Do not hold back! Destroy them! */
UPDATE script_texts SET content_loc3='Meine Geduld ist am Ende!' WHERE entry=-1603146; /* content: My patience has reached its limit! */
UPDATE script_texts SET content_loc3='Senkt Eure Waffen! Ich ergebe mich!' WHERE entry=-1603148; /* content: Stay your arms! I yield! */
UPDATE script_texts SET content_loc3='Ich fühle mich, als würde ich aus einem Alptraum erwachen, doch seine Schatten würden mich noch umschlingen.' WHERE entry=-1603149; /* content: I feel as though I am awakening from a nightmare, but the shadows in this place yet linger. */
UPDATE script_texts SET content_loc3='Ich brauche Zeit zum Nachdenken... Ich werde Eure Sache unterstützen, solltet Ihr dies wünschen. Das ist das Mindeste, was ich Euch schulde. Lebt wohl.' WHERE entry=-1603151; /* content: I need time to reflect.... I will aid your cause if you should require it. I owe you at least that much. Farewell. */
UPDATE script_texts SET content_loc3='Totales Systemversagen. Verteidigungsprotokoll verletzt. Leviathan schaltet ab.' WHERE entry=-1603161; /* content: Total systems failure. Defense protocols breached. Leviathan Unit shutting down. */
