UPDATE script_texts SET content_loc3='Unbefugter Versuch der Schaltkreisüberlastung. Anti-Personen-Maßnahmen aktiviert.' WHERE entry=-1603165; /* content: Unauthorized entity attempting circuit overload. Activating anti-personnel countermeasures. */
