UPDATE script_texts SET content_loc3='SANITÄTER!' WHERE entry=-1603180; /* content: MEDIC! */
UPDATE script_texts SET content_loc3='WUNDERBAR! Das sind Ergebnisse nach meinem Geschmack! Integrität der Hülle bei 98,9 Prozent! So gut wie keine Dellen! Und weiter geht\'s.' WHERE entry=-1603182; /* content: WONDERFUL! Positively marvelous results! Hull integrity at 98.9 percent! Barely a dent! Moving right along. */
UPDATE script_texts SET content_loc3='Ich präsentiere die VX-001 Anti-Personen-Angriffskanone! Ihr solltet vielleicht in Deckung gehen.' WHERE entry=-1603183; /* content: Behold the VX-001 Anti-personnel Assault Cannon! You might want to take cover. */
UPDATE script_texts SET content_loc3='Notiz an mich selbst: Kanone äußerst effizient gegen Zellgewebe.' WHERE entry=-1603185; /* content: Note to self: Cannon highly effective against flesh. */
UPDATE script_texts SET content_loc3='Ist sie nicht atemberaubend? Ich nenne sie die überragende Luftkommandoeinheit!' WHERE entry=-1603187; /* content: Isn\'t it beautiful? I call it the magnificent aerial command unit! */
