UPDATE script_texts SET content_loc3='Davon werdet Ihr Euch nicht mehr erholen, Freund.' WHERE entry=-1603193; /* content: You\'re not going to get up from that one, friend. */
UPDATE script_texts SET content_loc3='Es scheint, als wäre mir eine klitzekleine Fehlkalkulation unterlaufen. Ich habe zugelassen, dass das Scheusal im Gefängnis meine Primärdirektive überschreibt. Alle Systeme nun funktionstüchtig.' WHERE entry=-1603194; /* content: It would appear that I\'ve made a slight miscalculation. I allowed my mind to be corrupted by the fiend in the prison, overriding my primary directive. All systems seem to be functional now. Clear. */
UPDATE script_texts SET content_loc3='Kampfmatrix verbessert. Werdet Zeugen außergewöhnlicher Geschwindigkeit!' WHERE entry=-1603195; /* content: Combat matrix enhanced. Behold wonderous rapidity! */
UPDATE script_texts SET content_loc3='%s beginnt, \'Plasmaeruption\' zu wirken!' WHERE entry=-1603196; /* content: %s begins to cast Plasma Blast! */
UPDATE script_texts SET content_loc3='Was wollt ihr von mir? Lasst mich in Ruhe!' WHERE entry=-1603198; /* content: What do you want from me? Leave me alone! */
UPDATE script_texts SET content_loc3='Bald ist die Zeit gekommen, dem Untier den Kopf abzuschlagen! Konzentriert Euren Zorn und Euren Hass auf seine Diener!' WHERE entry=-1603199; /* content: The time to strike at the head of the beast will soon be upon us! Focus your anger and hatred on his minions! */
UPDATE script_texts SET content_loc3='Ja! JA! Gewährt ihnen keine Gnade! Lasst sie nicht zu Atem kommen!' WHERE entry=-1603201; /* content: Yes! YES! Show them no mercy! Give no pause to your attacks! */
UPDATE script_texts SET content_loc3='Lasst Eure Angriffe vom Hass und vom Zorn leiten!' WHERE entry=-1603202; /* content: Let hatred and rage guide your blows! */
UPDATE script_texts SET content_loc3='Hätten sie gerettet werden können?' WHERE entry=-1603203; /* content: Could they have been saved? */
UPDATE script_texts SET content_loc3='Ich bin der strahlende Traum.' WHERE entry=-1603206; /* content: I am the lucid dream. */
UPDATE script_texts SET content_loc3='Erzittert, Sterbliche, denn das sichere Ende ist nah!' WHERE entry=-1603207; /* content: Tremble, mortals, before the coming of the end! */
UPDATE script_texts SET content_loc3='Erstickt an Eurem eigenen Hass!' WHERE entry=-1603208; /* content: Suffocate upon your own hate! */
