UPDATE script_texts SET content_loc3='Der Hochinquisitor kommt! Macht Euch bereit, Todesritter! Lasst ihn Euch nicht aus der Schutzzone meines antimagischen Felds ziehen! Tötet ihn und nehmt seinen Kopf!' WHERE entry=-1609084; /* content: The High Inquisitor comes! Be ready, death knight! Do not let him draw you out of the protective bounds of my anti-magic field! Kill him and take his head! */
UPDATE script_texts SET content_loc3='Bleibt innerhalb des antimagischen Felds! Lasst sie zu Euch kommen!' WHERE entry=-1609085; /* content: Stay in the anti-magic field! Make them come to you! */
UPDATE script_texts SET content_loc3='Der Tod des Hochinquisitors von Neu-Avalon wird nicht unbemerkt bleiben. Ihr müsst hier sofort verschwinden! Geht, bevor noch mehr von ihnen auftauchen. Ich komme schon alleine klar.' WHERE entry=-1609086; /* content: The death of the High Inquisitor of New Avalon will not go unnoticed. You need to get out of here at once! Go, before more of them show up. I\'ll be fine on my own. */
UPDATE script_texts SET content_loc3='Ich werde ihr Feuer auf mich ziehen, auf dass Ihr hinter mir entkommen könnt.' WHERE entry=-1609087; /* content: I\'ll draw their fire, you make your escape behind me. */
UPDATE script_texts SET content_loc3='Euer Hochinquisitor ist nicht mehr als ein Fleischstapel, Kreuzzügler! Niemand kann sich dem Griff der Geißel entziehen!' WHERE entry=-1609088; /* content: Your High Inquisitor is nothing more than a pile of meat, Crusaders! There are none beyond the grasp of the Scourge! */
UPDATE script_texts SET content_loc3='Mama?' WHERE entry=-1609091; /* content: Mommy? */
UPDATE script_texts SET content_loc3='GEBT MIR GEHIRNE!' WHERE entry=-1609092; /* content: GIVE ME BRAINS! */
UPDATE script_texts SET content_loc3='Muss essen...' WHERE entry=-1609093; /* content: Must feed... */
UPDATE script_texts SET content_loc3='So hungrig...' WHERE entry=-1609094; /* content: So hungry... */
UPDATE script_texts SET content_loc3='Es wirft den Ghul in die Grube oder sonst gibt es die Peitsche!' WHERE entry=-1609096; /* content: It puts the ghoul in the pit or else it gets the lash! */
UPDATE script_texts SET content_loc3='%s bäumt sich auf und bedeutet Euch, aufzusteigen.' WHERE entry=-1609097; /* content: %s rears up, beckoning you to ride it. */
UPDATE script_texts SET content_loc3='Beeindruckend, Todesritter. Kehrt zu mir in die Welt der Lebenden zurück, um Eure Belohnung zu erhalten.' WHERE entry=-1609098; /* content: Impressive, death knight. Return to me in the world of the living for your reward. */
UPDATE script_texts SET content_loc3='Soldaten der Geißel, macht Euch bereit! Macht Euch bereit, Euren Zorn an der Argentumdämmerung zu kühlen!' WHERE entry=-1609201; /* content: Soldiers of the Scourge, stand ready! Prepare to unleash your fury upon the Argent Dawn! */
UPDATE script_texts SET content_loc3='Todesritter von Acherus, der Todesmarsch beginnt!' WHERE entry=-1609203; /* content: Death knights of Acherus, the death march begins! */
UPDATE script_texts SET content_loc3='Soldaten der Geißel, Todesritter von Acherus, Diener der Dunkelheit: vernehmt den Ruf des Hochlords!' WHERE entry=-1609204; /* content: Soldiers of the Scourge, death knights of Acherus, minions of the darkness: hear the call of the Highlord! */
UPDATE script_texts SET content_loc3='Hoch mit Euch!' WHERE entry=-1609205; /* content: RISE! */
UPDATE script_texts SET content_loc3='Der Himmel färbt sich rot vom Blut der Gefallenen! Der Lichkönig wacht über uns, Diener! Hinterlasst nur Asche und Verderben auf Eurem zerstörerischen Weg!' WHERE entry=-1609206; /* content: The skies turn red with the blood of the fallen! The Lich King watches over us, minions! Leave only ashes and misery in your destructive wake! */
UPDATE script_texts SET content_loc3='Die Armeen der Geißel kommen!' WHERE entry=-1609207; /* content: Scourge armies approach! */
UPDATE script_texts SET content_loc3='Gebt nicht auf, Brüder und Schwestern! Das Licht wird siegen!' WHERE entry=-1609208; /* content: Stand fast, brothers and sisters! The Light will prevail! */
UPDATE script_texts SET content_loc3='Kniet vor dem Hochlord!' WHERE entry=-1609209; /* content: Kneel before the Highlord! */
UPDATE script_texts SET content_loc3='Ihr habt keine Chance!' WHERE entry=-1609210; /* content: You stand no chance! */
UPDATE script_texts SET content_loc3='Die Geißel wird diesen Ort zerstören!' WHERE entry=-1609211; /* content: The Scourge will destroy this place! */
UPDATE script_texts SET content_loc3='Euer Leben ist verwirkt.' WHERE entry=-1609212; /* content: Your life is forfeit. */
UPDATE script_texts SET content_loc3='Wie lange werden Eure Streitkräfte wohl noch aushalten?' WHERE entry=-1609214; /* content: How much longer will your forces hold out? */
UPDATE script_texts SET content_loc3='Lasst keinen am Leben!' WHERE entry=-1609216; /* content: Spare no one! */
UPDATE script_texts SET content_loc3='Was ist das?! Mein... Ich kann nicht zuschlagen...' WHERE entry=-1609217; /* content: What is this?! My... I cannot strike... */
UPDATE script_texts SET content_loc3='GEHORCHE MIR, KLINGE!' WHERE entry=-1609218; /* content: Obey me, blade! */
UPDATE script_texts SET content_loc3='Du wirst tun, was ich dir befehle! Ich habe die Kontrolle!' WHERE entry=-1609219; /* content: You will do as I command! I am in control here! */
UPDATE script_texts SET content_loc3='Was geschieht mit mir?' WHERE entry=-1609221; /* content: What is happening to me? */
UPDATE script_texts SET content_loc3='Aschenbringer widersetzt sich mir...' WHERE entry=-1609223; /* content: Ashbringer defies me... */
UPDATE script_texts SET content_loc3='Diener, helft mir!' WHERE entry=-1609224; /* content: Minions, come to my aid! */
UPDATE script_texts SET content_loc3='Ihr könnt nicht gewinnen, Darion!' WHERE entry=-1609225; /* content: You cannot win, Darion! */
UPDATE script_texts SET content_loc3='Bringt sie zur Kapelle!' WHERE entry=-1609226; /* content: Bring them before the chapel! */
UPDATE script_texts SET content_loc3='Was Ihr jetzt fühlt, sind die Qualen von tausend verlorenen Seelen! Seelen, die Ihr und Euer Meister hierherbrachten! Das Licht wird Euch zerreißen, Darion!' WHERE entry=-1609230; /* content: What you are feeling right now is the anguish of a thousand lost souls! Souls that you and your master brought here! The Light will tear you apart, Darion! */
UPDATE script_texts SET content_loc3='Spart Euch die Worte, alter Mann. Es könnten Eure letzten sein.' WHERE entry=-1609231; /* content: Save your breath, old man. It might be the last you ever draw. */
UPDATE script_texts SET content_loc3='Mein Sohn! Mein lieber, werter Junge!' WHERE entry=-1609232; /* content: My son! My dear, beautiful boy! */
UPDATE script_texts SET content_loc3='Vater!' WHERE entry=-1609233; /* content: Father! */
UPDATE script_texts SET content_loc3='Vater, Ihr seid zurückgekehrt!' WHERE entry=-1609235; /* content: Father, you have returned! */
UPDATE script_texts SET content_loc3='Ihr wart lange fort, Vater. Ich habe gedacht...' WHERE entry=-1609236; /* content: You have been gone a long time, father. I thought... */
UPDATE script_texts SET content_loc3='Nichts hätte mich davon abhalten können herzukommen, Darion. Zu meinem Heim und meiner Familie.' WHERE entry=-1609237; /* content: Nothing could have kept me away from here, Darion. Not from my home and family. */
UPDATE script_texts SET content_loc3='Vater, ich möchte Euch im Kampf gegen die Untoten beistehen. Ich will kämpfen! Ich kann nicht länger untätig herumsitzen!' WHERE entry=-1609238; /* content: Father, I wish to join you in the war against the undead. I want to fight! I can sit idle no longer! */
UPDATE script_texts SET content_loc3='Denke stets daran...' WHERE entry=-1609242; /* content: Do not forget... */
UPDATE script_texts SET content_loc3='Rührend...' WHERE entry=-1609243; /* content: Touching... */
UPDATE script_texts SET content_loc3='Erbärmlich...' WHERE entry=-1609246; /* content: Pathetic... */
UPDATE script_texts SET content_loc3='Ihr seid ein verdammtes Monster, Arthas!' WHERE entry=-1609247; /* content: You\'re a damned monster, Arthas! */
UPDATE script_texts SET content_loc3='Ihr hattet Recht, Fordring. Ich habe sie in den Tod geschickt. Ihre Leben waren wertlos. Eures hingegen...' WHERE entry=-1609248; /* content: You were right, Fordring. I did send them in to die. Their lives are meaningless, but yours... */
UPDATE script_texts SET content_loc3='ANGRIFF!' WHERE entry=-1609250; /* content: ATTACK!!! */
UPDATE script_texts SET content_loc3='APOKALYPSE!' WHERE entry=-1609251; /* content: APOCALYPSE! */
UPDATE script_texts SET content_loc3='Dieser Tag ist nicht heute...' WHERE entry=-1609252; /* content: That day is not today... */
UPDATE script_texts SET content_loc3='Tirion!' WHERE entry=-1609253; /* content: Tirion! */
UPDATE script_texts SET content_loc3='ARTHAS!!!!' WHERE entry=-1609254; /* content: ARTHAS!!!! */
UPDATE script_texts SET content_loc3='Was ist das?' WHERE entry=-1609255; /* content: What is this? */
UPDATE script_texts SET content_loc3='Euer Ende.' WHERE entry=-1609256; /* content: Your end. */
UPDATE script_texts SET content_loc3='Unmöglich...' WHERE entry=-1609257; /* content: Impossible... */
UPDATE script_texts SET content_loc3='Ich gebe Euch jetzt ein Versprechen, Brüder und Schwestern: Der Lichkönig wird fallen! Heute fordere ich die Gründung eines Bunds.' WHERE entry=-1609264; /* content: I make a promise to you now, brothers and sisters: The Lich King will be defeated! On this day, I call for a union. */
UPDATE script_texts SET content_loc3='%s kniet geschlagen vor Tirion Fordring.' WHERE entry=-1609273; /* content: %s kneels in defeat before Tirion Fordring. */
UPDATE script_texts SET content_loc3='%s umarmt seinen Vater.' WHERE entry=-1609276; /* content: %s hugs his father. */
UPDATE script_texts SET content_loc3='%s ringt nach Luft.' WHERE entry=-1609280; /* content: %s gasps for air. */
UPDATE script_texts SET content_loc3='%s bricht zusammen.' WHERE entry=-1609283; /* content: %s collapses. */
UPDATE script_texts SET content_loc3='Ich fürchte nichts und niemanden! Am allerwenigsten euch!' WHERE entry=-1615000; /* content: I fear nothing! Least of all you! */
UPDATE script_texts SET content_loc3='Auf die Knie!' WHERE entry=-1615007; /* content: On your knees! */
UPDATE script_texts SET content_loc3='Ein Shadronjünger erscheint im Zwielicht!' WHERE entry=-1615008; /* content: A Shadron Disciple appears in the Twilight! */
UPDATE script_texts SET content_loc3='Keine Herausforderung.' WHERE entry=-1615010; /* content: No contest. */
UPDATE script_texts SET content_loc3='Ich hätte mich nicht... zurückhalten sollen...' WHERE entry=-1615012; /* content: I should not... have held back... */
UPDATE script_texts SET content_loc3='Meine Aufgabe ist es, über diese Eier zu wachen. Kommt ihnen zu nahe und von euch bleibt nur ein Häuflein Asche.' WHERE entry=-1615018; /* content: It is my charge to watch over these eggs. I will see you burn before any harm comes to them! */
UPDATE script_texts SET content_loc3='Dieser lächerliche Angriff endet JETZT!' WHERE entry=-1615019; /* content: This pathetic siege ends NOW! */
UPDATE script_texts SET content_loc3='Brennt, ihr armseligen Wichte!' WHERE entry=-1615020; /* content: Burn, you miserable wretches! */
UPDATE script_texts SET content_loc3='Tenebron! Auch Ihr sollt die Eier schützen!' WHERE entry=-1615022; /* content: Tenebron! The eggs are yours to protect as well! */
UPDATE script_texts SET content_loc3='Wie leicht sie brennen, diese kleinen Maden...' WHERE entry=-1615025; /* content: Such flammable little insects.... */
UPDATE script_texts SET content_loc3='Nurmehr eure verkohlten Knochen werden den Boden bedecken!' WHERE entry=-1615026; /* content: Your charred bones will litter the floor! */
UPDATE script_texts SET content_loc3='Wie viel Hitze könnt ihr vertragen?' WHERE entry=-1615027; /* content: How much heat can you take? */
UPDATE script_texts SET content_loc3='Nur noch Asche wird übrigbleiben!' WHERE entry=-1615028; /* content: All will be reduced to ash! */
UPDATE script_texts SET content_loc3='Ihr werdet ein köstliches Mahl für die Brut abgeben.' WHERE entry=-1615029; /* content: You will make a fine meal for the hatchlings. */
UPDATE script_texts SET content_loc3='Die Lava um %s brodelt!' WHERE entry=-1615032; /* content: The lava surrounding %s churns! */
UPDATE script_texts SET content_loc3='War das schon alles, was Ihr könnt?' WHERE entry=-1615035; /* content: Was that the best you can do? */
UPDATE script_texts SET content_loc3='Ich werde Eure Knochen als Zahnstocher benutzen!' WHERE entry=-1615037; /* content: I will pick my teeth with your bones! */
UPDATE script_texts SET content_loc3='Ein Vesperonjünger erscheint im Zwielicht!' WHERE entry=-1615041; /* content: A Vesperon Disciple appears in the Twilight! */
UPDATE script_texts SET content_loc3='%s beginnt, ein Zwielichtportal zu öffnen!' WHERE entry=-1615042; /* content: %s begins to open a Twilight Portal! */
UPDATE script_texts SET content_loc3='Niedere Wesen wagen es, hier einzudringen?! Eine Schande, dass euer großer Mut nicht eure Dummheit kompensiert!' WHERE entry=-1616000; /* content: Lesser beings, intruding here! A shame that your excess courage does not compensate for your stupidity! */
UPDATE script_texts SET content_loc3='Ich bin unaufhaltbar!' WHERE entry=-1616007; /* content: I AM UNSTOPPABLE! */
UPDATE script_texts SET content_loc3='Wenige haben den Schmerz gespürt, den ich euch jetzt zufügen werde!' WHERE entry=-1616012; /* content: Few have experienced the pain I will now inflict upon you! */
UPDATE script_texts SET content_loc3='Solange ich atme, werdet ihr nicht obsiegen!' WHERE entry=-1616013; /* content: You will not succeed while I draw breath! */
UPDATE script_texts SET content_loc3='Ich bin der Spruchwirker! Meine Macht ist unendlich!' WHERE entry=-1616016; /* content: I am the spell-weaver! My power is infinite! */
UPDATE script_texts SET content_loc3='Euer Geist wird für immer hier verweilen!' WHERE entry=-1616017; /* content: Your spirit will linger here forever! */
UPDATE script_texts SET content_loc3='Unterwerft Euch!' WHERE entry=-1616020; /* content: SUBMIT! */
UPDATE script_texts SET content_loc3='Die Mächte, die hier am Werk sind, übersteigen eure Vorstellungskraft!' WHERE entry=-1616024; /* content: The powers at work here exceed anything you could possibly imagine! */
UPDATE script_texts SET content_loc3='Eure Mission ist gescheitert!' WHERE entry=-1616026; /* content: Your cause is lost! */
UPDATE script_texts SET content_loc3='Eure zerbrechliche Seele wird zerschmettert!' WHERE entry=-1616027; /* content: Your fragile mind will be shattered! */
UPDATE script_texts SET content_loc3='Ich habe getan, was ich tun musste, Bruder. Ihr habt mir keine andere Wahl gelassen.' WHERE entry=-1616029; /* content: I did what I had to, brother. You gave me no alternative. */
UPDATE script_texts SET content_loc3='Dieser Entschluss schmerzt mich sehr, doch die Zerstörung, der riesige Verlust an Leben, musste ein Ende haben. Ungeachtet Malygos\' jüngster Verfehlungen werde ich seinen Tod betrauern. Er war einst ein Wächter, ein Beschützer. Heute ist einer der Mächtigsten der Welt gefallen.' WHERE entry=-1616031; /* content: This resolution pains me deeply, but the destruction, the monumental loss of life had to end. Regardless of Malygos\' recent transgressions, I will mourn his loss. He was once a guardian, a protector. This day, one of the world\'s mightiest has fallen. */
UPDATE script_texts SET content_loc3='Ein Energiefunke bildet sich aus einem nahegelegenen Graben!' WHERE entry=-1616033; /* content: A Power Spark forms from a nearby rift! */
UPDATE script_texts SET content_loc3='%s holt tief Luft.' WHERE entry=-1616034; /* content: %s takes a deep breath. */
UPDATE script_texts SET content_loc3='Die Geheimnisse der Tiefe werden verborgen bleiben!' WHERE entry=-1619000; /* content: The secrets of the deep shall remain hidden. */
UPDATE script_texts SET content_loc3='Shhhad ak kereeesshh chak-k-k!' WHERE entry=-1619002; /* content: Shhhad ak kereeesshh chak-k-k! */
UPDATE script_texts SET content_loc3='Für den Lichkönig!' WHERE entry=-1619004; /* content: For the Lich King! */
UPDATE script_texts SET content_loc3='Vielleicht sind wir bald Verbündete.' WHERE entry=-1619005; /* content: Perhaps we will be allies soon. */
UPDATE script_texts SET content_loc3='Ein Wächter der Ahn\'kahar schlüpft aus!' WHERE entry=-1619007; /* content: An Ahn\'kahar Guardian hatches! */
UPDATE script_texts SET content_loc3='Wer von Euch ist ergeben?' WHERE entry=-1619018; /* content: Who among you is devoted? */
UPDATE script_texts SET content_loc3='Ihr dort! Tretet vor!' WHERE entry=-1619019; /* content: You there! Step forward! */
UPDATE script_texts SET content_loc3='Meister, ein Geschenk für Euch!' WHERE entry=-1619021; /* content: Master, a gift for you! */
UPDATE script_texts SET content_loc3='Ruhm für Yogg-Saron!' WHERE entry=-1619022; /* content: Glory to Yogg-Saron! */
UPDATE script_texts SET content_loc3='Steht auf! Ihr habt nicht genug gelitten.' WHERE entry=-1619024; /* content: Get up! You haven\'t suffered enough. */
UPDATE script_texts SET content_loc3='Euer Sakrileg kann nicht... ungestraft bleiben.' WHERE entry=-1619025; /* content: Do not expect your sacrilege... to go unpunished. */
UPDATE script_texts SET content_loc3='Die Elemente selbst erheben sich gegen die zivilisierte Welt! Nur die Gläubigen werden verschont!' WHERE entry=-1619026; /* content: The elements themselves will rise up against the civilized world! Only the faithful will be spared! */
UPDATE script_texts SET content_loc3='Ich bin auserwählt worden!' WHERE entry=-1619031; /* content: I have been chosen! */
UPDATE script_texts SET content_loc3='Ich opfere mich selbst für den Meister!' WHERE entry=-1619032; /* content: I give myself to the master! */
UPDATE script_texts SET content_loc3='Shgla\'yos plahf mh\'naus.' WHERE entry=-1619033; /* content: Shgla\'yos plahf mh\'naus. */
UPDATE script_texts SET content_loc3='Gul\'kafh an\'shel. Yoq\'al shn ky ywaq nuul.' WHERE entry=-1619034; /* content: Gul\'kafh an\'shel. Yoq\'al shn ky ywaq nuul. */
UPDATE script_texts SET content_loc3='Ywaq puul skshgn: on\'ma yeh\'glu zuq.' WHERE entry=-1619035; /* content: Ywaq puul skshgn: on\'ma yeh\'glu zuq. */
UPDATE script_texts SET content_loc3='Ywaq ma phgwa\'cul hnakf.' WHERE entry=-1619036; /* content: Ywaq ma phgwa\'cul hnakf. */
UPDATE script_texts SET content_loc3='Ywaq maq oou; ywaq maq ssaggh. Ywaq ma shg\'fhn.' WHERE entry=-1619037; /* content: Ywaq maq oou; ywaq maq ssaggh. Ywaq ma shg\'fhn. */
UPDATE script_texts SET content_loc3='Iilth vwah, uhn\'agth fhssh za.' WHERE entry=-1619039; /* content: Iilth vwah, uhn\'agth fhssh za. */
UPDATE script_texts SET content_loc3='Dies ist der Anfang UND das Ende. Niemand darf das Heiligtum des Meisters betreten!' WHERE entry=-1631001; /* content: This is the beginning AND the end, mortals. None may enter the master\'s sanctum! */
UPDATE script_texts SET content_loc3='Die Geißel wird über diese Welt kommen wie ein Schwarm aus Tod und Zerstörung!' WHERE entry=-1631002; /* content: The Scourge will wash over this world as a swarm of death and destruction! */
UPDATE script_texts SET content_loc3='KNOCHENSTURM!' WHERE entry=-1631003; /* content: BONE STORM! */
UPDATE script_texts SET content_loc3='Gebunden durch Knochen!' WHERE entry=-1631004; /* content: Bound by bone! */
UPDATE script_texts SET content_loc3='Dranbleiben!' WHERE entry=-1631005; /* content: Stick around! */
UPDATE script_texts SET content_loc3='Der einzige Ausweg ist der Tod!' WHERE entry=-1631006; /* content: The only escape is death! */
UPDATE script_texts SET content_loc3='Mehr Knochen für die Opfergabe!' WHERE entry=-1631007; /* content: More bones for the offering! */
UPDATE script_texts SET content_loc3='Siecht in der Verdammnis!' WHERE entry=-1631008; /* content: Languish in damnation! */
UPDATE script_texts SET content_loc3='DER ZORN DES MEISTERS DURCHSTRÖMT MICH!' WHERE entry=-1631010; /* content: THE MASTER\'S RAGE COURSES THROUGH ME! */
UPDATE script_texts SET content_loc3='Ihr fandet hierher, weil Ihr zu denen gehört, die in einer mit Blindheit gestraften Welt sehen können.' WHERE entry=-1631011; /* content: You have found your way here, because you are among the few gifted with true vision in a world cursed with blindness. */
UPDATE script_texts SET content_loc3='Ihr durchblickt den Nebel, der wie ein Schleier auf dieser Welt liegt, und erkennt, wo die wahre Macht liegt.' WHERE entry=-1631012; /* content: You can see through the fog that hangs over this world like a shroud, and grasp where true power lies. */
UPDATE script_texts SET content_loc3='Richtet Euren Blick auf Eure plumpen Hände: Die Sehnen, das weiche Fleisch, das dunkle Blut, das sie durchströmt.' WHERE entry=-1631013; /* content: Fix your eyes upon your crude hands: the sinew, the soft meat, the dark blood coursing within. */
UPDATE script_texts SET content_loc3='Je eher Ihr Euren Zustand als Defekt anerkennt, desto eher werdet Ihr ihn überwinden können.' WHERE entry=-1631015; /* content: The sooner you come to accept your condition as a defect, the sooner you will find yourselves in a position to transcend it. */
UPDATE script_texts SET content_loc3='Durch unseren Meister ist alles möglich. Seine Macht ist grenzenlos und sein Wille unnachgiebig.' WHERE entry=-1631016; /* content: Through our Master, all things are possible. His power is without limit, and his will unbending. */
UPDATE script_texts SET content_loc3='Diejenigen, die sich ihm widersetzen, werden restlos zerstört! Diejenigen aber, die ihm dienen - ganz und bedingungslos - werden Macht jenseits Eures Horizonts erfahren.' WHERE entry=-1631017; /* content: Those who oppose him will be destroyed utterly, and those who serve -- who serve wholly, unquestioningly, with utter devotion of mind and soul -- elevated to heights beyond your ken. */
UPDATE script_texts SET content_loc3='Genug! Ich sehe, ich muss die Dinge selbst in die Hand nehmen!' WHERE entry=-1631019; /* content: Enough! I see I must take matters into my own hands! */
UPDATE script_texts SET content_loc3='Ich erlöse Euch vom Fluch des Fleisches!' WHERE entry=-1631021; /* content: I release you from the curse of flesh! */
UPDATE script_texts SET content_loc3='Ihr seid schwach, unfähig, meinem Willen zu widerstehen!' WHERE entry=-1631023; /* content: You are weak, powerless to resist my will! */
UPDATE script_texts SET content_loc3='BEI DER MACHT DES LICHKÖNIGS!' WHERE entry=-1631028; /* content: BY THE MIGHT OF THE LICH KING! */
UPDATE script_texts SET content_loc3='Der Boden ist rot vor Eurem Blut!' WHERE entry=-1631029; /* content: The ground runs red with your blood! */
UPDATE script_texts SET content_loc3='Schlemmt, meine Diener!' WHERE entry=-1631030; /* content: Feast, my minions! */
UPDATE script_texts SET content_loc3='Ihr seid nichts!' WHERE entry=-1631031; /* content: You are nothing! */
UPDATE script_texts SET content_loc3='Eure Seele wird hier keine Erlösung finden!' WHERE entry=-1631032; /* content: Your soul will find no redemption here! */
UPDATE script_texts SET content_loc3='Dann beeilen wir uns! Brechen wir au...' WHERE entry=-1631035; /* content: Let\'s get a move on then! Move ou... */
UPDATE script_texts SET content_loc3='Und jetzt kommt es noch schlimmer. Kommt, spürt die Macht, die mir der Lichkönig verliehen hat!' WHERE entry=-1631037; /* content: Things are about to get much worse. Come, taste the power that the Lich King has bestowed upon me! */
UPDATE script_texts SET content_loc3='Greift an!' WHERE entry=-1631039; /* content: Charge! */
UPDATE script_texts SET content_loc3='Schließt Euch mir an, Vater, und wir werden diese Welt zerquetschen - im Namen der Geißel - zu Ehren des Lichkönigs!' WHERE entry=-1631042; /* content: Join me, father. Join me and we will crush this world in the name of the Scourge -- for the glory of the Lich King! */
UPDATE script_texts SET content_loc3='Mein Sohn starb an der Pforte des Zorns. Ich bin nur hier, um seinen Körper zu holen.' WHERE entry=-1631043; /* content: My boy died at the Wrath Gate. I am here only to collect his body. */
UPDATE script_texts SET content_loc3='Störrisch und alt. Was bleibt Euch übrig? Ich bin stärker und mächtiger als Ihr es je wart.' WHERE entry=-1631044; /* content: Stubborn and old. What chance do you have? I am stronger, and more powerful than you ever were. */
UPDATE script_texts SET content_loc3='Bevor sie starb, versprach ich seiner Mutter, dass ich alleine durch das Dunkle Portal treten würde - und sollte ich auch sterben, mein Sohn würde sicher sein. Unverdorben...' WHERE entry=-1631046; /* content: I made a promise to his mother before she died; that I would cross the Dark Portal alone - whether I lived or died, my son would be safe. Untainted... */
UPDATE script_texts SET content_loc3='Heute erfülle ich dieses Versprechen.' WHERE entry=-1631047; /* content: Today, I fulfill that promise. */
UPDATE script_texts SET content_loc3='Das war Saurfangs Junge... Hordenkommandant an der Pforte des Zorns. Welch tragisches Ende...' WHERE entry=-1631051; /* content: That was Saurfang\'s boy - the Horde commander at the Wrath Gate. Such a tragic end... */
UPDATE script_texts SET content_loc3='Haltet ein, Muradin. Lasst einen trauernden Vater durch.' WHERE entry=-1631057; /* content: Stand down, Muradin. Let a grieving father pass. */
UPDATE script_texts SET content_loc3='%s weint.' WHERE entry=-1631061; /* content: %s cries. */
UPDATE script_texts SET content_loc3='Ach nichts, Eure Majestät. Ich... bin nur stolz auf meinen König.' WHERE entry=-1631063; /* content: It was nothing, your majesty. Just... I\'m proud of my king. */
UPDATE script_texts SET content_loc3='Sofort, Euer Majestät!' WHERE entry=-1631065; /* content: Right away, yer majesty! */
