UPDATE script_texts SET content_loc3='Törichte Sterbliche. Ihr glaubt, wir wären so einfach besiegt? Die San\'layn sind die unsterblichen Soldaten des Lichkönigs! Seht nun ihre vereinte Macht!' WHERE entry=-1631101; /* content: Foolish mortals. You thought us defeated so easily? The San\'layn are the Lich King\'s immortal soldiers! Now you shall face their might combined! */
UPDATE script_texts SET content_loc3='Solch erstaunliche Macht! Die Kugel der Sinistren macht mich UNBEZWINGBAR!' WHERE entry=-1631103; /* content: Such wondrous power! The Darkfallen Orb has made me INVINCIBLE! */
UPDATE script_texts SET content_loc3='Wart Ihr je eine Gefahr?' WHERE entry=-1631105; /* content: Were you ever a threat? */
UPDATE script_texts SET content_loc3='Wahrheit findet sich im Tode.' WHERE entry=-1631106; /* content: Truth is found in death. */
UPDATE script_texts SET content_loc3='Zittert vor Taldaram, Sterbliche, denn die Macht der Kugel durchströmt mich!' WHERE entry=-1631109; /* content: Tremble before Taldaram, mortals, for the power of the orb flows through me! */
