UPDATE script_texts SET content_loc3='Haltet Eure Waffen bereit, Argentumbrüder! Die Vrykul werden die Frostkönigin mit ihrem Leben schützen.' WHERE entry=-1631130; /* content: Ready your arms, my Argent Brothers. The Vrykul will protect the Frost Queen with their lives. */
UPDATE script_texts SET content_loc3='Genug Geplänkel! Unsere Champions sind hier - helft ihnen, während wir unseren Weg durch die Halle bahnen!' WHERE entry=-1631132; /* content: Enough idle banter! Our champions have arrived - support them as we push our way through the hall! */
UPDATE script_texts SET content_loc3='Ihr mögt mit mir gekämpft haben, Crok, aber nun seid Ihr nicht mehr als ein Verräter. Kommt, Euer zweiter Tod erwartet Euch!' WHERE entry=-1631133; /* content: You may have once fought beside me, Crok, but now you are nothing more than a traitor. Come, your second death approaches! */
UPDATE script_texts SET content_loc3='Ich halte nicht mehr lange durch.' WHERE entry=-1631143; /* content: I will not last much longer! */
UPDATE script_texts SET content_loc3='Eure lächerliche Magie hintergeht euch, Sterbliche. Leidet!' WHERE entry=-1631149; /* content: Suffer, mortals, as your pathetic magic betrays you! */
UPDATE script_texts SET content_loc3='Fühlt ihr die kalte Hand des Todes auf euren Herzen?' WHERE entry=-1631150; /* content: Can you feel the cold hand of death upon your heart? */
UPDATE script_texts SET content_loc3='Euer Vormarsch endet hier! Keiner wird überleben!' WHERE entry=-1631152; /* content: Your incursion ends here! None shall survive! */
