UPDATE script_texts SET content_loc3='Genug! Ich bin diese Spielchen leid!' WHERE entry=-1631156; /* content: Enough! I tire of these games! */
UPDATE script_texts SET content_loc3='So sei es denn. Champions, ANGRIFF!' WHERE entry=-1631161; /* content: So be it. Champions, attack! */
UPDATE script_texts SET content_loc3='Ihr bleibt bis zum Ende am Leben, Fordring. Ich möchte nicht, dass der größte Champion des Lichts verpasst, wie ich diese elende Welt neu gestalte.' WHERE entry=-1631162; /* content: I\'ll keep you alive to witness the end, Fordring. I would not want the Light\'s greatest champion to miss seeing this wretched world remade in my image. */
UPDATE script_texts SET content_loc3='Seht, wie die Welt um Euch herum zusammenbricht!' WHERE entry=-1631165; /* content: Watch as the world around you collapses! */
UPDATE script_texts SET content_loc3='Val\'kyr, Euer Meister ruft Euch!' WHERE entry=-1631166; /* content: Val\'kyr, your master calls! */
UPDATE script_texts SET content_loc3='Frostgram hungert...' WHERE entry=-1631167; /* content: Frostmourne hungers... */
UPDATE script_texts SET content_loc3='Seid Ihr gekommen, Arthas zu richten? Um den Lichkönig zu zerstören?' WHERE entry=-1631168; /* content: You have come to bring Arthas to justice? To see the Lich King destroyed? */
UPDATE script_texts SET content_loc3='Helft mir, diese gequälten Seelen zu vernichten! Gemeinsam lösen wir den Griff Frostgrams und schwächen den Lichkönig von innen heraus!' WHERE entry=-1631170; /* content: Aid me in destroying these tortured souls! Together we will loosen Frostmourne\'s hold and weaken the Lich King from within! */
UPDATE script_texts SET content_loc3='Frostgram nährt sich an der Seele Eures gefallenen Freundes!' WHERE entry=-1631172; /* content: Frostmourne feeds on the soul of your fallen ally! */
UPDATE script_texts SET content_loc3='APOKALYPSE!' WHERE entry=-1631173; /* content: APOCALYPSE! */
UPDATE script_texts SET content_loc3='Kniet nieder vor Eurem Herrn und Meister!' WHERE entry=-1631174; /* content: Bow down before your lord and master! */
UPDATE script_texts SET content_loc3='Das Ende ist gekommen!' WHERE entry=-1631177; /* content: The end has come! */
UPDATE script_texts SET content_loc3='Seht her, wie ich sie zu Anführern der Geißel wiedererwecke. Sie werden die Welt mit Chaos und Zerstörung überziehen. Azeroth wird durch sie fallen - und Ihr sterbt als Erster.' WHERE entry=-1631182; /* content: Watch now as I raise them from the dead to become masters of the Scourge. They will shroud this world in chaos and destruction. Azeroth\'s fall will come at their hands -- and you will be the first to die. */
UPDATE script_texts SET content_loc3='Welch köstliche Ironie.' WHERE entry=-1631183; /* content: I delight in the irony. */
