UPDATE script_texts SET content_loc3='Genug, Arthas! Euer Hass wird keine Leben mehr verzehren!' WHERE entry=-1631186; /* content: No more, Arthas! No more lives will be consumed by your hatred! */
UPDATE script_texts SET content_loc3='Endlich frei! Es ist vorbei, mein Sohn. Dies ist der Moment der Abrechnung.' WHERE entry=-1631187; /* content: Free at last! It is over, my son. This is the moment of reckoning. */
UPDATE script_texts SET content_loc3='Erhebt Euch, Champions des Lichts!' WHERE entry=-1631188; /* content: Rise up, champions of the Light! */
