UPDATE script_texts SET content_loc3='Ist das alles, was die Horde zu bieten hat?' WHERE entry=-1649055; /* content: Is this the best the Horde has to offer? */
UPDATE script_texts SET content_loc3='Im Namen unseres dunklen Meisters. Für den Lichkönig. Ihr. Werdet. Sterben.' WHERE entry=-1649056; /* content: In the name of our dark master. For the Lich King. You. Will. Die. */
UPDATE script_texts SET content_loc3='Ihr seid erledigt!' WHERE entry=-1649057; /* content: You are finished! */
