UPDATE script_texts SET content_loc3='Die Geißel kann nicht aufgehalten werden...' WHERE entry=-1649059; /* content: The Scourge cannot be stopped... */
UPDATE script_texts SET content_loc3='Unwürdig!' WHERE entry=-1649061; /* content: Unworthy! */
