UPDATE script_texts SET content_loc3='Wie könnt Ihr es wagen, mein Sanktum zu betreten!' WHERE entry=-1209000; /* content: How dare you enter my sanctum! */
UPDATE script_texts SET content_loc3='Hol euch der Sand!' WHERE entry=-1209001; /* content: Sands consume you! */
