UPDATE script_texts SET content_loc3='Ha! Ha! Ha! Danke, dass Ihr mich befreit habt, ihr Dummköpfe. Ich werde es Euch vergelten, indem ich Euch das Fleisch von den Knochen brenne.' WHERE entry=-1229003; /* content: Ha! Ha! Ha! Thank you for freeing me, fools. Now let me repay you by charring the flesh from your bones. */
UPDATE script_texts SET content_loc3='Ah, Perfekt!' WHERE entry=-1230000; /* content: Ah, hits the spot! */
UPDATE script_texts SET content_loc3='Kommt und helft dem Thron!' WHERE entry=-1230001; /* content: Come to aid the Throne! */
UPDATE script_texts SET content_loc3='Hoch lebe der König, mein Kleiner!' WHERE entry=-1230002; /* content: Hail to the king, baby! */
UPDATE script_texts SET content_loc3='Ihr habt die Sieben herausgefordert; jetzt werdet Ihr sterben.' WHERE entry=-1230003; /* content: You have challenged the Seven, and now you will die! */
UPDATE script_texts SET content_loc3='Die Söhne Thaurissans werden Euch im Ring des Gesetzes sterben sehen!' WHERE entry=-1230004; /* content: The Sons of Thaurissan shall watch you perish in the Ring of the Law! */
UPDATE script_texts SET content_loc3='Ihr wurdet wegen Verbrechen an der Dunkeleisennation zum Tode verurteilt.' WHERE entry=-1230005; /* content: You have been sentenced to death for crimes against the Dark Iron Nation! */
UPDATE script_texts SET content_loc3='Entfesselt die Wut und lasst es geschehen!' WHERE entry=-1230006; /* content: Unleash the fury and let it be done! */
UPDATE script_texts SET content_loc3='Doch Eure wahre Strafe liegt noch vor Euch.' WHERE entry=-1230007; /* content: But your real punishment lies ahead. */
UPDATE script_texts SET content_loc3='Haha! Ihr dachtet bestimmt, das wäre es gewesen!' WHERE entry=-1230008; /* content: Haha! I bet you thought you were done! */
UPDATE script_texts SET content_loc3='Auf Nimmerwiedersehen!' WHERE entry=-1230009; /* content: Good Riddance! */
UPDATE script_texts SET content_loc3='Frisches Brot zu verkaufen!' WHERE entry=-1230028; /* content: Get him out of there! */
UPDATE script_texts SET content_loc3='Was für ein Zufall. Normalerweise muss ich meinen Unterschlupf verlassen, um etwas zu essen.' WHERE entry=-1249000; /* content: How fortuitous. Usually, I must leave my lair to feed. */
UPDATE script_texts SET content_loc3='Ihr müsst lernen, wo Euer Platz ist, sterbliches Wesen!' WHERE entry=-1249001; /* content: Learn your place mortal! */
UPDATE script_texts SET content_loc3='Ich werde euch von oben verbrennen!' WHERE entry=-1249002; /* content: This meaningless exertion bores me. I\'ll incinerate you all from above! */
UPDATE script_texts SET content_loc3='Es scheint, als wenn Ihr eine weitere Lektion braucht.' WHERE entry=-1249003; /* content: It seems you\'ll need another lesson, mortals! */
