UPDATE `locales_quest` SET `details_loc9`='Per la Luce! Non portare cose simili nel Padiglione... È poco salutare, indicibile, poco... poco... salutare!$B$BTi spiace portarla fuori di qui? Il tuo trofeo sanguinolento non interessa a nessuno!',`objectives_loc9`='Porta la testa del Vecchio Grigione a Vyrin Arialesta al Padiglione Lungopasso.' WHERE `entry`=531;
