UPDATE `locales_quest` SET `title_loc9`='Cartapecora semplice',`details_loc9`='Ah. Mentre eri via $N, ti è arrivata della posta.$B$BQuando hai tempo, dagli una lettura. Se non mi sbaglio proviene da Frang, l\'Istruttore dei Guerrieri. Quando sarai disponibile, vorrebbe scambiare due parole con te. Lo troverai fuori dall\'antro, nascosto tra le ombre.',`objectives_loc9`='Leggi la Cartapecora Semplice e parla con Frang nella Valle delle Sfide.' WHERE `entry`=2383;
