UPDATE `locales_quest` SET `title_loc9`='Nota Verdastra',`details_loc9`='$N, proprio un attimo fa ti cercava una messaggera. Credo fosse stata inviata dall\'Istruttore dei Druidi Gart. Se questa nota è davvero sua, sarebbe meglio leggerla subito.',`objectives_loc9`='Leggi la Nota Verdastra e parla con Gart Cavalca Nebbia al Campo Narache.' WHERE `entry`=3094;
UPDATE `locales_quest` SET `title_loc9`='Pergamena Semplice',`details_loc9`='Mentre eri via a combattere il Flagello, è arrivata questa pergamena per te. Deve essere importante, dato che reca il sigillo dell\'Istruttore dei Guerrieri Dannal. Se fossi in te, la leggerei attentamente prima di ripartire.',`objectives_loc9`='Leggi la Pergamena Semplice e parla con Dannal Stern ad Albamorta.' WHERE `entry`=3095;
