UPDATE `locales_quest` SET `title_loc9`='Obiettivo: Perforatori dell\'Alveare Zora',`details_loc9`='Abbiamo richiesto rinforzi per l\'assalto all\'Alveare Zora. Sei <stato selezionato/stata selezionata> per colpire i Perforatori dell\'Alveare Zora. Fai rapporto al Comandante Mar\'alith una volta completata la missione.',`objectives_loc9`='Uccidi 30 Perforatori dell\'Alveare Zora e fai rapporto al Comandante Mar\'alith nella Fortezza Cenariana a Silitus. Per poter completare la missione, devi anche avere con te le Istruzioni per il Combattimento VII.' WHERE `entry`=8687;
