UPDATE `locales_quest` SET `title_loc9`='La chiamata del Protettore',`details_loc9`='$N,$B$Bin passato hai aiutato l\'Alba d\'Argento al massimo delle tue possibilità. Ancora una volta, facciamo risuonare la Chiamata Chiara in un momento di gran necessità.$B$B$N, se vuoi aiutarci ancora, parla con il Custode degli Archivi nella Cappella della Luce delle Terre Infette Orientali.',`objectives_loc9`='Riporta la Lettera al Custode degli Archivi nella Cappella della Luce delle Terre Infette Orientali.' WHERE `entry`=9247;
