UPDATE `locales_quest` SET `title_loc9`='Cristallo Necrotico Rotto',`details_loc9`='<Sul cadavere dell\'Abominio, trovi un Cristallo Infranto che pulsa leggermente d\'Energia Necrotica.>',`objectives_loc9`='Porta il Cristallo Necrotico Infranto al Tenente Orrin fuori dalle Porte di Roccavento.' WHERE `entry`=9292;
UPDATE `locales_quest` SET `title_loc9`='Quel che va fatto...',`details_loc9`='Il vicino lago è stato contaminato da uno dei cristalli energetici della nave. Molte piante e molti animali della Valle di Ammen hanno subito delle mutazioni.$B$BHo una teoria su come risanare il lago, ma prima ho bisogno di studiare alcuni campioni delle Radici Sferzanti Mutate. Si trovano a ovest, nei Campi di Ammen.$B$BMi aiuterai, $C?',`objectives_loc9`='Ottieni 10 Campioni di Radice Sferzante e portali alla Botanica Taerix al Punto d\'Impatto nella Valle di Ammen.' WHERE `entry`=9293;
UPDATE `locales_quest` SET `title_loc9`='Risanare il lago',`details_loc9`='Grazie al cielo, penso che funzionerà! Ho un reagente che vorrei spargessi per il Lago di Filargento a sud. Neutralizzerà gli effetti del Cristallo Energetico Irradiato caduto qui.$B$BNuota e spargi il reagente neutralizzante nelle sue vicinanze. Non preoccuparti, non resterai $Gesposto:esposta; tanto a lungo da subire gli effetti della contaminazione.',`objectives_loc9`='Spargi il Reagente Neutralizzante presso il Cristallo Energetico Irradiato, quindi torna dalla Botanica Taerix al Punto d\'Impatto nella Valle di Ammen.' WHERE `entry`=9294;
UPDATE `locales_quest` SET `title_loc9`='Una lettera dal fronte',`details_loc9`='<Hai trovato questa lettera addosso a uno dei tanti servi del Flagello che hai ucciso. A giudicare dall\'aspetto e dall\'odore, era in suo possesso da un bel po\' di tempo. Forse qualcuno alla Cappella della Luce è interessato a esaminarla...>',`objectives_loc9`='Porta la Lettera Strappata al Custode degli Archivi nella Cappella della Luce delle Terre Infette Orientali.' WHERE `entry`=9295;
