UPDATE `locales_quest` SET `title_loc9`='I Reietti',`details_loc9`='So che può sembrare strano, ma questi Reietti sono qui per aiutarci. Dovresti parlare con il loro capo, il Gran Esecutore Mavren. Si trova all\'interno della torre nella parte occidentale del villaggio.$B$B$C, quando hai finito di parlare con lui torna da me. C\'è una faccenda della massima urgenza della quale dobbiamo occuparci!',`objectives_loc9`='Parla con il Gran Esecutore Mavren a Tranquillien nelle Terre Spettrali.' WHERE `entry`=9327;
UPDATE `locales_quest` SET `title_loc9`='Eroe dei Sin\'dorei',`details_loc9`='È l\'inizio di un nuovo capitolo nella storia della nostra razza, $N. Riavremo le nostre terre e consolideremo il legame con Sylvanas e i suoi alleati.$B$BPorta la testa del traditore al Reggente Lor\'themar Theron a Lunargenta. Sarà ansioso di udire le novità.',`objectives_loc9`='Porta la Testa di Dar\'khan a Lor\'themar Theron a Lunargenta.' WHERE `entry`=9328;
