UPDATE `locales_quest` SET `title_loc9`='Il giuramento del Saggio',`details_loc9`='$C, hai confermato il tuo impegno. Ora sta a te tradurlo in pratica.$B$BServi bene le Scaglie delle Sabbie e sarai ben $Gricompensato:ricompensata;.$B$BFallisci e la linea temporale che conosciamo verrà distrutta!',`objectives_loc9`='Porta la tua Vera dell\'Eternità a Soridormi nelle Caverne del Tempo dopo aver acquisito la reputazione Onorato con le Scaglie delle Sabbie.' WHERE `entry`=10464;
