UPDATE `locales_quest` SET `title_loc9`='La promessa del Saggio',`details_loc9`='Hai mantenuto la promessa che ci avevi fatto, $N, e per questo otterrai il riconoscimento che meriti.$B$BMan mano che accrescerai la tua sintonia con gli eventi del passato che circondano il Monte Hyjal, io continuerò ad accrescere il potere contenuto nel tuo anello fino a realizzarne l\'intero potenziale.',`objectives_loc9`='Porta la tua Vera dell\'Eternità a Soridormi nelle Caverne del Tempo dopo aver acquisito la reputazione Riverito con le Scaglie delle Sabbie.' WHERE `entry`=10468;
UPDATE `locales_quest` SET `title_loc9`='La promessa del Curatore',`details_loc9`='Hai mantenuto la promessa che ci avevi fatto, $N, e per questo otterrai il riconoscimento che meriti.$B$BMan mano che accrescerai la tua sintonia con gli eventi del passato che circondano il Monte Hyjal, io continuerò ad accrescere il potere contenuto nel tuo anello fino a realizzarne l\'intero potenziale.',`objectives_loc9`='Porta la tua Vera dell\'Eternità a Soridormi nelle Caverne del Tempo dopo aver acquisito la reputazione Riverito con le Scaglie delle Sabbie.' WHERE `entry`=10469;
