UPDATE `locales_quest` SET `title_loc9`='Lungo il Sentiero Violaceo',`details_loc9`='$C, hai scelto il tuo sentiero. Ora devi percorrerlo fin dove ti condurrà.$B$BScoprirai che avere degli amici fra gli Arcimaghi di Dalaran comporta dei vantaggi. Aiutaci e sarai $Gricompensato:ricompensata;.',`objectives_loc9`='Porta il tuo Anello con Sigillo Violaceo all\'Arcimaga Leryda nel Valico Ventomorto dopo aver acquisito la reputazione Onorato con l\'Occhio Violaceo.' WHERE `entry`=10733;
