UPDATE `locales_quest` SET `title_loc9`='Il tocco del maestro',`objectives_loc9`='Recati alle Caverne del Tempo e convinci Medivh ad attivare la Chiave dell\'Apprendista Ricostituita.' WHERE `entry`=10737;
UPDATE `locales_quest` SET `title_loc9`='Un servizio prestato con onore',`details_loc9`='$N, non ci hai delusi. Nutriamo sempre enormi speranze in te.$B$BContinua a servire l\'Occhio Violaceo e sarai $Gricompensato:ricompensata; a dovere.',`objectives_loc9`='Porta il Sigillo Violaceo all\'Arcimaga Leryda nel Valico Ventomorto dopo aver ottenuto la reputazione Riverito conl\'Occhio Violaceo.' WHERE `entry`=10738;
