UPDATE `locales_quest` SET `title_loc9`='Sire Illidan Grantempesta',`details_loc9`='Vieni, $GGran Signore:Gran Signora; $N. Illidan in persona ci aspetta!',`objectives_loc9`='Incontra Illidan Grantempesta. Cerca Barthamus nella Città Bassa di Shattrath qualora dovessi sopravvivere.' WHERE `entry`=11108;
