UPDATE `locales_quest` SET `title_loc9`='La nascita di un Campione',`details_loc9`='Hai meritato il mio rispetto e quello dei tuoi pari. Hai sconfitto tutti quelli che ti hanno lanciato una sfida e hai appreso le tecniche di combattimento da torneo. Senza dubbio, ora puoi affrontare i Campioni delle altre città per la gloria di Orgrimmar!$B$BLa Giustiziera Mariel Cuorsincero desidera conferire con te, $N, per inserirti ufficialmente tra i Campioni della Gran Mischia.',`objectives_loc9`='Parla con la Giustiziera Mariel Cuorsincero ai Campi del Torneo d\'Argento.' WHERE `entry`=13736;
