UPDATE `locales_quest` SET `title_loc9`='Ricetta Fradicia',`details_loc9`='<Questi sembrano essere i resti di una mistura per un potente vino. Forse la proprietaria di Alla Salute a Dalaran potrebbe esservi interessata...>',`objectives_loc9`='Porta la Ricetta Fradicia a Christi Stockton nella città di Dalaran.' WHERE `entry`=14203;
