UPDATE `locales_quest` SET `title_loc9`='C\'è qualcosa nell\'aria (e non è amore)',`details_loc9`='All\'interno del biglietto sono scarabocchiati i dettagli di un folle piano per infettare la maggior parte di Azeroth. Mostralo immediatamente a un investigatore di Spargifumo!',`objectives_loc9`='Consegna il Biglietto d\'Auguri Amoroso Sbiadito all\'Ispettore Snip Catenaccio di Roccavento.' WHERE `entry`=24745;
