UPDATE `locales_quest` SET `title_loc9`='Una vittoria per il Patto d\'Argento',`details_loc9`='$N! Hai ricostruito Quel\'Delar, alla faccia dei proclami della Regina Lana\'thel! Era da lungo tempo che sognavamo questo momento.$B$BIl Patto d\'Argento... Anzi, tutti i Quel\'dorei ti sono debitori.$B$BLa spada sa che, come $C, ti manca l\'addestramento necessario per impugnarla. Per questo non ha stabilito un legame con te. Ma allora chi sarà il suo prossimo padrone? Forse la Crociata d\'Argento può trovarne uno. Va\' a nord, nei Campi del Torneo d\'Argento, e mostrala alla Giustiziera Mariel Cuorsincero da parte del Patto d\'Argento.',`objectives_loc9`='Porta Quel\'Delar Ricostruita alla Giustiziera Mariel Cuorsincero nei Campi del Torneo d\'Argento.' WHERE `entry`=24795;
UPDATE `locales_quest` SET `title_loc9`='Una vittoria per il Patto d\'Argento',`details_loc9`='$N! Hai ricostruito Quel\'Delar, alla faccia dei proclami della Regina Lana\'thel! Era da lungo tempo che sognavamo questo momento.$B$BIl Patto d\'Argento... Anzi, tutti i Quel\'dorei ti sono debitori.$B$BVereesa Ventolesto, il capo del Patto d\'Argento, ha chiesto di vederti quando fossi $Gtornato:tornata; dal Pozzo Solare. $N, porta Quel\'Delar con te e raggiungila nella Cittadella Violacea di Dalaran.',`objectives_loc9`='Porta Quel\'Delar Ricostruita a Vereesa Ventolesto nella Cittadella Violacea di Dalaran.' WHERE `entry`=24796;
