UPDATE `locales_quest` SET `title_loc9`='Odori insoliti',`details_loc9`='C\'è qualcosa che non mi convince nelle Industrie F.lli Corona. Spargifumo sta indagando, ma un paio d\'occhi in più ci farebbero comodo.$B$BSe vuoi aiutarci, va\' a Roccavento. Le indagini sono condotte dall\'Ispettore Snip Catenaccio. Ha sicuramente bisogno di una mano.',`objectives_loc9`='Parla con l\'Ispettore Snip Catenaccio a Roccavento.' WHERE `entry`=24804;
