UPDATE db_script_string SET content_loc1='무쇠 드워프는 이렇게 생긴 상자에 도구를 보관합니다. 천막 근처에서 찾아보세요.' WHERE entry=2000000238; /* content: They keep their tools in boxes that look like this. Look for them near the pavilions. */
UPDATE db_script_string SET content_loc1='거인을 지배할 수 있으리라 생각하는 룬을 새기려는 참입니다.' WHERE entry=2000000239; /* content: I\'m only going to carve the runes I think will give you command over a giant. */
UPDATE db_script_string SET content_loc1='운명을 놓고 우물쭈물할 시간이 있는가? 그만큼 불확실한 미래가 늘어날 뿐인데...' WHERE entry=2000000240; /* content: The longer you wait, the less future you have for me to foretell. */
UPDATE db_script_string SET content_loc1='여기 있습니다, 지배의 룬입니다... 그 이름대로 작동한다면 말이지만 말입니다. 무쇠 드워프가 만든 것만큼 사악한 의도는 없지만 쓸 만할 겁니다.' WHERE entry=2000000241; /* content: There we go, the Rune of Command... hopefully. Not nearly as vicious as its Iron Dwarf cousin, but functional. */
UPDATE db_script_string SET content_loc1='대지에서 일어나라, 형제들이여!' WHERE entry=2000000242; /* content: Arise from the earth, my brothers! */
UPDATE db_script_string SET content_loc1='강철의 주인께서 우리에게 임무를 내리셨다!' WHERE entry=2000000243; /* content: Our iron masters have a mission for us! */
UPDATE db_script_string SET content_loc1='나를 따라 산으로 간다! 주인님의 계획을 실천하는 거다!' WHERE entry=2000000244; /* content: Follow me into the mountains to carry out the plan of the masters! */
UPDATE db_script_string SET content_loc1='우리에게 실패란 없다!' WHERE entry=2000000245; /* content: We will not fail! */
UPDATE db_script_string SET content_loc1='안 돼! 제발 부탁이오! 날 죽이지 마시오!' WHERE entry=2000000246; /* content: No! I beg you! Please don\'t kill me! */
UPDATE db_script_string SET content_loc1='여기서 끝이라니, 그게 무슨 말이지?! 지금 이 자리에서 죽여버리겠어!' WHERE entry=2000000247; /* content: What do you mean my time has come?! I\'ll kill you where you stand! */
UPDATE db_script_string SET content_loc1='주인님께서는 엑소다르의 소재에 관한 정보를 지키려고 엄청난 노력을 하셨다. 너 때문에 내부 첩자가 발각될 뻔했을 뿐만 아니라 우리 계획도 전부 엉망이 될 뻔했다는 걸 아나?' WHERE entry=2000000248; /* content: The Master has gone to great lengths to secure information about the whereabouts of the Exodar. You could have blown the entire operation, including the cover of our spy on the inside. */
UPDATE db_script_string SET content_loc1='이게 무슨 짓인가?! 그만두게! 놈들이 말한 수고비의 두 배를 주겠네!' WHERE entry=2000000248; /* content: What is the meaning of this?! Stop! I\'ll double whatever they\'re paying you! */
UPDATE db_script_string SET content_loc1='그럴 수는 없네... 자네를 바보로 만들 내용을 작성해 주지, $c!' WHERE entry=2000000249; /* content: You can\'t possibly mean to.... I\'ll write you up for this, $c! */
UPDATE db_script_string SET content_loc1='그대 말이 맞소, 아이트리그. 시기가 좋지 않군. 헬스크림에게 갈 사절은 최고의 인재들로 구성하시오.' WHERE entry=2000000250; /* content: You are right, Eitrigg.  The timing is unfavorable.  Make sure our best ambassadors are sent as envoys to Hellscream. */
UPDATE db_script_string SET content_loc1='그가 아버지와 같은 인물이라면 마그하르와 동맹을 맺음으로써 호드에게 새로운 시대가 도래할 것이야.' WHERE entry=2000000252; /* content: If he\'s anything like his father, allying with the Mag\'har will mark the dawn of a new age for the Horde. */
UPDATE db_script_string SET content_loc1='음... 대단해... 정말 강력한 혼합물이로군!' WHERE entry=2000000254; /* content: ...a potent concoction! */
UPDATE db_script_string SET content_loc1='하지만, 어디서 저자와 같은 적당한 실험 대상을 찾을 수 있겠소?' WHERE entry=2000000255; /* content: But where could I ever find a suitable test subject? */
UPDATE db_script_string SET content_loc1='흠... 새 실험 대상을 찾아야겠는데...' WHERE entry=2000000258; /* content: Hmmm... looks like I\'m going to need a new test subject. */
UPDATE db_script_string SET content_loc1='지금까지 잘했네, $n. 지금까지 배운 역사를 잊지 말고 앞으로도 계속 지식을 쌓아 가게.' WHERE entry=2000000259; /* content: You have done well, $N. Continue your search for knowledge, and remember the history you have learned. */
UPDATE db_script_string SET content_loc1='테렌디스 님과 레이네 님께서 당신을 찾기 위해 나를 보내셨습니다. 잿빛 골짜기의 파수꾼들이 도움을 필요로 하고 있습니다.' WHERE entry=2000000260; /* content: Terenthis, Raene sent me to find you. The Sentinels are in need of help in Ashenvale. */
UPDATE db_script_string SET content_loc1='난 아무것도 두렵지 않다. 어디 해 보자!' WHERE entry=2000000261; /* content: I\'m not afraid of anything -- bring it on! */
UPDATE db_script_string SET content_loc1='전혀 옳지 않아, $n. 바람의 조련사 바라가 해준 이야기에 따르면 스톰후프는 악당이 아닌 영웅이었네.' WHERE entry=2000000262; /* content: This isn\'t right at all, $N. In the tale Wind Tamer Barah told me, Stormhoof was a hero, not a villain. */
UPDATE db_script_string SET content_loc1='북풍은 예로부터 타운카의 적이었네. 그는 정령의 군주이자 부족민의 목숨을 오랫동안 앗아간 존재라네.' WHERE entry=2000000263; /* content: The North Wind is the ancient enemy of the taunka, a ruler among the elements, and the very force which bleeds the life from these ancient people. */
UPDATE db_script_string SET content_loc1='그들은 북풍에 굴하기는커녕 죽음을 택했을 걸세. 뭔가 잘못되었어.' WHERE entry=2000000264; /* content: They would rather die than bow before the North Wind. Something is amiss here. */
UPDATE db_script_string SET content_loc1='가르침의 망치라...' WHERE entry=2000000265; /* content: The Lorehammer... */
UPDATE db_script_string SET content_loc1='더욱 의심스러워지는군. 시간의 실마리가 꼬이며 잘못된 무늬를 만들어내 버렸어. 하지만 어떻게 말인가?' WHERE entry=2000000266; /* content: It confirms our suspicions. The threads of time have been severed and rewoven in the wrong pattern. But, how? */
UPDATE db_script_string SET content_loc1='나의 형제 스톰후프는 나보다 훨씬 위대한 전사였네. 내가 부족장이 되기 위한 훈련을 받는 동안 그는 더욱 중대한 임무를 준비했지.' WHERE entry=2000000267; /* content: My brother, Stormhoof, was a far greater warrior than I. While I trained to be the chieftain of our clan, he prepared for a larger quest. */
UPDATE db_script_string SET content_loc1='그런데 어째서 위험에 빠지게 된 거지? 그는 그저 부족의 골칫거리인 정령을 지배할 힘을 얻으려 한 것뿐이라네.' WHERE entry=2000000268; /* content: How could he be in danger? All he wanted was to give us power over the elements that made life harsh for us here. */
UPDATE db_script_string SET content_loc1='그는 어떤 뿔피리를 찾으려 했던 것 같네. 오랜 기간 마을을 떠나 그 유물을 찾으러 다녔지.' WHERE entry=2000000269; /* content: He sought an artifact... a horn, I think. He left the village on a long journey in search of it. */
UPDATE db_script_string SET content_loc1='그가 뿔피리를 손에 얻자 무서운 적이 그를 추격하기 시작했는데... 기억이 나질 않는군... 그다음에... 무슨 일이 일어났는지 말이야.' WHERE entry=2000000270; /* content: I know that a terrible enemy pursued him after he won the horn, but I... I don\'t recall... what happened... next. */
UPDATE db_script_string SET content_loc1='마치 내 기억이 안갯속에 가려진 것 같다네. 그에게 무슨 일이 있었는지조차 생각나질 않아. 이것이 혹시 그가 위험에 처한 이유인가?' WHERE entry=2000000271; /* content: It is as though my memories are shrouded in mist. I cannot even recall what became of my brother. Is this how you mean that he is in danger? */
UPDATE db_script_string SET content_loc1='아마도 묘지에서 일어난 문제와 관련이 있는 게 틀림없네. 주위를 둘러보게, $n. 저들이 보이는가?' WHERE entry=2000000272; /* content: The disturbances in the tomb, they must be involved. Look around you, $N. Do you see them? */
UPDATE db_script_string SET content_loc1='그래, 잘못은 그들에게 있지만 나에겐 그를 막을 힘이 없다네. 툰카로 주민에게 경고해주겠나, 이방인이여? 그들의 선조와 과거가 위험에 처했다고 말이야.' WHERE entry=2000000273; /* content: Yes, they are at fault, but I am powerless to stop them. Will you warn the people of Tunka\'lo, stranger, that their past and their ancestors are in danger? */
UPDATE db_script_string SET content_loc1='가르침의 망치가 반응하지 않습니다. 이것은 스톰후프의 시대에서 온 것이 아닙니다.' WHERE entry=2000000275; /* content: The Lorehammer is silent. This isn\'t from Stormhoof\'s time. */
UPDATE db_script_string SET content_loc1='이런 거짓말쟁이 $r같으니! 시로나스 님께서 너와 네 종족을 몰살하실 것이다!' WHERE entry=2000000276; /* content: Treacherous whelp! Sironas will destroy you and your people! */
UPDATE db_script_string SET content_loc1='모라에... 당신이오?' WHERE entry=2000000277; /* content: Morae... Is that you? */
UPDATE db_script_string SET content_loc1='아, 당신은 내 아내가 아니로군. 하지만, 그녀와 만난 적이 있군. 그녀를 만나거든 내가 추락에서는 살아남았으나 이곳 침투요원들에게 죽임을 당했다고 전해 주시오.' WHERE entry=2000000278; /* content: No... you\'re not Morae, but I sense that you have met. When you see her, tell her that I survived the crash, only to be done in by the infiltrators. */
UPDATE db_script_string SET content_loc1='그리고 아내에게... 내가 죽기 전까지 그녀만을 생각했다고 전해 주시오...' WHERE entry=2000000279; /* content: Let her know... my last thought was of her... */
UPDATE db_script_string SET content_loc1='가장 강한 자가 승리할 것이다!' WHERE entry=2000000281; /* content: May the best win! */
UPDATE db_script_string SET content_loc1='그대는 저를 꺾을 수 없습니다!' WHERE entry=2000000282; /* content: You will not defeat me! */
UPDATE db_script_string SET content_loc1='너는 토림을 위해 일할 자격이 없다!' WHERE entry=2000000283; /* content: You\'re not worthy of Thorim! */
UPDATE db_script_string SET content_loc1='이교도가 정신없이 말을 쏟아냅니다.' WHERE entry=2000000286; /* content: The cultist practically sputters. */
UPDATE db_script_string SET content_loc1='음... 이상하군...' WHERE entry=2000000286; /* content: Hmm... I wonder... */
UPDATE db_script_string SET content_loc1='주인님.' WHERE entry=2000000287; /* content: My lady. */
UPDATE db_script_string SET content_loc1='어휴, 그게 어디 갔지?' WHERE entry=2000000287; /* content: Now, where is it? */
UPDATE db_script_string SET content_loc1='보고하거라.' WHERE entry=2000000288; /* content: Report. */
UPDATE db_script_string SET content_loc1='바보 같은 마법사들... 재료를 끝까지 다 쓰고는 한 번도 채워놓은 적이 없다니까, 쯧.' WHERE entry=2000000288; /* content: Stupid mages... always using the last of a reagent and never replacing it. */
UPDATE db_script_string SET content_loc1='요툰하임에서 소식이 들어왔습니다. 수면의 감시자들이 상당히 중요한 인물을 발견한 모양입니다.' WHERE entry=2000000289; /* content: There is word from Jotunheim. The sleep-watchers there believe that they have found someone of significance. */
UPDATE db_script_string SET content_loc1='좀 더 자세히 설명해 보아라. 어떻게 생겼지?' WHERE entry=2000000290; /* content: Describe this vrykul. What does he look like? */
UPDATE db_script_string SET content_loc1='생김새요? 글쎄요, 브리쿨처럼 보이던데요. 저도 아직 본 적은 없어서요. 어둠의 무기고 때 이후로 통...' WHERE entry=2000000291; /* content: Look like, my lady? A vrykul, I suppose. They did not actually show him to me. Ever since The Shadow Vault.... */
UPDATE db_script_string SET content_loc1='변명은 여전하구나. 그나저나 이름은 알아 왔겠지?' WHERE entry=2000000292; /* content: I am not interested in excuses. Perhaps they gave you a name? */
UPDATE db_script_string SET content_loc1='진정하라고? 캘타스 님께서 그분께 실망을 안겨준 자들에게 어떻게 하시는지 알고나 하는 소린가, 기즐? 영원한 고통과 괴로움에 몸부림치는 거다... 이번 일을 망치면 알아서 해, 멍청한 고블린 녀석.' WHERE entry=2000000292; /* content: Relax? Do you know what Kael\'thas does to those that fail him, Geezle? Eternal suffering and pain... Do NOT screw this up, fool. */
UPDATE db_script_string SET content_loc1='이름이요? 아, 맞다. 그 이름이... 이스칼더였던 것 같은데요.' WHERE entry=2000000293; /* content: A name? Oh, yes, the name! I believe it was Iskalder. */
UPDATE db_script_string SET content_loc1='우리 핏빛안개 척후병들이 우리 정보원의 위치를 파악했다. 그 멍청이 벨렌은 곧 무방비 상태가 될 것이다. 우리가 습격하기에 충분한 시간이지! 이제, 네놈을 없애 버리기 전에 눈앞에서 사라져라...' WHERE entry=2000000293; /* content: Our Bloodmyst scouts have located our contact. The fool, Velen, will soon leave himself open and defenseless -- long enough for us to strike! Now get out of my sight before I vaporize you... */
UPDATE db_script_string SET content_loc1='요툰하임으로 돌아가 전해라. 내가 도착할 때까지 그분을 깨우지 말라고.내 눈으로 직접 확인해 봐야겠다.' WHERE entry=2000000295; /* content: Return to Jotunheim and tell them to keep him asleep until I arrive. I will judge this vrykul with my own eyes. */
UPDATE db_script_string SET content_loc1='쏜살처럼 다녀오겠습니다, 주인님.' WHERE entry=2000000296; /* content: Right away, my lady. */
UPDATE db_script_string SET content_loc1='%s|1이;가; 나가 깃발을 집어듭니다.' WHERE entry=2000000297; /* content: %s picks up the naga flag. */
UPDATE db_script_string SET content_loc1='무슨 일이라도 있습니까, 불똥 님? 모이라고 하신 이유가 뭡니까?' WHERE entry=2000000298; /* content: What\'s the big idea, Spark? Why\'d you call for this meeting? */
UPDATE db_script_string SET content_loc1='뭔가... 다르군.' WHERE entry=2000000299; /* content: I feel... different. */
UPDATE db_script_string SET content_loc1='무슨 짓인가? 너 때문에 내 정체가 거의 드러날 뻔했잖아, 멍청한 녀석! 내가 분명 나침반하고 항해 지도를 안전한 곳에 두라고 하지 않았나. 바보도 쉽게 찾을 수 있는 뻥 뚫린 곳 말고!' WHERE entry=2000000299; /* content: What\'s the big idea? You nearly blew my cover, idiot! I told you to put the compass and navigation maps somewhere safe - not out in the open for any fool to discover. */
UPDATE db_script_string SET content_loc1='주인님께서는 엑소다르의 소재에 관한 정보를 지키려고 엄청난 노력을 하셨다. 너 때문에 내부 첩자가 발각될 뻔했을 뿐만 아니라 우리 계획도 전부 엉망이 될 뻔했다는 걸 아나?' WHERE entry=2000000300; /* content: The Master has gone to great lengths to secure information about the whereabouts of the Exodar. You could have blown the entire operation, including the cover of our spy on the inside. */
UPDATE db_script_string SET content_loc1='진정하십시오, 불똥 님! 모든 것이 제 통제 하에 있습니다. 우리는 그들 몰래 엑소다르에서 채굴을 할 것이고 불똥님과 저는 아주 큰 부자가 될 겁니다.' WHERE entry=2000000301; /* content: Relax, Spark! I have it all under control. We\'ll strip mine the Exodar right out from under \'em - making both you and I very, very rich in the process. */
UPDATE db_script_string SET content_loc1='진정하라고? 캘타스 님께서 그분께 실망을 안겨준 자들에게 어떻게 하시는지 알고나 하는 소린가, 기즐? 영원한 고통과 괴로움에 몸부림치는 거다... 이번 일을 망치면 알아서 해, 멍청한 고블린 녀석.' WHERE entry=2000000302; /* content: Relax? Do you know what Kael\'thas does to those that fail him, Geezle? Eternal suffering and pain... Do NOT screw this up, fool. */
UPDATE db_script_string SET content_loc1='우리 핏빛안개 척후병들이 우리 정보원의 위치를 파악했다. 그 멍청이 벨렌은 곧 무방비 상태가 될 것이다. 우리가 습격하기에 충분한 시간이지! 이제, 네놈을 없애 버리기 전에 눈앞에서 사라져라...' WHERE entry=2000000303; /* content: Our Bloodmyst scouts have located our contact. The fool, Velen, will soon leave himself open and defenseless -- long enough for us to strike! Now get out of my sight before I vaporize you... */
UPDATE db_script_string SET content_loc1='네, 알겠습니다. 다시는 이런 일이 없도록 하겠습니다...' WHERE entry=2000000304; /* content: Yes, sir. It won\'t happen again... */
UPDATE db_script_string SET content_loc1='%s|1이;가; 웃습니다.' WHERE entry=2000000305; /* content: %s laughs. */
UPDATE db_script_string SET content_loc1='하찮은 너희 문명사회에서 조용한 시민인 척하다가, 부름만 받으면 이렇게 헌신적으로 불타는 군단을 섬길 이들이 있다는 게 놀랍지 않나?' WHERE entry=2000000306; /* content: Does it frighten you to know that there are those that would serve the Legion with such devotion as to remain unwavering citizens of your pointless civilizations until called upon? */
UPDATE db_script_string SET content_loc1='두려움에 떨면서 살든가 캘타스 님의 뜻에 따라 죽어라... 결국 죽기는 마찬가지가 아닌가?' WHERE entry=2000000307; /* content: Live in fear, die by the will of Kael\'thas... It\'s all the same. */
UPDATE db_script_string SET content_loc1='이제 널 베겠다!' WHERE entry=2000000308; /* content: And now, I cut you! */
UPDATE db_script_string SET content_loc1='죽어라!' WHERE entry=2000000309; /* content: DIE! */
UPDATE db_script_string SET content_loc1='그러지!' WHERE entry=2000000310; /* content: Grek try! */
UPDATE db_script_string SET content_loc1='이 기름은 나한테 아무짝에도 소용이 없군! 이 그렉이 로브나 뒤집어쓰고 다니는 약골로 보이나?' WHERE entry=2000000311; /* content: This oil no good for Grek! What Grek look like to you, some weakling in robes? */
UPDATE db_script_string SET content_loc1='구해 주셔서 정말 감사합니다, $n! 우리 아버지께서 기뻐하실 거예요!' WHERE entry=2000000313; /* content: Thank you for saving me, $N! My father will be delighted! */
UPDATE db_script_string SET content_loc1='방해해 봤자 소용없다! 제이나가 얼마나 비겁한지 우리가 세상에 알리고 말 테다!' WHERE entry=2000000314; /* content: You can\'t stop us from exposing the truth about Jaina\'s cowardice! */
UPDATE db_script_string SET content_loc1='여기 있는 모든 이는 프라우드무어 제독님이 진정한 영웅이셨다는 걸 알게 될 것이다!' WHERE entry=2000000316; /* content: These people will know Admiral Proudmoore for the true hero he was! */
UPDATE db_script_string SET content_loc1='이 오만하고 멍청한 놈! 감히 젤레마르의 피를 훔치려고 했나? 그 대가를 치르게 해주마!' WHERE entry=2000000317; /* content: Insolent fool! You thought to steal Zelemar\'s blood? You shall pay with your own! */
UPDATE db_script_string SET content_loc1='내가 바라는 것은 오직 그가 성스러운 빛의 정의를 깨닫고 열정을 다해 한때 아름다웠던 쿠엘탈라스를 재건하는 것뿐이라네.' WHERE entry=2000000322; /* content: I can only hope that he will see the Light and instead turn his energies to restoring once-beautiful Quel\'Thalas. */
UPDATE db_script_string SET content_loc1='그를 어서 꺼내주시오!' WHERE entry=2000000323; /* content: Get him out of there! */
UPDATE db_script_string SET content_loc1='당신과 볼일이 끝나면 오그라비시가 당신 머리를 작은 모자로 쓸 지도 모르겠군.' WHERE entry=2000000324; /* content: Perhaps Ograbisi will use your head as a tiny little hat when I\'m through with you. */
UPDATE db_script_string SET content_loc1='내가 살던 곳에서는 다른 죄수의 감옥문을 열어주다 걸리면 큰일난다고!' WHERE entry=2000000325; /* content: Where I come from, you get shanked for opening another inmate\'s cell door! */
UPDATE db_script_string SET content_loc1='얼음 기둥이 녹아 내렸다!' WHERE entry=2000000327; /* content: The Ice Stone has melted! */
UPDATE db_script_string SET content_loc1='아훈, 네 힘은 더 커지지 못한다!' WHERE entry=2000000328; /* content: Ahune, your strength grows no more! */
UPDATE db_script_string SET content_loc1='얼어붙은 통치의 시대는 오지 않을 것이다!' WHERE entry=2000000329; /* content: Your frozen reign will not come to pass! */
UPDATE db_script_string SET content_loc1='놈이 죽었군! 두 번째 파멸의 암호 조각은 이제 우리 것이다. 나... 나는...' WHERE entry=2000000330; /* content: It is dying! The second part of the Cipher of Damnation is ours. I... */
UPDATE db_script_string SET content_loc1='난 사라지고 있어... 알토르에게 돌아... 가... 십시오...' WHERE entry=2000000331; /* content: I am fading... Return to Ar\'tor... Ret... rn... to... */
UPDATE db_script_string SET content_loc1='대단한 전투였죠! 보셨습니까, 리디안 님?!' WHERE entry=2000000332; /* content: What a battle! Did you see that, Rhydian?! */
UPDATE db_script_string SET content_loc1='저희가 브란의 전갈을 받고 미리 준비를 시작했습니다.' WHERE entry=2000000333; /* content: We received Brann\'s message, and we have begun preparations. */
UPDATE db_script_string SET content_loc1='딱 좋군요. 그럼 신호를 보내버립시다.' WHERE entry=2000000334; /* content: Just in time. Let\'s see this thing off, then. */
UPDATE db_script_string SET content_loc1='달라란의 시민이여! 두 눈을 들어 하늘을 올려다보라!' WHERE entry=2000000335; /* content: Citizens of Dalaran! Raise your eyes to the skies and observe! */
UPDATE db_script_string SET content_loc1='오늘 우리는, 바로 우리를 창조한 존재에 맞서 세계의 파괴를 막아냈다!' WHERE entry=2000000336; /* content: Today our world\'s destruction has been averted in defiance of our very makers! */
UPDATE db_script_string SET content_loc1='용감한 우리 동료가, 티탄 도시 울두아르 깊숙한 곳에서 티탄의 사자인 관찰자 알갈론을 물리쳤다!' WHERE entry=2000000337; /* content: Algalon the Observer, herald of the titans, has been defeated by our brave comrades in the depths of the titan city of Ulduar. */
UPDATE db_script_string SET content_loc1='알갈론은 우리 세상의 운명을 결정하러 왔었다.' WHERE entry=2000000338; /* content: Algalon was sent here to judge the fate of our world. */
UPDATE db_script_string SET content_loc1='그가 찾은 행성에는 티탄의 청사진을 벗어난 종족들이 살고 있었다. 그곳은 티탄의 계획과 다르게 돌아가고 있었다.' WHERE entry=2000000339; /* content: He found a planet whose races had deviated from the titans\' blueprints. A planet where not everything had gone according to plan. */
UPDATE db_script_string SET content_loc1='차가운 논리는 이 세상을 보전할 가치가 없다고 판단했다. 그러나 차가운 논리는, 자유 의지의 힘을 간과했다! 이제 우리가 증명해야 한다. 이 세상의 가치를!' WHERE entry=2000000340; /* content: Cold logic deemed our world not worth saving. Cold logic, however, does not account for the power of free will. It\'s up to each of us to prove this is a world worth saving. */
UPDATE db_script_string SET content_loc1='그리고 우리의 삶이... 살 가치가 있음을.' WHERE entry=2000000341; /* content: That our lives... our lives are worth living. */
UPDATE db_script_string SET content_loc1='온다! 놈들이 이곳을 통과하게 해서는 안 된다! 화염 마법의 힘을 집중하라! 지금, 바로 지금이다!' WHERE entry=2000000344; /* content: INCOMING! Do not let them through! Focus fire! NOW, NOW, NOW! */
UPDATE db_script_string SET content_loc1='달의 눈물의 힘은 내 것이다! 내 것이라고!' WHERE entry=2000000345; /* content: The power of the Tear of the Moons is mine! Mine I say! */
UPDATE db_script_string SET content_loc1='%s|1이;가; 달의 눈물을 문지르기 시작합니다.' WHERE entry=2000000346; /* content: %s begins to rub the Tear of the Moons. */
UPDATE db_script_string SET content_loc1='힘! 엄청난 힘이 솟는다!' WHERE entry=2000000347; /* content: Power! Glorious power! */
UPDATE db_script_string SET content_loc1='대지에서 일어나라, 형제들이여!' WHERE entry=2000000348; /* content: Arise from the earth, my brothers! */
UPDATE db_script_string SET content_loc1='%s|1이;가; 윙윙거리는 이상한 소리를 내기 시작합니다. 그러자 달의 눈물이 땅에 떨어져 박살이 납니다.' WHERE entry=2000000348; /* content: %s begins to make strange grunting noises. The Tear of the Moons drops to the ground and shatters. */
UPDATE db_script_string SET content_loc1='강철의 주인께서 우리에게 임무를 내리셨다!' WHERE entry=2000000349; /* content: Our iron masters have a mission for us! */
UPDATE db_script_string SET content_loc1='나를 따라 산으로 간다! 주인님의 계획을 실천하는 거다!' WHERE entry=2000000350; /* content: Follow me into the mountains to carry out the plan of the masters! */
UPDATE db_script_string SET content_loc1='아파요... 참을 수 없을 정도로... 이 고통을 끝내 주세요... 제발요!' WHERE entry=2000000350; /* content: I hurt... suffering unbearable... end my pain... I beg of you! */
UPDATE db_script_string SET content_loc1='나는 주인님의 명령을 따르고자 죽음에서 돌아왔다.' WHERE entry=2000000352; /* content: I return from the grave to do my master\'s bidding. */
UPDATE db_script_string SET content_loc1='또다시 실패로군...' WHERE entry=2000000353; /* content: Another failure... */
UPDATE db_script_string SET content_loc1='이 녀석은 너무 일찍 깨어났다. 아직 감정과 기억이 남았어...' WHERE entry=2000000354; /* content: This one has awoken too soon. It retains emotion and memory... */
UPDATE db_script_string SET content_loc1='리치 왕께서 이 수습 기사를 마음에 들어 하실 것이다.' WHERE entry=2000000355; /* content: The Lich King will be pleased with this initiate. */
UPDATE db_script_string SET content_loc1='쓸모 없는 놈...' WHERE entry=2000000356; /* content: Useless... */
UPDATE db_script_string SET content_loc1='네, 훈련교관님.' WHERE entry=2000000360; /* content: Yes, instructor. */
UPDATE db_script_string SET content_loc1='즉시 시행하겠습니다, 훈련교관님.' WHERE entry=2000000361; /* content: Right away, instructor. */
UPDATE db_script_string SET content_loc1='일어나라, 구울이여. 일어나 약한 자를 먹어치워라!' WHERE entry=2000000362; /* content: Rise, minions. Rise and feast upon the weak! */
UPDATE db_script_string SET content_loc1='스컬지에 승리를!' WHERE entry=2000000365; /* content: Victory to the Scourge! */
UPDATE db_script_string SET content_loc1='우리에게 대항하는 모든 자에게 죽음을!' WHERE entry=2000000366; /* content: Death to all that oppose us! */
UPDATE db_script_string SET content_loc1='애처롭구나...' WHERE entry=2000000368; /* content: Pathetic... */
UPDATE db_script_string SET content_loc1='네가 지금 무슨 짓을 하고 있다고 생각하느냐? 감히 주인님의 실험을 방해하려는 것이냐?' WHERE entry=2000000369; /* content: And just what do you think you are doing?  You dare to interfere with my master\'s experiment? */
UPDATE db_script_string SET content_loc1='우리가 여기서 한 일이 마음에 드나? 우리는 하늘에서 이 수정을 아웃랜드 전역에 뿌릴 것이다.' WHERE entry=2000000370; /* content: Do you like what we\'ve done here?  Perhaps we will drop these crystals from the sky all over Outland. */
UPDATE db_script_string SET content_loc1='이 세계를 치유하고 거대괴수를 불러내서 미치게 하는 힘을 막으려는 네 행동이 점점 지겨워지는군. 거대괴수의 왕 골리아손이여, 캘타스 왕자와 내가 명하노니 이리 와서 수정을 보호해라!' WHERE entry=2000000371; /* content: I grow bored with your attempt to heal the land and quell the energies summoning and driving the colossi mad.  Goliathon, King of the Colossi, Prince Kael\'thas and I demand that you defend the crystal! */
UPDATE db_script_string SET content_loc1='조만간 다시 만나게 될 것이다.' WHERE entry=2000000372; /* content: We will meet again soon. */
UPDATE db_script_string SET content_loc1='이건 왜 여기 있는 거야? 감독한테 혼나기 전에 어서 배로 가져가야겠군!' WHERE entry=2000000373; /* content: Where\'d this come from? I better get this down to the ships before the foreman sees it! */
UPDATE db_script_string SET content_loc1='그냥 여기 남겨 둬야지. 그게 현명한 행동일 거라고!' WHERE entry=2000000374; /* content: I\'ll just leave it here and nobody will be the wiser! */
UPDATE db_script_string SET content_loc1='내 옆에 바짝 붙으라. 아무 일 없도록 내가 지켜주겠다.' WHERE entry=2000000376; /* content: Stand near me, $N. I will protect you should anything go wrong. */
UPDATE db_script_string SET content_loc1='말퓨리온!' WHERE entry=2000000377; /* content: Malfurion! */
UPDATE db_script_string SET content_loc1='말퓨리온, 온통 악몽만 꾸었어. 도대체 에메랄드의 꿈속에서 무슨 일이 일어나고 있는 거지? 무엇 때문에 그렇게 끔찍한 일이 벌어진 걸까?' WHERE entry=2000000379; /* content: It was shrouded in nightmares, Malfurion. What is happening in the Dream? What could cause such atrocities? */
UPDATE db_script_string SET content_loc1='나 역시 많은 것이 느껴져, 말퓨리온. 암울한 시대가 다가오고 있어.' WHERE entry=2000000381; /* content: I sensed as much, Malfurion. Dark days loom ahead. */
UPDATE db_script_string SET content_loc1='오랜 친구여, 부디... 잘 가게...' WHERE entry=2000000386; /* content: Farewell, old friend... Farewell... */
UPDATE db_script_string SET content_loc1='숲으로 돌아가자.' WHERE entry=2000000387; /* content: Let us return to the grove, mortal. */
UPDATE db_script_string SET content_loc1='좋아요, 어서 가죠! 내가 완벽한 곳을 알고 있답니다!' WHERE entry=2000000388; /* content: Ok, let\'s go.  I know just the perfect spot! */
UPDATE db_script_string SET content_loc1='아니 이런! 나한테... 무슨 일이... 벌어지고 있는 거죠? 악! 이 고양이 좀 저리 치워줘요!' WHERE entry=2000000389; /* content: What... is happening... to me?  Get this cat away from me! */
UPDATE db_script_string SET content_loc1='아롱이가 굶주림에 가득 찬 눈으로 비에라 선위스퍼를 쳐다봅니다.' WHERE entry=2000000390; /* content: Twinkle looks at Viera Sunwhisper with very hungry eyes. */
UPDATE db_script_string SET content_loc1='꼬마 새, 이리 와라! 한 번 놀자!' WHERE entry=2000000392; /* content: Come out, little birdies! Mug\'gok want to play! */
UPDATE db_script_string SET content_loc1='내 기분 망치지 마라, 새대가리!' WHERE entry=2000000393; /* content: No hurt Mug\'gok\'s feelings, bird-men! */
UPDATE db_script_string SET content_loc1='좋아! 새들 늘 그런 식이지! 무그고크 새 친구 생겼다!' WHERE entry=2000000394; /* content: Fine! Birdies be dat way! Mug\'gok find new friends! */
UPDATE db_script_string SET content_loc1='%s|1이;가; 유물 바닥에서 먼지를 털어냅니다.' WHERE entry=2000000395; /* content: %s dusts the base of the artifact. */
UPDATE db_script_string SET content_loc1='%s|1이;가; 큰 꽃병을 꼼꼼히 관찰합니다.' WHERE entry=2000000396; /* content: %s meticulously examines the large vase. */
UPDATE db_script_string SET content_loc1='불쌍한 도리우스... 망할 검은무쇠 놈들, 내 손에 걸리기만 해봐. 날 좀 도와...' WHERE entry=2000000397; /* content: Poor Dorius. If I ever get my hands on those Dark Irons, so help me... */
UPDATE db_script_string SET content_loc1='이 험난한 시대의 희생양들을 도웁시다. 조금만 자선을 베풀어 주세요! 제발 부탁드립니다.' WHERE entry=2000000398; /* content: Give to the charities who seek to help the victims of these hard times! Please. */
UPDATE db_script_string SET content_loc1='스톰윈드의 고아들을 잊지 마세요!' WHERE entry=2000000399; /* content: Don\'t forget the orphans of Stormwind! */
UPDATE db_script_string SET content_loc1='따뜻한 마음을 나눠 주세요! 부모 잃은 스톰윈드의 아이들에게 도움을 부탁 드립니다!' WHERE entry=2000000400; /* content: Give of your hearts and your purses! Give to the children of Stormwind who have lost their parents. */
UPDATE db_script_string SET content_loc1='스톰윈드의 아이들을 도와주세요... 전쟁과 질병에 희생된 불쌍한 아이들입니다!' WHERE entry=2000000401; /* content: Help the children of Stormwind... victims of the war and plague! */
UPDATE db_script_string SET content_loc1='%s|1이;가; 막강한 스컬지 군대를 소집합니다!' WHERE entry=2000000402; /* content: %s calls upon the mighty armies of the Scourge! */
UPDATE db_script_string SET content_loc1='스컬지 군대여, 내 말을 들어라! 붉은십자군 대참사가 시작됐다! 이 땅을 갈가리 찢어발기고, 너희가 지나간 자리에 죽음만을 남겨라!' WHERE entry=2000000403; /* content: Armies of the Scourge, hear my call! The scarlet apocalypse has begun! Tear this land asunder and leave only death in your wake! */
UPDATE db_script_string SET content_loc1='지금 최선을 다하고 있습니다!' WHERE entry=2000000407; /* content: I\'m workin\' as fast as I can, cap\'n! */
UPDATE db_script_string SET content_loc1='계속 이 상태로는 일할 수 없습니다! 스컬지 손에 죽지 않는다면 전 피로로 죽을 겁니다!' WHERE entry=2000000408; /* content: I can\'t keep this pace up, sir! If the Scourge don\'t get me, exhaustion will! */
UPDATE db_script_string SET content_loc1='고통을 즐겨라, 죽음의 기사여...' WHERE entry=2000000409; /* content: Suffer well, death knight... */
UPDATE db_script_string SET content_loc1='어둠의 영역이 기다리는구나...' WHERE entry=2000000410; /* content: The realm of shadows awaits... */
UPDATE db_script_string SET content_loc1='이제 어떻게 하죠? 모든 걸 다 잃었는데!' WHERE entry=2000000411; /* content: What are we to do? We\'ve lost everything! */
UPDATE db_script_string SET content_loc1='우리는 싸운다! 언제나 그랬듯이 놈들을 밀어붙여라!' WHERE entry=2000000413; /* content: We fight! We push them back, just as we have always done! */
UPDATE db_script_string SET content_loc1='%s|1이;가; 깜짝 놀라면서 일어납니다.' WHERE entry=2000000467; /* content: %s wakes up, startled. */
UPDATE db_script_string SET content_loc1='대체 무슨 근거로 박쥐가 저 각도로 안전히 착지할 거로 생각하는 건지 모르겠단 말이야. 남는 건 부러진 목에 냄새 나는 박쥐 스튜밖에 없을 거라고.' WHERE entry=2000000467; /* content: What do they expect, making the bats come in at that angle? Broken necks and gamey bat stew, that\'s what they get. */
UPDATE db_script_string SET content_loc1='그게... 모두 꿈이었다니? 하늘안개 섬, 핏빛안개 섬, 장가르 습지대, 어둠달 골짜기... 그리고 줄아만까지도... 제인! 어디 있니?!' WHERE entry=2000000468; /* content: Was... Was it all a dream? Azuremyst, Bloodmyst, Zangarmarsh, Shadowmoon... even Zul\'Aman... Jane! Jane where are you! */
UPDATE db_script_string SET content_loc1='저 여기 있잖아요, 아빠. 오늘 하루 종일 주무셨어요. 굉장히 피곤하셨나 봐요!' WHERE entry=2000000469; /* content: I\'m right here, daddy. You\'ve been asleep all day long. You sure must be tired! */
UPDATE db_script_string SET content_loc1='아, 멋지군요. 모든 게 한낱 꿈이었습니다...' WHERE entry=2000000470; /* content: Ah, wonderful. It was all just a dream... */
UPDATE db_script_string SET content_loc1='%s|1이;가; 다시 해변에서 놀기 시작합니다.' WHERE entry=2000000471; /* content: %s resumes playing on the beach. */
UPDATE db_script_string SET content_loc1='병을 치우더니 %s|1이;가; 한숨을 쉽니다.' WHERE entry=2000000474; /* content: Putting the bottles away, %s sighs. */
UPDATE db_script_string SET content_loc1='정말 멋진 솜씨로군요!' WHERE entry=2000000482; /* content: What a beautiful shot! */
UPDATE db_script_string SET content_loc1='하하! 명중했어요!' WHERE entry=2000000483; /* content: Hah! Bullseye! */
UPDATE db_script_string SET content_loc1='아... 등골이 다 오싹하군.' WHERE entry=2000000484; /* content: Agh... a chill ran up my spine. */
UPDATE db_script_string SET content_loc1='바라건대 저들도 빛을 보게 될 거예요.' WHERE entry=2000000485; /* content: They\'ll see the light, I hope. */
UPDATE db_script_string SET content_loc1='이... 이제 충분히 본 것 같소... 정말 지치는군...' WHERE entry=2000000522; /* content: I... I\'ve seen enough I think... so very draining... */
UPDATE db_script_string SET content_loc1='%s|1이;가; 책을 훑어봅니다.' WHERE entry=2000000524; /* content: %s skims the pages of the book. */
UPDATE db_script_string SET content_loc1='정말로 대단하군, 몰트레이크. 내 계획이 완벽하게 진행된 것 같아. 안 그런가, 나약한 $R|1이여;여;?' WHERE entry=2000000526; /* content: Glorious, indeed, Maltrake. It looks as if my plan worked perfectly. Wouldn\'t you say so, mortal? */
UPDATE db_script_string SET content_loc1='%s|1이;가; 파충류와 비슷한 소리를 냅니다... 웃음소리인 것 같습니다.' WHERE entry=2000000527; /* content: %s lets loose a reptilian laugh... at least you think it\'s a laugh. */
UPDATE db_script_string SET content_loc1='우리가 단순히 지원을 해달라고 요청했다면 자네는 아마 검은바위 부족을 도우려고 하지 않았겠지?' WHERE entry=2000000528; /* content: You would not have helped the legion of Blackrock had we just asked for your assistance, now would you? */
UPDATE db_script_string SET content_loc1='어떻게 처리할까요, 주인님? 이제 이용할 가치가 없는 것 같습니다.' WHERE entry=2000000529; /* content: What will we do with them, master? They are of no use to us anymore. */
UPDATE db_script_string SET content_loc1='조용해, 멍청한 것!' WHERE entry=2000000530; /* content: SILENCE FOOL! */
UPDATE db_script_string SET content_loc1='이제 나는 가겠다, 나약한 $r|1이여;여;. 목숨만은 살려주지. 몰트레이크! 만물 상자를 주도록 하여라!' WHERE entry=2000000532; /* content: I leave you now, mortals. Alive and with these trinkets. Maltrake! Present them with the trinkets! */
UPDATE db_script_string SET content_loc1='알겠습니다, 주인님!' WHERE entry=2000000533; /* content: Right away, master! */
UPDATE db_script_string SET content_loc1='%s|1이;가; 날개를 점점 더 빨리 퍼덕이기 시작하며 날아갈 준비를 합니다.' WHERE entry=2000000534; /* content: %s begins to flap his massive wings faster. He is preparing for flight. */
UPDATE db_script_string SET content_loc1='아, 그 허물은 잃어버리지 마라. 불타는 평원에는 검은용군단의 허물로 강력한 물건을 만드는 종족이 있다고 하니까.' WHERE entry=2000000535; /* content: Oh yes, the molt - do not lose it. There are those of my kin in the Burning Steppes that would craft items that only the molt of the black dragonflight could fortify. */
UPDATE db_script_string SET content_loc1='드워프들이여! 검은바위 부족의 군대가 온다. 우리는 대지와 하늘을 불태울 것이다. 아무도 살아남지 못하리라.' WHERE entry=2000000536; /* content: The legion of Blackrock comes, dwarflings! We shall scorch the earth and set fire to the heavens. None shall survive... */
UPDATE db_script_string SET content_loc1='서둘러야 하오.' WHERE entry=2000000537; /* content: We must hurry. */
UPDATE db_script_string SET content_loc1='숲의 정령들이 앞으로 나와 자연의 부름에 귀를 기울이고 있소이다.' WHERE entry=2000000538; /* content: Spirits of the forest come forth and heed nature\'s call! */
UPDATE db_script_string SET content_loc1='숲의 정령들이... 당신의 도움을 필요로 하고 있소! 서둘러 잿더미 계곡으로 갑시다!' WHERE entry=2000000539; /* content: Spirits of the forest, you are needed! Make haste to the Charred Vale! */
UPDATE db_script_string SET content_loc1='어디 보자... 내 생각이 맞았다면 이건... 흠...' WHERE entry=2000000540; /* content: Now, let\'s see... If I am correct, then... Hmmm... */
UPDATE db_script_string SET content_loc1='푸른 수정에 붉은 수정을 더하면... 내 생각에는...' WHERE entry=2000000541; /* content: Blue plus red... I predict... */
UPDATE db_script_string SET content_loc1='$n, 해냈어요! 드디어 알아냈어요!' WHERE entry=2000000542; /* content: $N, that\'s it! I have finally figured it out! */
UPDATE db_script_string SET content_loc1='고마워요, 친구.' WHERE entry=2000000558; /* content: Thank you, friend. */
UPDATE db_script_string SET content_loc1='영원히 갚지 못할 빚을 져버렸습니다.' WHERE entry=2000000559; /* content: I am forever indebted to you, friend. */
UPDATE db_script_string SET content_loc1='그대에게 감사를 드리오, 이방인.' WHERE entry=2000000561; /* content: You have my thanks, stranger. */
UPDATE db_script_string SET content_loc1='%s|1이;가; 고마운 듯 울음소리를 냅니다!' WHERE entry=2000000562; /* content: %s trumpets in approval! */
UPDATE db_script_string SET content_loc1='형제자매들이여, 잘 들으시오. 우리 종족의 생존에 대해서 논해 봅시다. 지난번 재앙에서는 가까스로 살아남았을지 몰라도 다음엔 우리 모두 멸망할까 두렵습니다.' WHERE entry=2000000571; /* content: My brothers and sisters. Listen to me. We must speak of the survival of our people. We have narrowly survived one calamity, but I fear the next shall destroy us. */
UPDATE db_script_string SET content_loc1='상황이 어떻게 돌아가고 있는지 모르겠소? 눈을 크게 뜨시오. 보려고 하면 보일 거요. 대체 이 새 동맹은 뭐지? 우리가 오랜 동맹을 배신한 이유가 고작 공공의 적과 친해지기 위해서란 말이오?' WHERE entry=2000000572; /* content: Can you not see what is happening? Open your eyes. It is laid bare for all to see. What is this new alliance? Why have we betrayed our allies of old, only to take up with the enemies of all that live? */
UPDATE db_script_string SET content_loc1='아니, 당신이 틀렸습니다! 배신을 당한 것은 우리 쪽이라고요!' WHERE entry=2000000573; /* content: No, you\'re wrong! We were the ones betrayed! */
UPDATE db_script_string SET content_loc1='드워프들이 우리의 동맹이었던 적이 있던가?' WHERE entry=2000000574; /* content: When have the dwarves ever been our friends? */
UPDATE db_script_string SET content_loc1='암흑의 시기에 인간들은 우리를 버렸잖아! 인간 종족의 왕자는 몸소 우리 땅을 습격했고...' WHERE entry=2000000575; /* content: The humans abandoned us in our darkest hour! Their prince himself led the assault on our lands. */
UPDATE db_script_string SET content_loc1='바보 같으니! 왕자의 부하들은 현재 우리가 동맹을 맺은 바로 그 포세이큰이었고 이제 그 왕자가 우릴 배반했소!' WHERE entry=2000000576; /* content: You fool! The prince\'s minions were the very Forsaken we have now allied ourselves with, and now he has betrayed us! */
UPDATE db_script_string SET content_loc1='실버문의 순찰대 사령관은 동맹으로서 의리를 지킬 것입니다!' WHERE entry=2000000577; /* content: The Ranger-General of Silvermoon will be a loyal ally! */
UPDATE db_script_string SET content_loc1='그녀를 진정 친구로서 여기는 거요? 정말 바보 같군. 현재 어둠의 여왕은 예전의 그 실바나스가 아니오.' WHERE entry=2000000578; /* content: You speak of her as friend? You are a fool. This "Dark Lady" is not Sylvanas. */
UPDATE db_script_string SET content_loc1='진실한 믿음은 곧 피에 있습니다. 우리 종족 외에는 누구도 믿을 수 없어요!' WHERE entry=2000000579; /* content: Blood will be true. We can trust in none but our own! */
UPDATE db_script_string SET content_loc1='방금 피라고 하셨소? 과연 자기 좋을 때만 친구인 척하는 오크들이 우리를 도와줄까?' WHERE entry=2000000580; /* content: Blood? And these orcs, these fair weather friends? They will aid us? */
UPDATE db_script_string SET content_loc1='은빛십자군이 여는 마상시합에 여러분을 초대합니다. 다른 아제로스의 용사들과 명예로운 전투를 치르고 자신의 실력을 가늠해 보십시오.' WHERE entry=2000000580; /* content: The Argent Crusade invites you to test your skills in honorable combat against other heroes of Azeroth at the Argent Tournament. */
UPDATE db_script_string SET content_loc1='지금 우리의 땅에 지독한 병이 돌고 있소. 날로 심각해지고 있지! 마력에 대한 욕구가 클수록 우리는 약해질 테고 결국 몰락하고 말 거요. 과거에 그랬던 것처럼!' WHERE entry=2000000581; /* content: A great blight lies across our land! It grows every day. Our need for magic weakens us, and will bring us to destruction, just as it has before! */
UPDATE db_script_string SET content_loc1='전 세계의 용사들이 도전자들과 맞서기 위해 북동쪽 얼음왕관에 있는 은빛십자군 마상시합 광장에 모여들고 있습니다.' WHERE entry=2000000581; /* content: The champions of the world\'s great nations have gathered at the Argent Tournament Grounds in northeastern Icecrown to face all challengers. */
UPDATE db_script_string SET content_loc1='하지만, 우리가 마력 없이 어떻게 살아갑니까?' WHERE entry=2000000582; /* content: How are we to live without magic? */
UPDATE db_script_string SET content_loc1='경이로운 이 도시를 한번 보십시오! 이것이 바로 마법의 힘입니다.' WHERE entry=2000000583; /* content: Look at the wonders of this city! This is magic. */
UPDATE db_script_string SET content_loc1='우리가 배신자라는 말입니까?' WHERE entry=2000000585; /* content: You call us traitors? */
UPDATE db_script_string SET content_loc1='마법학자들이야말로 배신자들이오! 우리가 이렇게 된 것도 다 그들 때문이지.' WHERE entry=2000000586; /* content: The magisters are the traitors! They have sealed our doom. */
UPDATE db_script_string SET content_loc1='그런 허튼소리 그만 늘어놓고 숲으로 돌아가시오!' WHERE entry=2000000587; /* content: Idealistic blather. Go back to your forests! */
UPDATE db_script_string SET content_loc1='그래, 점쟁이가 너를 돕기로 했다는 말이구나, 이방인. 좋다, 내가 아는 걸 말해 주마.' WHERE entry=2000000587; /* content: So the oracle has decided to aid you, outlander. Very well, I will offer you what i know. */
UPDATE db_script_string SET content_loc1='당신 같은 자들 때문에 우리는 미개한 종족처럼 살아야 하잖아!' WHERE entry=2000000588; /* content: You would have us live like the savages! */
UPDATE db_script_string SET content_loc1='네 동료는 우두머리 수정뿔 코뿔소인 파룬을 찾고 있다. 녀석은 자기 형제들에게서 멀리 떨어져 있지.' WHERE entry=2000000588; /* content: Your companion seeks the shardhorn patriarch Farunn, who holds himself aloof from his brothers. */
UPDATE db_script_string SET content_loc1='절대 그런 것은 아니오! 내 양심을 걸고 말하겠소. 우리 일족의 무절제는 우리 자신과, 그동안 힘들게 쌓아올린 모든 것을 파괴할 거요.' WHERE entry=2000000589; /* content: No, not savagery! Conscience. The excesses of our people will destroy us, and all that we have struggled to build. */
UPDATE db_script_string SET content_loc1='우리는 살아남고자 투쟁하지 않았소. 그것이 결국 철저한 파괴를 불러오고 만 것이지.' WHERE entry=2000000590; /* content: We have not struggled so hard to survive, only to bring about the true destruction. */
UPDATE db_script_string SET content_loc1='너희 동료에게 돌아가라. 계속 나아가서 파룬을 처치하고 전리품을 취하라고 일러라.' WHERE entry=2000000590; /* content: Return to your companion. Tell him to go forth and claim his prize in Farunn. */
UPDATE db_script_string SET content_loc1='우리는 파멸의 갈림길에 서 있소. 이 성급한 행동에 대해 다시 생각해 봐야 하오.' WHERE entry=2000000591; /* content: We stand on the brink of destruction. We must reconsider this rash course of action. */
UPDATE db_script_string SET content_loc1='일을 마치거든 조용히 이 땅을 떠나라고 전해라. 그는 이 땅에 속한 자가 아니다.' WHERE entry=2000000591; /* content: Once he has done so, tell him to leave this land in peace. He does not belong here. */
UPDATE db_script_string SET content_loc1='우리가 한목소리로 말한다면 그 누구도 우릴 무시할 수 없을 거요!' WHERE entry=2000000592; /* content: If we speak as one, we cannot be ignored! */
UPDATE db_script_string SET content_loc1='우리에게 여러분의 힘을 실어주시오! 지금이야말로 우리 종족을 구원하기 위해 행동에 나설 때요!' WHERE entry=2000000593; /* content: Lend us your voices! Now is the time we must act to save our people. */
UPDATE db_script_string SET content_loc1='로르테마르는 우리 말을 경청해야 할 거요! 우리는 절대 조용히 있지 않을 테니까!' WHERE entry=2000000594; /* content: Lor\'themar must hear us! We will not be silenced! */
UPDATE db_script_string SET content_loc1='그 문제에 대해서 생각해보겠소. 도움이 필요하면 부르시오.' WHERE entry=2000000595; /* content: That should take care of that. Summon me if you have further need of my services. */
UPDATE db_script_string SET content_loc1='우린 얼라이언스의 배신을 갚아줘야 해! 절대 잊어서도, 용서해서도 안 될 것이오!' WHERE entry=2000000596; /* content: We will repay the betrayal of the Alliance! We shall never forget, and never forgive! */
UPDATE db_script_string SET content_loc1='지금 우리의 삶이 예전보다 개선되지 않았다고 말할 자가 있소? 우리의 생활이 윤택해졌다는 사실을 부인할 자가 있느냐는 말이오.' WHERE entry=2000000597; /* content: How can you say our lives are not better now? How can you deny the betterment of our lives? */
UPDATE db_script_string SET content_loc1='로르테마르는 실버문을 재건하여 우리가 진정 자랑스러워할 만한 도시로 만들었소.' WHERE entry=2000000598; /* content: Lor\'themar has remade Silvermoon into something we may truly be proud of. */
UPDATE db_script_string SET content_loc1='겨울 히아신스인가요? 제게 주시려고요?' WHERE entry=2000000599; /* content: Are those winter hyacinths? For me? */
UPDATE db_script_string SET content_loc1='우리의 새로운 동맹이 우리를 예전의 영광으로 인도할 거요. 이 동맹은 진실할 것이고, 우리는 또다시 배신당하지 않을 거요!' WHERE entry=2000000599; /* content: Our new allies will bring us back to our former glory. These bonds will be true, and we will not be betrayed again! */
UPDATE db_script_string SET content_loc1='중화제를 호수에 섞자 수정에 오염된 물이 깨끗해지는 것 같습니다.' WHERE entry=2000000633; /* content: As the neutralizing agent mixes into the waters of the lake, the water that was irradiated by the crystal seems to clear. */
UPDATE db_script_string SET content_loc1='테돈, 이제 변이된 생물에게 중화제를 시험해 보십시오.' WHERE entry=2000000634; /* content: Tedon, go ahead and try the neutralizing agent on the mutated creature. */
UPDATE db_script_string SET content_loc1='너를 철창에서 꺼내 줄 시간이 된 것 같구나.' WHERE entry=2000000635; /* content: Looks like it\'s time to let you out of this cage. */
UPDATE db_script_string SET content_loc1='자, 이제 중화제를 뿌려 주면...' WHERE entry=2000000636; /* content: Now, to put the neutralizing agent on it... */
UPDATE db_script_string SET content_loc1='성공적입니다!' WHERE entry=2000000637; /* content: Success! */
UPDATE db_script_string SET content_loc1='이건 아무 소용도 없습니다! 지지대로 떠받치지 않으면 건물이 무너지고 말 겁니다.' WHERE entry=2000000645; /* content: This won\'t work at all! The buildings will surely collapse if we do not shore up the support beams. */
UPDATE db_script_string SET content_loc1='소용없다고 하지 않았습니까! 기초를 튼튼히 하지 않으면 건물 전체가 무너질 겁니다!' WHERE entry=2000000646; /* content: This won\'t do at all! We have to secure the foundation or the entire structure will crumble! */
UPDATE db_script_string SET content_loc1='이 건물은 적어도 8가지 이상의 건축 규정을 위반했습니다!' WHERE entry=2000000647; /* content: This structure violates at least 8 different ordinances! */
UPDATE db_script_string SET content_loc1='흠, 훨씬 좋아 보이는군. 안을 한번 살펴볼까...' WHERE entry=2000000649; /* content: Hrm, this looks much better. Let\'s have a look on the inside. */
UPDATE db_script_string SET content_loc1='킬제덴의 썩은 몸통에서 스며 나오는 더러운 기운에 뒤쪽 전체가 날아갔습니다! 어떻게 이런 곳에서 사람이 살겠습니까? 볼만큼 봤단 말입니다!' WHERE entry=2000000650; /* content: By the foul teat of Kil\'Jaeden\'s rotted torso, the entire backside is blown out! How can someone be expected to live in here? I\'ve seen enough! */
UPDATE db_script_string SET content_loc1='이거면 될 것이오...' WHERE entry=2000000655; /* content: This should do it... */
UPDATE db_script_string SET content_loc1='일어나십시오, 작은 친구. 그만 잠에서 깨십시오... 당신은 이제 안전합니다.' WHERE entry=2000000656; /* content: Wake up little creature. Wake up from your slumber... You are safe now. */
UPDATE db_script_string SET content_loc1='%s|1이;가; 신음합니다.' WHERE entry=2000000657; /* content: %s groans. */
UPDATE db_script_string SET content_loc1='%s|1이;가; 일어나 앉습니다.' WHERE entry=2000000658; /* content: %s sits up. */
UPDATE db_script_string SET content_loc1='여... 여기가 어디지? 내... 내가 어디 있는거지?' WHERE entry=2000000659; /* content: Whe... Where am I? What... What is this place? */
UPDATE db_script_string SET content_loc1='%s|1이;가; 다시 의식을 잃습니다.' WHERE entry=2000000661; /* content: %s falls back into a coma. */
UPDATE db_script_string SET content_loc1='전쟁이다!' WHERE entry=2000000661; /* content: This mean war! */
UPDATE db_script_string SET content_loc1='이단을 퍼트리는 저런 자와 함께 일을 할 수 있을 거라고 생각하세요?' WHERE entry=2000000663; /* content: How can I be expected to get any work done with that thing spreading its heresy? */
UPDATE db_script_string SET content_loc1='나는 메카조드 님이 보낸 전령이다. 네게 걸린 저주를 풀어주마!' WHERE entry=2000000663; /* content: I am the herald of Mechazod. You will be decursed! */
UPDATE db_script_string SET content_loc1='쓸데없는 참견도 이걸로 끝이다. 내가 널 쓰러뜨리고 나면 메카조드 님이 저주를 풀어주실 것이다.' WHERE entry=2000000664; /* content: Your meddling is at an end. Mechazod will relieve your curse once I am done with you. */
UPDATE db_script_string SET content_loc1='여기에 당신 같은 부류는 필요 없어요.' WHERE entry=2000000665; /* content: We do not need your kind here. */
UPDATE db_script_string SET content_loc1='ED-210 작동 개시!' WHERE entry=2000000665; /* content: ED-210 online! */
UPDATE db_script_string SET content_loc1='%s|1이;가; 자기를 따라오라고 합니다.' WHERE entry=2000000666; /* content: %s urges you to follow him. */
UPDATE db_script_string SET content_loc1='트웡키!' WHERE entry=2000000666; /* content: Twonky! */
UPDATE db_script_string SET content_loc1='%s|1이;가; 쿠우의 토템을 가리킵니다.' WHERE entry=2000000667; /* content: %s points to the totem of Coo. */
UPDATE db_script_string SET content_loc1='%s|1이;가; 자기를 따라오라고 손짓합니다.' WHERE entry=2000000668; /* content: %s motions for you to follow him. */
