UPDATE script_texts SET content_loc1='%s|1이;가; 마지막 힘을 근처의 마법석으로 방출하여 성공적으로 재가동시킵니다.' WHERE entry=-1000668;
UPDATE script_texts SET content_loc1='이 악마! 세나리우스의 힘을 보여 주마!' WHERE entry=-1000676;
UPDATE script_texts SET content_loc1='%s|1이;가; 사악한 웃음을 터뜨립니다.' WHERE entry=-1000677;
UPDATE script_texts SET content_loc1='날 구원하겠다고? 애송이들이 배짱 하나는 두둑하구나. 구원이란 나의 신께서만 베푸실 수 있는 은혜인 것을!' WHERE entry=-1000680;
UPDATE script_texts SET content_loc1='%s|1이;가; 격분하여 포효합니다.' WHERE entry=-1000681;
