UPDATE script_texts SET content_loc1='부디 우리 백성들을 지켜다오.' WHERE entry=-1000683;
UPDATE script_texts SET content_loc1='악몽의 종들아, 일어서라! 일어서서 너희의 광기로 이 세계를 파괴해라! 살아 있는 모든 생명을 없애라...' WHERE entry=-1000684;
UPDATE script_texts SET content_loc1='내 부하들을 해치웠다는 건가? 그럼 어디 날 상대해 봐라, 필멸의 존재여!' WHERE entry=-1000687;
UPDATE script_texts SET content_loc1='레물로스, 이들이 내 앞에서 얼마나 쉽게 목숨을 잃어 가는지 똑똑히 지켜봐라. 아직 기회는 있다, 레물로스. 그 드루이드 녀석만 내게 넘기면, 이 모든 고통이 끝날 것이며 너는 이 학살을 막는 영웅이 될 것이다...' WHERE entry=-1000688;
UPDATE script_texts SET content_loc1='엘룬이시여, 저의 기도를 들어주소서. 저희에게 평화를 내려 주소서! 죽어 간 동료를 굽어살피소서...' WHERE entry=-1000689;
UPDATE script_texts SET content_loc1='부상자들의 상처를 돌봐 주세요, 자매들이여!' WHERE entry=-1000690;
UPDATE script_texts SET content_loc1='에라니쿠스여, 그대는 용서 받을 것입니다. 엘룬께서는 언제나 그대를 사랑하실겁니다. 그대를 지배하고 있는 속박에서 벗어나십시오!' WHERE entry=-1000692;
UPDATE script_texts SET content_loc1='느낄 수 있다... 느껴져... 다시 나를 쓰다듬으시는 엘룬 님의 손길이 느껴지는구나... 엘룬께서 내게 미소를 지으시는구나... 그래... 나는...' WHERE entry=-1000696;
UPDATE script_texts SET content_loc1='%s|1이;가; 한쪽 무릎을 꿇습니다.' WHERE entry=-1000698;
UPDATE script_texts SET content_loc1='엘룬께 찬양을... 에라니쿠스가 구원 받았도다.' WHERE entry=-1000699;
UPDATE script_texts SET content_loc1='허나... 말퓨리온, 세나리우스, 이세라... 그들은 아직도 다툼을 계속하고 있다. 그들에겐 내가 필요하니 난 다시 꿈으로 돌아가야 한다.' WHERE entry=-1000701;
UPDATE script_texts SET content_loc1='그리고 영웅들이여... 내게 그대들이 찾고 있는 것이 있다. 다시 한번 아제로스에서 악마가 사라지는 모습을 볼 수 있기를... 레물로스, 우리의 용사에게 녹색용군단의 파편을 건네 주도록 하라.' WHERE entry=-1000703;
UPDATE script_texts SET content_loc1='곧 끝날 것이오, 에라니쿠스. 몸조심하시오, 고대의 존재여.' WHERE entry=-1000704;
UPDATE script_texts SET content_loc1='네놈들의 세계는 무자비한 종말을 맞이하게 될 것이다. 악몽이 너희를 찾아갈 것이니라!' WHERE entry=-1000706;
UPDATE script_texts SET content_loc1='저 푸른 빛은... 이상하군요. 저게 과연 무슨 뜻일까요?' WHERE entry=-1000707;
UPDATE script_texts SET content_loc1='우리가 찾아냈어요!' WHERE entry=-1000708;
UPDATE script_texts SET content_loc1='여기예요. 불을 밝혀 주세요.' WHERE entry=-1000710;
UPDATE script_texts SET content_loc1='다음 목표를 찾으러 가요.' WHERE entry=-1000711;
UPDATE script_texts SET content_loc1='여기서 멈출 수는 없어요.' WHERE entry=-1000712;
UPDATE script_texts SET content_loc1='이제 마지막 단계입니다. 제단을 여세요.' WHERE entry=-1000713;
UPDATE script_texts SET content_loc1='난 돌에 새겨진 룬을 읽겠어요. 그대는 제단을 열 방법을 찾으세요.' WHERE entry=-1000714;
UPDATE script_texts SET content_loc1='제단이 빛나고 있어요! 우리가 해냈어요!' WHERE entry=-1000715;
UPDATE script_texts SET content_loc1='무슨 일이 일어난 걸까요? 앗! 보세요!' WHERE entry=-1000716;
UPDATE script_texts SET content_loc1='아... 실로 오랜 세월이 흘렀구나...' WHERE entry=-1000717;
UPDATE script_texts SET content_loc1='감히 누가 엘룬의 제단에서 소란을 일으키는 것이냐?' WHERE entry=-1000718;
UPDATE script_texts SET content_loc1='여사제시여, 성소에 들어와 소란을 일으킨 죄를 용서하소서. 절대 이곳에 어떤 피해도 입히지 않겠나이다.' WHERE entry=-1000719;
UPDATE script_texts SET content_loc1='저희는 단지 올빼미야수가 이 지역을 수호하는 이유를 알고자 할 뿐입니다.' WHERE entry=-1000720;
UPDATE script_texts SET content_loc1='한 때 우리는 저 보석을 통해 엘룬 님과 직접 대화를 할 수가 있었느니라.' WHERE entry=-1000724;
UPDATE script_texts SET content_loc1='엘룬께서는 보석을 통해서 무한한 지혜를 보내셨다.' WHERE entry=-1000725;
UPDATE script_texts SET content_loc1='우리는 엘룬 님을 섬기는 자로서, 보석을 수호해야 한다는 사실을 깨달았느니...' WHERE entry=-1000726;
UPDATE script_texts SET content_loc1='그리고 얼마 후 어떤 짐승을 보게 되었다. 깃털은 분명 올빼미의 것이지만, 곰의 힘과 의지가 있었노라.' WHERE entry=-1000727;
UPDATE script_texts SET content_loc1='선천적으로 올빼미야수는 마법을 사용할 줄 안다. 바로 엘룬께서 주신 능력이지.' WHERE entry=-1000732;
UPDATE script_texts SET content_loc1='만약 그대들이 이 세계의 어떤 장소에서 올빼미야수들을 발견한다면, 그것은 뭔가 중요한 걸 수호하고 있다는 뜻이다. 아주 오래전부터 맡아온 임무이지.' WHERE entry=-1000733;
UPDATE script_texts SET content_loc1='부디 지금 내가 그대들에게 보여준 것을 잊지마라.' WHERE entry=-1000734;
UPDATE script_texts SET content_loc1='안녕히.' WHERE entry=-1000735;
UPDATE script_texts SET content_loc1='여기에서 조금 더 머무르시며, 방금 우리가 알아낸 사실을 생각해 보셨으면 해요. 그리고 에렐라스를 만나서, 이 소식을 전해주세요.' WHERE entry=-1000737;
UPDATE script_texts SET content_loc1='%s|1이;가; 이상한 주문을 외우기 시작합니다.' WHERE entry=-1000738;
UPDATE script_texts SET content_loc1='방법이 있기는 한데...' WHERE entry=-1000742;
UPDATE script_texts SET content_loc1='%s|1이;가; 알았다는 듯 고개를 끄덕입니다.' WHERE entry=-1000743;
UPDATE script_texts SET content_loc1='%s|1이;가; 동료를 힐끗 쳐다봅니다.' WHERE entry=-1000745;
UPDATE script_texts SET content_loc1='딱 붙어 있으라고.' WHERE entry=-1000754;
UPDATE script_texts SET content_loc1='아주 잠깐 시간의 흐름이 정지할 것이오! 지금부터 보호막을 소환할 것이니...' WHERE entry=-1000755;
UPDATE script_texts SET content_loc1='주문을 끝내시오, 스태그헬름! 더 이상 수호의 문장을 유지할 수 없을 것 같소! 힘을 내시오!' WHERE entry=-1000756;
UPDATE script_texts SET content_loc1='고대의 존재께서 나의 손을 인도하시니... 잠에서 깨어나라! 깨어나서 이 저주받은 곳을 봉인하라!' WHERE entry=-1000757;
UPDATE script_texts SET content_loc1='%s|1이;가; 탈진하여 한쪽 무릎을 털썩 꿇습니다.' WHERE entry=-1000758;
UPDATE script_texts SET content_loc1='드디어... 끝났군, 대드루이드 스태그헬름이여. 비록 엄청난 희생이 있었지만 우리의 승리요.' WHERE entry=-1000759;
UPDATE script_texts SET content_loc1='용이여, 나의 백성들에게 이런 참혹함을 보고 겪게 한 후에도 내가 또다시 짐을 떠맡을 것이라고 기대했는가? 물론 그대는 미쳤으니까 그럴 수도 있겠지.' WHERE entry=-1000763;
UPDATE script_texts SET content_loc1='%s|1이;가; 보호막으로 흐르는 모래의 홀을 집어던지자 박살 납니다.' WHERE entry=-1000765;
UPDATE script_texts SET content_loc1='%s|1이;가; 실망하여 고개를 가로 젓습니다.' WHERE entry=-1000768;
UPDATE script_texts SET content_loc1='갑자기 불길한 예감이 드는데... 조심해, 놈들이 왔다!' WHERE entry=-1000772;
UPDATE script_texts SET content_loc1='자네가 발견한 그 이교도 조직에서 보낸 암살자로군. 다른 놈들이 우리를 발견하기 전에 어서 가세나.' WHERE entry=-1000773;
UPDATE script_texts SET content_loc1='잠깐! 뭔가 사악한 기운이 느껴지는데... 언데드다!' WHERE entry=-1000774;
UPDATE script_texts SET content_loc1='오늘 목공 일을 끝내고 오던 놈들이군. 계속 이동하지 않으면 날 노리는 적을 더 많이 만나게 될 거야.' WHERE entry=-1000776;
UPDATE script_texts SET content_loc1='이런... 또 이 세 놈들이군.' WHERE entry=-1000777;
UPDATE script_texts SET content_loc1='수호자의 도움 없이는 자신이 없는 것 같군?' WHERE entry=-1000778;
UPDATE script_texts SET content_loc1='이번에는 영원히 끝장내 주마!' WHERE entry=-1000779;
UPDATE script_texts SET content_loc1='수고했네. 여기서부터는 혼자 가더라도 안전할 거야. 잿빛 골짜기의 마에스트라 주둔지로 가서 델그렌과 대화하는 걸 잊지 마시게.' WHERE entry=-1000780;
UPDATE script_texts SET content_loc1='루리엘이 도와줄 줄 알았어요! 감사합니다, 친구여. 루리엘에게도 고맙다고 전해주세요!' WHERE entry=-1000781;
UPDATE script_texts SET content_loc1='감사합니다. 자매들을 다시 만나지도 못하고 죽는 줄 알았어요!' WHERE entry=-1000783;
UPDATE script_texts SET content_loc1='우리가 해냈소. 테렌디스를 찾아 우리가 무사하다는 사실을 알려 줘야 한다는 걸 잊지 마시오. 다시 한번 고맙소.' WHERE entry=-1000790;
UPDATE script_texts SET content_loc1='놈들이 오고 있어요.' WHERE entry=-1000791;
UPDATE script_texts SET content_loc1='전장에서 죽는 것은 두렵지 않소!' WHERE entry=-1000793;
UPDATE script_texts SET content_loc1='졸려... 너무 졸려요...' WHERE entry=-1000796;
UPDATE script_texts SET content_loc1='%s|1이;가; 꽥 소리를 내더니 장막의 샬라스를 향해 갑니다. 서둘러 따라가야 합니다!' WHERE entry=-1000797;
UPDATE script_texts SET content_loc1='%s|1이;가; 나무 앞에서 잠시 멈추더니 안으로 들어갑니다.' WHERE entry=-1000798;
UPDATE script_texts SET content_loc1='%s|1이;가; 뭔가를 찾고 있는 것 같습니다. 당신이 따라오기를 바랍니다.' WHERE entry=-1000799;
UPDATE script_texts SET content_loc1='%s|1이;가; 아래쪽 탑 쪽으로 날아갑니다! 따라잡으려면 뛰어내려야 합니다. 서두르십시오!' WHERE entry=-1000800;
UPDATE script_texts SET content_loc1='%s|1이;가; 큰 소리로 울어댑니다!' WHERE entry=-1000801;
UPDATE script_texts SET content_loc1='드디어 그 끔찍한 저주에서 풀려났구나! 고맙습니다! 릴라크에게 저는 무사하다고 전해 주십시오. 저는 스케티스에서 할 일이 있습니다. 테로크를 처치해야 합니다!' WHERE entry=-1000802;
UPDATE script_texts SET content_loc1='자네만 준비되면 우리는 언제든지 싸울 수 있네.' WHERE entry=-1000804;
UPDATE script_texts SET content_loc1='네 손아귀에서 정령들을 풀어줄 것이다. 무력으로!' WHERE entry=-1000805;
UPDATE script_texts SET content_loc1='당신이 듣는 것이나 제가 듣는 것이나 별로 다를 바는 없습니다, 형제여. 뒤를 보십시오...' WHERE entry=-1000807;
UPDATE script_texts SET content_loc1='그들이 구원을 받았군요! 그럼 우리가 이긴 겁니까?' WHERE entry=-1000808;
UPDATE script_texts SET content_loc1='이제 모든 게 제자리를 찾았습니다. 치유사여, 잘하셨습니다.' WHERE entry=-1000809;
UPDATE script_texts SET content_loc1='그래, 이곳 정령들은 충분히 구원을 받았어요. 하지만, 암호가 다른 이들에게 알려지면... 불의 정령들이 동요하고 있습니다... 이것을 막지 못하면 필멸의 존재들이 온 세계는 멸망할 것입니다.' WHERE entry=-1000810;
UPDATE script_texts SET content_loc1='필멸의 존재들이여, 안녕히... 대지치유사는 불의 정령들이 무엇을 느끼고 있는지 알고 있습니다.' WHERE entry=-1000811;
UPDATE script_texts SET content_loc1='비통의 오로노크와 그 아들들이 대지치유사 토르록에게 자신들의 무기를 건넵니다.' WHERE entry=-1000813;
UPDATE script_texts SET content_loc1='이 위업을 달성하게 해준 영웅들에게 이걸 주십시오.' WHERE entry=-1000814;
UPDATE script_texts SET content_loc1='치유되어라!' WHERE entry=-1000815;
UPDATE script_texts SET content_loc1='이제 됐어요! 인부들이 스컬지에게서 탈출했습니다!' WHERE entry=-1000816;
UPDATE script_texts SET content_loc1='아, 난 또다시 실패했군요...' WHERE entry=-1000817;
UPDATE script_texts SET content_loc1='이제야 내가 왔던 곳으로 돌아왔는데 또다시 엄청난 비극을 겪게 되다니...' WHERE entry=-1000818;
UPDATE script_texts SET content_loc1='스컬지가 나타났다! 달아나라! 목숨을 건지려면 도망쳐라!' WHERE entry=-1000819;
UPDATE script_texts SET content_loc1='도와주세요! 왕자가 미쳐버렸어요!' WHERE entry=-1000820;
UPDATE script_texts SET content_loc1='신호를 보냈으니 윈저 님께서 금방 도착하실 겁니다.' WHERE entry=-1000822;
UPDATE script_texts SET content_loc1='여어!' WHERE entry=-1000823;
UPDATE script_texts SET content_loc1='당신이 올 줄 알았소, $N. 또 보게 돼서 반갑군, 친구.' WHERE entry=-1000824;
UPDATE script_texts SET content_loc1='조심하게, 친구. 프레스톨은 쉽게 쓰러지지 않을 걸세.' WHERE entry=-1000825;
UPDATE script_texts SET content_loc1='아주 오래전 카라잔에서 결정된 대로 내가 왔소. 그리고 정의의 심판이 내릴 것이오!' WHERE entry=-1000826;
UPDATE script_texts SET content_loc1='저놈을 잡아라! 저 쓸모없는 죄인과 그 친구들을 잡아들여라!' WHERE entry=-1000827;
UPDATE script_texts SET content_loc1='레지널드, 자네를 통과시킬 수 없다는 것을 알지 않나?' WHERE entry=-1000828;
UPDATE script_texts SET content_loc1='자신이 옳다고 생각하는 것을 해야 하네, 마커스. 우리는 투랄리온 장군 밑에서 함께 복무했잖나. 투랄리온 장군께서는 오늘날의 우리를 만들어 주신 분이시네. 진정 내가 우리 동맹에 해를 끼치려 한다고 믿는 것인가? 내가 우리 영웅들을 수치스럽게 할 인물로 보이나?' WHERE entry=-1000829;
UPDATE script_texts SET content_loc1='나를 이곳에 붙들고 있는 것은 현명하지 못한 처사야, 마커스.' WHERE entry=-1000830;
UPDATE script_texts SET content_loc1='%s|1이;가; 사색에 잠긴 듯합니다.' WHERE entry=-1000831;
