UPDATE script_texts SET content_loc1='이제 됐어. 여기서부턴 나 혼자 갈 수 있어.' WHERE entry=-1000577; /* content: This is far enough. I can make it on my own from here. */
UPDATE script_texts SET content_loc1='저기! 놈을 해치우세요! 파멸의 암호를 되찾아야 해요!' WHERE entry=-1000579; /* content: There! Destroy him! The Cipher must be recovered! */
UPDATE script_texts SET content_loc1='훌륭한 표본이로군.' WHERE entry=-1000581; /* content: A wonderful specimen. */
