UPDATE script_texts SET content_loc1='끔찍했어요... 꿈에서 저는 친구들의 적이 되어 싸웠었어요.' WHERE entry=-1000598; /* content: It was awful... I dreamt I was fighting against my friends. */
UPDATE script_texts SET content_loc1='기적이군! 스스로 가죽을 벗어버리다니!' WHERE entry=-1000599; /* content: It\'s a miracle! The beast skinned itself! */
